  grid-template-rows: 3em;
}

#top-bar .open-menu a {
  grid-area:open-menu;
  display: flex;
  justify-content: center;
}

#top-bar {
  grid-area:top-bar;
  top: 1rem !important;
  left: 0;
  margin-left: 1rem;

  align-items: stretch;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
}

#search-top-box {
  grid-area:search;
  width: 100%;
  display: flex;
  justify-content: flex-end;
}

#login-status {
  grid-area:user;
