[[module CSS]]

/* SIDE BAR */
#top-bar .open-menu a {
    position: fixed;
    bottom: 0.5em;
    left: 0.5em;
    z-index: 15;
    font-family: san-serif;
    font-size: 30px;
    font-weight: 700;
    width: 1.325em;
    height: 1.325em;
    line-height: 0.9em;
    text-align: center;
    background-color: #000000 !important;
    border: solid 3px #000000 !important;
    border-radius: 50%;
    color: transparent !important;
    text-decoration: none!important;
}

#top-bar .open-menu a::after {
    content: "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    background: var(--sb-btn) 3px no-repeat;
    background-size: calc(1.325em - 3px);
    background-position: center;
    width: 100%;
    height: 100%;
}

@media (min-width: 767px) {
  .mobile-top-bar {
    display: block;
  }
  .mobile-top-bar li {
    display: none;
  }
  #main-content {
    max-width: 767px;
    margin: 0 auto;
    padding: 0;
    transition: max-width 0.2s ease-in-out;
  }
  #content-wrap::after{
    display: none;
  }
  #side-bar {
    display: block;
    position: fixed;
    top: 0;
    left: -25em;
    width: 14rem;
    height: 100%;
    background-color: #FFFFFF;
    overflow-y: auto;
    z-index: 10;
    padding: 0;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    -moz-transition: left 0.5s ease-in-out 0.1s;
    -ms-transition: left 0.5s ease-in-out 0.1s;
    -o-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
  }
  #side-bar:after {
    content: "";
    position: absolute;
    top: 0;
    width: 0;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);
  }
  #side-bar:target {
    display: block;
    left: 0;
    width: 14rem;
    margin: 0;
    border: none;
    z-index: 10;
  }
  #side-bar:target + #main-content {
    left: 0;
  }
  #side-bar:target .close-menu {
    display: block;
    position: fixed;
    width: calc(100% - 14rem);
    height: 100%;
    top: 0;
    left: unset;
    right: 0;
    background: rgba(0,0,0,0.3) 1px 1px repeat;
    z-index: -1;
  }
}

