content:
[[include :fondationscp:theme:sous-le-monde]]

[[iftags +테마]]
[[[sous-le-monde|아래세상]]] 카논의 테마입니다. 작품에서 사용하려면 아래 코드를 복사해서 맨 위에 붙여넣으세요.
[[/iftags]]

[[include :scpko:component:modele-theme
|titres2=block
]]

[[div [[iftags -테마]]style="display: none"[[/iftags]]]]
[[module CSS show="true"]]
@font-face {
    font-family: 'RIDIBatang';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_twelve@1.0/RIDIBatang.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}
@font-face {
    font-family: 'Special Elite Centennial';
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: url(https://fonts.gstatic.com/s/specialelite/v18/XLYgIZbkc4JPUL5CVArUVL0ntnAOSA.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
@font-face {
    font-family: 'Special Elite Centennial';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2206-02@1.0/ChosunCentennial.woff2') format('woff2');
    unicode-range: U+AC00-D7A3;
    font-weight: normal;
    font-style: normal;
}

/* Masquer le titre */
#page-title {
  display: block;
}

/* Grand titre : 1er = Texte titre 2e = Contour, ombre */

#header h1 a::before {
    content: ;
    font-family: chomsky;
    font-weight: normal;
    font-style: normal;
    color:black;
}

#header h1 a {
    display: none;
    margin: 0;
    padding: 0px 0 0px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'RIDIBatang';
    font-size: 0%;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h2 span::before {
    content: ;
    color:black;
}

#header h2 a {
    display: none;
    margin: 0;
    padding: 0px 0 0px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Special Elite Centennial', cursive;
    font-size: 0%;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.0px;
}


h2 {
  font-family: 'Special Elite Centennial', cursive;
  font-size: 17pt;
  color:black;
}

/* LOGO */

#header {
    height: 200px;
    padding-bottom: 0px; /* FOR MENU */
    position: relative;
 background: url( BLANC )
}

/* BANNIÈRE */

div#container-wrap {
    background: url(https://fondationscp.wdfiles.com/local--files/theme%3Asous-le-monde/body-bg.png) top left repeat-x;
}
body {
    background-position: top center;
    background-repeat: repeat;
    background-color: white;
    color: black;
}

/* FOND DE PAGE */

body {
    color:black;
    background-image: url( BLANC );
    font-family: 'RIDIBatang';
}

/* TITRE DE LA PAGE */

#page-title {
  display: block;
  font-family: 'RIDIBatang', cursive;
  font-style: italic;
  color: #000;
  font-size: 200%;
  padding: 35px 0 10px;
  position: relative;
}

/* HIDE THE HEADER TEXT */
#header h1 a {
    display: block;
    margin: 0;
    padding: 0px 0 0px;
    line-height: 0px;
    max-height: 0px;
    color: #00003D;
    background: transparent;
    font-family: Akashi, Arial, sans-serif;
    font-size:0%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #000;
    letter-spacing: 0.9px;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #f0f0c0;
    font-family: Helvetica, sans-serif;
    font-size: 0%;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* BARRE DE RECHERCHE */
#search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
    background-color: transparent;
    color: transparent;
    display: none;
    font-size: 0pt;
    padding: 0px;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 0px black;
    padding: 40px;
    color: black;
    display: none;
    font-family: 'RIDIBatang';
    background-color: transparent;
    box-shadow: inset 0px 0px 0px rgba(0,0,0,.8);
}
#search-top-box-form input[type=submit] {
    display: none;
    background: transparent;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: transparent;
    display: none;
}

/* TOP BARRE */
#top-bar {
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding-top: 93px;
    z-index: 20;
    font-size: 90%;
}
#top-bar ul {
    float: right;
}
#top-bar a {
    color: #fff;
    background: transparent;
}
#top-bar ul li {
    border: 0;
}
#top-bar ul li ul {
    border: solid 1px transparent;
    box-shadow: 0 2px 6px rgba(0,0,0,.5);
    border-top: 0;
}
#top-bar ul li a {
    border-left: solid 1px rgba(64,64,64,.1);
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #fff;
    color: #414446;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid #ddd;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #fff;
    color: #414446;
    text-decoration: none;
}
#top-bar ul li ul {
    border-width: 0 1px 1px 1px;
    width: auto;
}
#top-bar ul li ul li, #top-bar ul li ul li.sfhover,
#top-bar ul li ul li, #top-bar ul li ul li:hover {
    border-width: 0;
}
#top-bar ul li ul li a {
    border-width: 0;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #a01;
}

/* SIDEBAR */

#side-bar {
    background: transparent;
    padding-top: 80px;
}

#side-bar .side-block {
    border: 1px double #000;
    box-shadow: 0 0px 0px rgba(21, 21, 21, 1);
    background: white;
    border-radius: 0px;
}
#side-bar .heading {
    color: #BC0E12;
    font-size: 120%;
    border-bottom: solid 1px #00003D;
}

#side-bar img {
    filter: hue-rotate(70deg);
    -webkit-filter: hue-rotate(70deg);
}

#side-bar div.side-block:nth-child(7) > div:nth-child(1) > div:nth-child(1) img {
    filter: none;
    -webkit-filter: none;
}

/* MENU OUVERTURE SIDEBAR */

open-menu a {
    border: 0.2em solid #003333!important;
    background-color: lightgrey!important;
    color: #003333!important;
}

/* MOBILE SIDEBAR */
@media (max-width: 767px) {

#side-bar{
    background-color: transparent;
}

.open-menu a {
    border: 0.2em double white!important;
    background-color: #807665!important;
    color: white!important;
}
}

/* NOM UTILISATEUR */

#login-status {
    color: #990000;
    font-family: 'Special Elite Centennial', cursive;
    font-size: 100%;
    z-index: 30;
   display: none;
}

#login-status a {
    background: transparent;
    font-family: 'Special Elite Centennial', cursive;
    font-size: 100%;
    color: black;
border: none;
}

#login-status ul a {
    color: black;
    font-family: 'Special Elite Centennial', cursive;
    font-size: 100%;
    background: transparent;
background-color: transparent;

}

#login-status ul a:hover{
color: black;
    font-family: 'Special Elite Centennial', cursive;
    font-size: 100%;
}

#account-topbutton {
    background: transparent;
    color: #000;
}

/* Adjust Mobile Image Size */
@media (max-width: 321px) {

div#container-wrap {
    background: url(https://fondationscp.wdfiles.com/local--files/theme%3Asous-le-monde/body-bg-portable.png) top left repeat-x;
}
#top-bar {
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0px;
    z-index: 20;
    font-size: 90%;
}

#page-title {
  display: block;
  font-family: 'RIDIBatang', cursive;
  font-style: italic;
  color: #000;
  font-size: 200%;
  padding: -30px 0px 0px 0px;
  position: relative;
  padding-top: -50px;
  margin-top: -70px;
}

#side-bar {
    background: transparent;
    padding-top: 0px;
}

}

@media (min-width: 322px) and (max-width: 424px) {
div#container-wrap {
    background: url(https://fondationscp.wdfiles.com/local--files/theme%3Asous-le-monde/body-bg-portable.png) top left repeat-x;
}
#top-bar {
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0px;
    z-index: 20;
    font-size: 90%;
}

#side-bar {
    background: transparent;
    padding-top: 0px;
}

#page-title {
  display: block;
  font-family: 'RIDIBatang', cursive;
  font-style: italic;
  color: #000;
  padding-top: -50px;
  margin-top: -70px;
}
}
@media (min-width: 425px) and (max-width: 767px) {
div#container-wrap {
    background: url(https://fondationscp.wdfiles.com/local--files/theme%3Asous-le-monde/body-bg-portable.png) top left repeat-x;
}
#top-bar {
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0px;
    margin-bottom: -50px;
    z-index: 20;
    font-size: 90%;
}

#side-bar {
    background: transparent;
    padding-top: 0px;
}

#page-title {
  display: block;
  font-family: 'RIDIBatang', cursive;
  font-style: italic;
  color: #000;
  padding-top: -50px;
  margin-top: -70px;
}
}
[[/module]]
[[/div]]
