[!-- {$spidersfalling-header-bhl}
[[module css]]
:root
#extra-div-1 {
    height: 2rem;
    width: 100%;
    top: 9rem;
    position: absolute;
    background: url('http://scp-sandbox-3.wdfiles.com/local--files/mooagain/Spider.png');
    background-repeat: repeat-x;
    background-size: 6.2rem;
    pointer-events: none;
    z-index: 9;
   animation: lightOne 3s infinite steps(1, end);
}
#extra-div-2 {
    height: 2rem;
    width: 100%;
    top: 9rem;
    position: absolute;
    background: url('http://scp-sandbox-3.wdfiles.com/local--files/mooagain/Spider.png');
    background-repeat: repeat-x;
    background-size: 6.2rem;
    pointer-events: none;
    z-index: 9;
   animation: lightTwo 3s infinite steps(1, end);
}
@keyframes lightOne {
    0%
    {
       opacity: 0;
    }
    50%
    {
       opacity: 1;
    }
    100%
    {
       opacity: 0;
    }
}
@keyframes lightTwo {
    0%
    {
       opacity: 1;
    }
    50%
    {
       opacity: 0;
    }
    100%
    {
       opacity: 1;
    }
}
[[/module]]
[!----]

