[[module CSS]]

@import url(https://scp-wiki-cn.wdfiles.com/local--code/theme:mox/8);
@import url(https://scp-wiki-cn.wdfiles.com/local--code/theme:starry/1);

[[/module]]

[[module CSS]]

/* {$normal-header}/

div#extrac-div-1, div#extrac-div-2 {
    display: none;
}

div#header {
    margin-top: 0 !important;
}

/{$normal-header} */

/* {$no-sticky-header}/

div#header {
    position: relative;
}

/{$no-sticky-header} */

/* {$no-bg}/

div#container {
    background-image: none !important;
    background-position: unset;
    background-size: unset;
    background-attachment: unset;
    background-color: var(--mox-bg-color) !important;
}

/{$no-bg} */

/* {$show-page-title}/

div#page-title {
    display: block;
    margin-top: 2rem;
}

/{$show-page-title} */

/* {$f-header-animation-disabled}/

div#extrac-div-2 {
    animation: none;
}

/{$f-header-animation-disabled} */

/* {$c}/

div#container {
    background-image: linear-gradient(180deg, var(--mox-bg-color) 50vh, transparent), linear-gradient(45deg, #00000045, #00000063), url({$page-bg});
}

div#extrac-div-1 {
    animation-duration: calc(2.5s * {$animation-speed});
    background-size: auto {$f-header-logo-size}, contain;
}

div#extrac-div-1 span::before {
    animation-duration: calc(1s * {$animation-speed});
}

div#extrac-div-1 span::after {
    animation-duration: calc(2s * {$animation-speed});
}

div#extrac-div-2 {
    animation-duration: calc({$f-header-animation-speed} * {$animation-speed});
}

div#extrac-div-1 {
    height: {$f-header-size};
    top: calc({$f-header-size} * -1);
    background-image: url({$f-header-logo}), {$f-header-gradient} !important;
}

div#extrac-div-1 span::before, div#extrac-div-1 span::after {
    top: calc(({$f-header-logo-size} + {$f-header-size}) / 2)
}

div#extrac-div-1 span::before {
    content: '{$f-header-title}';
}

div#extrac-div-1 span::after {
    content: '{$f-header-subtitle}';
}

div#extrac-div-2 {
    height: {$f-header-size};
    top: calc({$f-header-size} * -1);
    background-image: linear-gradient(0deg, var(--mox-bg-color), transparent 50%), url({$f-header-bg});
    background-size: contain, {$f-header-bg-size};
}

div#header {
    margin-top: calc({$f-header-size} + 10vh - 5rem);
}

@keyframes stars-scroll {

    0% {
        background-position: center, 0vh 0vh;
    }

    100% {
        background-position: center, {$f-header-animation-velocity};
    }

}

/{$c} */

/* {$default-light}/

:root {
    --mox-bg-color: #ececec;
    --mox-text-color: #292929;
    --mox-layer-dark-1: #000000;
    --mox-layer-dark-2: #3a3a3a;
    --mox-layer-dark-3: #848484;
    --mox-layer-light-1: #f8f8f8;
    --mox-layer-light-2: #f0f0f0;
    --mox-layer-light-3: #dcdcdc;
    --mox-bgblur-front-color: rgb(255 255 255 / 56%);
}

div#header {
    background-color: #ffffffbd;
    border-bottom-color: transparent;
    backdrop-filter: blur(8px);
    box-shadow: 0 0.5rem 1.5rem #0000000a;
}

div#container {
    background-image: linear-gradient(180deg, var(--mox-bg-color) 50vh, transparent), linear-gradient(45deg, #ffffff1c, #ffffff30), url(https://scpsandboxcn.wdfiles.com/local--files/oxygen9-files-2/049l.jpg);
}

div#extrac-div-2 {
    background-image: linear-gradient(0deg, var(--mox-bg-color), #ffffffb0 50%), url(https://stsandbox.wdfiles.com/local--files/oxyfiles/starsbg.jpg);
}

div#side-bar::after {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(var(--mox-header-height) * -1) / cover !important;
}

#header #search-top-box input.button {
    background: url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(var(--mox-header-height)* -2) center / cover !important;
}

a.weditor-bold::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 3) center / cover !important;
}

a.weditor-italic::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 4) center / cover !important;
}

a.weditor-underline::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 22) center / cover !important;
}

a.weditor-strikethrough::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 5) center / cover !important;
}

a.weditor-quote::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 6) center / cover !important;
}

a.weditor-superscript::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 7) center / cover !important;
}

a.weditor-subscript::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 8) center / cover !important;
}

a.weditor-table::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 9) center / cover !important;
}

a.weditor-code::before, a.weditor-codewiz::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 10) center / cover !important;
}

a.weditor-uri::before, a.weditor-uriwiz::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 11) center / cover !important;
}

a.weditor-pagelink::before, a.weditor-pagelinkwiz::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 12) center / cover !important;
}

a.weditor-image::before, a.weditor-imagewiz::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 13) center / cover !important;
}

a.weditor-html::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 14) center / cover !important;
}

a.weditor-numlist::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 15) center / cover !important;
}

a.weditor-bullist::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 15.8) center / cover !important;
}

a.weditor-incindent::before, a.weditor-decindent::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 16) center / cover !important;
}

a.weditor-deflist::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 17) center / cover !important;
}

a.weditor-footnote::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 18) center / cover !important;
}

a.weditor-bib::before, a.weditor-bibcite::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 20.8) center / cover !important;
}

:where(a.weditor-imagewiz, a.weditor-codewiz, a.weditor-uriwiz, a.weditor-pagelinkwiz, a.weditor-incindent, a.weditor-decindent, a.weditor-eqref,a.weditor-bibcite)::after {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2) no-repeat local calc(-2.3rem* 20) center / cover;
}

:where(a.weditor-imagewiz, a.weditor-codewiz, a.weditor-uriwiz, a.weditor-pagelinkwiz, a.weditor-incindent, a.weditor-decindent, a.weditor-eqref,a.weditor-bibcite)::after {
    background-color: var(--mox-layer-dark-1);
    background-image: url(https://oxygennine.wdfiles.com/local--files/system:files/icons-2);
    background-repeat: no-repeat;
    background-position: left center !important;
    background-size: cover;
    background-position: -20.055rem !important;
    background-origin: content-box;
}

/{$default-light} */

/* {$c}/

:root {
    --mox-header-title: "{$header-title}";
    --mox-header-subtitle: "{$header-subtitle}";
    --mox-header-logo: url({$header-logo}) !important;
    --mox-theme-color: {$theme-color};
    --mox-theme-dark-color: {$theme-color-dark};
    --mox-theme-light-color: {$theme-color-light};
}

/{$c} */

