[[code type="CSS"]]

:root {

    --mox-bg-color: #000000;
    --mox-text-color: #cccccc;
    --mox-layer-dark-1: #dcdcdc;
    --mox-layer-dark-2: #c5c5c5;
    --mox-layer-dark-3: #b6b6b6;
    --mox-layer-light-1: #1e1e1e;
    --mox-layer-light-2: #3a3a3a;
    --mox-layer-light-3: #464646;
    --mox-bgblur-front-color: rgb(0 0 0 / 56%);

    --mox-header-title: "SCP基金会";
    --mox-header-subtitle: "控制 - 收容 - 保护";
    --mox-header-logo: url(http://oxygennine.wdfiles.com/local--files/system:files/scp-logo-small-white.svg);

    --mox-theme-color: #d54e4e;
    --mox-theme-dark-color: #b95656;
    --mox-theme-light-color: #ff6b6b;

}

div#extrac-div-2 {
    width: 100vw;
    height: 90vh;
    position: absolute;
    top: -90vh;
    right: 0;
    margin-top: 0vh;
    background-image: linear-gradient(0deg, var(--mox-bg-color), transparent 50%), url(https://stsandbox.wdfiles.com/local--files/oxyfiles/starsbg.jpg);
    z-index: -1;
    background-position: center, 0vh 0vh;
    background-size: contain, 50vh, 50vh;
    animation: stars-scroll 10s infinite;
    animation-timing-function: linear;
}

div#extrac-div-1 {
    width: 100vw;
    height: 90vh;
    position: absolute;
    top: -90vh;
    right: 0;
    margin-top: 0vh;
    background-image: url(https://scpsandboxcn.wdfiles.com/local--files/oxygen9-files-2/scplogofantasy.png), linear-gradient(192deg, var(--mox-layer-light-3), transparent 70%);
    background-position: center;
    background-repeat: no-repeat;
    background-size: auto 60vh, contain;

    animation: logo-label-fadein 2.5s;
    animation-timing-function: ease-in-out;
    animation-delay: 0s;
    animation-fill-mode: forwards;
        opacity: 0;
}

div#header {
    position: sticky;
    top: 0vh;
    margin-top: calc(100vh - 5rem);
    background-color: #000000bd;
    border-bottom-color: transparent;
    backdrop-filter: blur(4px);
    box-shadow: 0 1rem 1rem #000000a8;
}

div#container {
    background-image: linear-gradient(180deg, var(--mox-bg-color) 50vh, transparent), linear-gradient(45deg, #00000045, #00000063), url(https://stsandbox.wdfiles.com/local--files/oxyfiles/049.jpg);
    background-position: center;
    background-size: contain;
    background-attachment: scroll, fixed, fixed;
}

div#extrac-div-1 span::before, div#extrac-div-1 span::after {
    content: '';
    display: block;
    position: relative;
    z-index: 100;
    top: 75vh;
    left: 0;
    color: var(--mox-layer-dark-2);
    width: 100vw;
    text-align: center;
    font-family: var(--mox-ui-font);
}

div#extrac-div-1 span::after {
    content: "SCP FOUNDATION";
    font-size: 0.7rem;
    font-weight: lighter;
    letter-spacing: 0.2rem;
    animation: logo-label-fadein 2s;
    animation-timing-function: ease-in-out;
    animation-delay: 2.5s;
    animation-fill-mode: forwards;
        opacity: 0;
}

div#extrac-div-1 span::before {
    content: 'SCP基金会';
    font-size: 1.4rem;
    letter-spacing: 0.25rem;
    font-weight: normal;
    animation: logo-label-intro 1s;
    animation-timing-function: ease-in-out;
    animation-delay: 2.5s;
    animation-fill-mode: forwards;
        opacity: 0;
}

div#side-bar::before {
    display: none;
}

div#footer::before {
    filter: invert(0);
}

#footer {
    box-shadow: 0rem -0.15rem 0rem 0rem var(--mox-theme-color);
}

div#page-title {
    display: none;
}

span.s-title-line.type-2 {
    font-size: 2rem;
    padding-top: 0.5rem;
    box-shadow: 0rem -0.3rem var(--mox-text-color);
    margin-top: 0.5rem;
}

div.s-title p span {
    text-align: justify;
    width: auto;
    display: block;
    text-align-last: justify;
}

div.s-title p span {
    line-height: 5rem;
}

div.s-title p {
    display: contents;
}

span.s-title-line.type-2 {
    line-height: 2.5rem;
}

span.s-title-line.type-1 {
    font-size: 4rem;
    font-weight: bolder;
}

.s-title {
    width: max-content;
    margin: 3rem auto 8rem auto;
}

#header h1 a span::after {
    bottom: calc(var(--mox-header-height)* 0.115 + -0.175rem);
}

#header h1 a span::before {
    font-size: calc(var(--mox-header-height)* 0.4) !important;
}

#page-content {
    width: 80vw;
    margin: 0 auto;
    line-height: 2rem;
}

#main-content {
    max-width: 95vw !important;
}

.shr p {
    display: flex;
    align-items: center;
    justify-content: space-between;
    column-gap: 0.5rem;
}

.shr p::before, .shr p::after {
    display: block;
    width: -webkit-fill-available;
    height: 0.1rem;
    content: '';
    background: var(--mox-text-color);
}

.side-block ul li {
    marker: none;
    list-style: none;
}

#side-bar ul {
    padding: 0 0 0 0em;
    margin: 0em 0;
}

.blanklines.line1 {
    margin: 4rem auto;
}

.blanklines.line2 {
    margin: 8rem auto;
}

.blanklines.line3 {
    margin: 12rem auto;
}

.blanklines.linesc {
    margin: 55vh auto;
}

div.s-list p>a:hover {
    text-shadow: 0 0 0.8rem #fff !important;
    box-shadow: none !important;
}

div.s-list p>a {
    color: var(--mox-text-color) !important;
    margin-left: 1rem;
    font-weight: bold;
    font-size: 1.75rem;
    font-family: var(--mox-ui-font);
}

div.s-list blockquote p:first-child::before {
    content: '"';
    display: block;
    font-size: 4rem;
    font-weight: bolder;
    font-family: var(--mox-mono-font);
    font-style: italic;
    position: absolute;
    transform: translate(-3rem, 0.15rem);
}

div.s-list blockquote {
    box-shadow: none;
    padding-left: 3rem;
}

.code {
    background-image: linear-gradient(var(--mox-layer-dark-2) 1.7em, var(--mox-layer-dark-1) 1.7em);
}

@media (max-width: 876px) {

    span.sci-title-line.type-1 {
        font-size: 3rem;
    }

    span.sci-title-line.type-2 {
        font-size: 1.5rem;
    }

    #header h1 a::before {
        opacity: 1;
    }

    #header h1 a span {
        background-image: linear-gradient(90deg, #00000000 0%, #000000 25%);
        padding: calc(var(--mox-header-height)* 0.5) calc(var(--mox-header-height)* 1);
        text-shadow: 0rem 0rem 1rem #fff, 0rem 0rem 1rem #000;
    }

    #header {
        top: calc(var(--mox-header-height)* -2) !important;
    }


}

@keyframes logo-label-intro {

    0% {
        letter-spacing: 1.25rem;
        opacity: 0;
    }

    100% {
        letter-spacing: 0.25rem;
        opacity: 1;
    }

}

@keyframes logo-label-fadein {

    0% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }

}

@keyframes stars-scroll {

    0% {
        background-position: center, 0vh 0vh;
    }

    100% {
        background-position: center, 500vh -50vh;
    }

}

/* darkmode */


div#side-bar::after {
    background: url(https://oxygennine.wdfiles.com/local--files/system:files/icons-1) no-repeat local calc(var(--mox-header-height) * -1) / cover !important;
}

#header #search-top-box input.button {
    background: url(https://oxygennine.wdfiles.com/local--files/system:files/icons-1) no-repeat local calc(var(--mox-header-height)* -2) center / cover !important;
}

a.weditor-bold::before {
    background: var(--mox-layer-light-1) url(https://oxygennine.wdfiles.com/local--files/system:files/icons-1) no-repeat local calc(-2.3rem* 3) center / cover !important;
}

a.weditor-italic::before {
