/*
 * 移动版调整
 */

@media (max-width: 950px) {
    #top-bar {
        top: 75px;
        left: 55px;
    }
    #search-top-box {
        top: 50px;
    }
}

@media (max-width: 767px) {
    #top-bar {
        width: 29em;
    }
    #side-bar {
        background-color: rgba(0, 0, 0, 0.7);
    }
    #page-content .sidebarButton a {
        display: block;
        position: fixed;
        z-index: 10;
    }
    #page-content .guideContainer .guideSidebar {
        display: block;
        position: fixed;
        top: 0;
        right: -50em;
        height: 100%;
        max-width: 50%;
        margin: 0;
        overflow-y: auto;
        z-index: 10;
        padding: 1em 1em 0 1em;
        background-color: rgba(0, 0, 0, 0.1);
        -webkit-transition: left 0.4s ease-in-out;
        -o-transition: left 0.4s ease-in-out;
        -moz-transition: left 0.4s ease-in-out;
        transition: left 0.4s ease-in-out;
        scrollbar-width: thin;
        background-color: rgba(0, 0, 0, 0.7);
        -webkit-transition: right 0.5s ease-in-out 0.1s;
        -o-transition: right 0.5s ease-in-out 0.1s;
        -moz-transition: right 0.5s ease-in-out 0.1s;
        transition: right 0.5s ease-in-out 0.1s;
        direction: rtl;
    }
    #page-content .guideSidebar .sidebarPanel {
        background: #24384d;
        direction: ltr;
    }
    #page-content .guideSidebar:target {
        display: block;
        right: 0;
        margin: 0;
        border: 1px solid #dedede;
        z-index: 10;
    }
    #page-content .guideContainer .guideContent {
        max-width: 100%;
    }
    #page-content .guideSidebar:target .close-menu {
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        background: rgba(0, 0, 0, 0.3) 1px 1px repeat;
        z-index: -1;
    }
    #login-status {
        right: 35px;
    }
}

@media (max-width: 479px) {
    #top-bar {
        left: 0;
    }
    #header {
        -webkit-background-size: 35px 35px;
           -moz-background-size: 35px 35px;
             -o-background-size: 35px 35px;
                background-size: 35px 35px;
        background-position: 30px 35px;
    }
    #header h1 a {
        padding: 32px 0 20px 0
    }
    #header h1 a span::before {
        font-size: 18px;
    }
    #header h2 span::before {
        font-size: 12px;
    }
}

@media (max-width: 425px) {
    #top-bar {
        width: 25em;
    }
    #top-bar li a {
        padding: 1px 0.5em;
    }
}
