[[collapsible show="Open" hide="Close"]]
[[code type="CSS"]]
/*
    Esterberg Theme
    [2023 Wikidot Theme]
    By EstrellaYoshte

    Based on:
    Generic Space Theme by EstrellaYoshte
    SkipOS Theme by EstrellaYoshte
*/

/*-----------------------------*/
/*------ FONTS ------*/
/*-----------------------------*/

@import url('https://api.fonts.coollabs.io/css2?family=Sora:wght@400;700&display=swap');
@import url('https://api.fonts.coollabs.io/css2?family=Public+Sans:ital,wght@0,400;0,700;1,400;1,700&display=swap');

@font-face {
    font-family: 'iA Writer Mono';
    src: url(https://cdn.jsdelivr.net/gh/SYwaves/Webfont-Archive@master/iAWriterMonoS-Regular.woff2) format("woff2");
    font-weight: normal;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'iA Writer Mono';
    src: url(https://cdn.jsdelivr.net/gh/SYwaves/Webfont-Archive@master/iAWriterMonoS-Italic.woff2) format("woff2");
    font-weight: normal;
    font-style: italic;
    font-display: swap;
}
@font-face {
    font-family: 'iA Writer Mono';
    src: url(https://cdn.jsdelivr.net/gh/SYwaves/Webfont-Archive@master/iAWriterMonoS-Bold.woff2) format("woff2");
    font-weight: bold;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'iA Writer Mono';
    src: url(https://cdn.jsdelivr.net/gh/SYwaves/Webfont-Archive@master/iAWriterMonoS-BoldItalic.woff2) format("woff2");
    font-weight: bold;
    font-style: italic;
    font-display: swap;
}

@font-face {
    font-family: 'iA Writer Duo';
    src: url(https://cdn.jsdelivr.net/gh/SYwaves/Webfont-Archive@master/iAWriterDuoS-Regular.woff2) format("woff2");
    font-weight: normal;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'iA Writer Duo';
    src: url(https://cdn.jsdelivr.net/gh/SYwaves/Webfont-Archive@master/iAWriterDuoS-Italic.woff2) format("woff2");
    font-weight: normal;
    font-style: italic;
    font-display: swap;
}
@font-face {
    font-family: 'iA Writer Duo';
    src: url(https://cdn.jsdelivr.net/gh/SYwaves/Webfont-Archive@master/iAWriterDuoS-Bold.woff2) format("woff2");
    font-weight: bold;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'iA Writer Duo';
    src: url(https://cdn.jsdelivr.net/gh/SYwaves/Webfont-Archive@master/iAWriterDuoS-BoldItalic.woff2) format("woff2");
    font-weight: bold;
    font-style: italic;
    font-display: swap;
}

/*-----------------------------*/
/*------ BASE ------*/
/*-----------------------------*/

:root {
    --accent-h: 17deg;
    --complement-h: calc(var(--accent-h) + 18deg);

    --primary-accent-1: var(--accent-h) 97.5% 53.25%;
    --primary-accent-2: calc(var(--accent-h) - 5deg) 77.5% 38.5%;
    --primary-accent-3: calc(var(--accent-h) - 15deg) 85% 30%;

    --light-accent-1: calc(var(--accent-h) + 12.5deg) 95% 98.25%;
    --light-accent-2: calc(var(--accent-h) + 7.5deg) 85.5% 80.5%;
    --light-accent-3: calc(var(--accent-h) + 5deg) 95% 75%;

    --dark-accent: calc(var(--accent-h) - 16.5deg) 60% 15%;

    --primary-complement: var(--complement-h) 97.5% 52.5%;
    --light-complement: calc(var(--complement-h) + 12.5deg) 95% 97.5%;
    --dark-complement: var(--complement-h) 55% 11.25%;

    --assist-color: var(--accent-h) 8% 13%;

    --s-header-desktop-height: 21.75rem;
    --s-header-mobile-height: 15.25rem;
    --sp_header-height: var(--s-header-desktop-height);
    --sp_top-bar-height: 2.75rem;
    --sp_dropdown-width: 28ch;
    --sp_header-logo: url(https://scp-wiki.wdfiles.com/local--files/theme:sunside/esterberg-sun.png);
    --sp_header-logo-size: 15.25rem;
    --sp_header-title-scale: 1.325;
    --sp_header-subtitle-scale: 1.25;

    --sp_side-bar-width: 15.5rem;

    --sp_rate-module-text-color: hsl(var(--light-complement));
    --sp_rate-module-background: hsl(var(--assist-color));
    --sp_rate-module-button-color: var(--sp_rate-module-text-color);
    --sp_rate-module-button-background: transparent;
    --sp_rate-module-button-hover-background: transparent;

    --header-font: 'Sora', Nanum Gothic, sans-serif;
    --body-font: 'Public Sans', Nanum Gothic, Arial, sans-serif;
    --UI-font: 'iA Writer Duo', sans-serif;
    --mono-font: 'iA Writer Mono', sans-serif;
    --header-title: "Esterberg";
    --header-subtitle: "Weaving New Paradigms";
    --main-content-width: 50rem;

    --sp_tab-background: hsl(var(--dark-accent));
    --sp_tab-text-color: hsl(var(--light-complement));
    --sp_tab-hover-background: hsl(var(--assist-color));
    --sp_tab-hover-text-color: hsl(var(--primary-accent-1));
    --sp_tab-selected-background: hsl(var(--primary-accent-1));
}

body {
    font-family: var(--body-font);
    font-size: 0.9675rem;
    background-color: hsl(var(--light-accent-1));
    color: hsl(var(--dark-complement));
}
body, li, p { line-height: 1.5; }

tt, .page-source, .code, pre {
    font-family: var(--mono-font);
}

.page-source, .code {
    border: none;
    background-color: hsl(var(--light-complement));
}

::selection {
    background-color: hsl(var(--primary-accent-1) / 0.875);
    color: hsl(var(--light-accent-1));
}

/*---------- SCROLLBAR ----------*/

::-webkit-scrollbar {
  width: 0.5rem;
  background: transparent;
}
::-webkit-scrollbar-thumb {
  background: hsl(var(--light-accent-2));
  border: none;
}
::-webkit-scrollbar-track {
  background: hsl(var(--assist-color));
}

:root {
    scrollbar-color: hsl(var(--light-accent-2)) hsl(var(--assist-color));
    scrollbar-width: thin;
}

/*-----------------------------*/
/*------ HEADER ------*/
/*-----------------------------*/

#header {
    order: -1;
    max-width: initial;
    grid-template-areas:
        "login login login search"
        ". . . ."
        "h1 h1 h1 h1"
        "h2 h2 h2 h2"
        ". . . ."
        "top-bar top-bar top-bar top-bar";
    grid-template-columns: var(--sp_area-comp-margin) 1fr auto auto;
