div[id*="header-extra-div"] {
    z-index: -1;
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    -webkit-mask-size: contain;
    -webkit-mask-repeat: repeat-x;
    -webkit-mask-position: top;
}

#header-extra-div-1 {
    height: 82.5%;
    background-color: hsl(var(--light-accent-3));
    -webkit-mask-image: var(--esterberg-header-layer1);
    mask-image: var(--esterberg-header-layer1);
}
#header-extra-div-2 {
    height: 59.25%;
    background-color: hsl(var(--primary-accent-1));
    -webkit-mask-image: var(--esterberg-header-layer2);
    mask-image: var(--esterberg-header-layer2);
}
#header-extra-div-3 {
    height: 45%;
    bottom: var(--sp_top-bar-height);
    background-color: hsl(var(--primary-accent-2));
    -webkit-mask-image: var(--esterberg-header-layer3);
    mask-image: var(--esterberg-header-layer3);
}

#header :is(h1,h2) {
    margin-left: initial;
    padding: 0;
    float: none;
    max-height: unset;
    margin-inline-start: calc(var(--sp_header-logo-adaptive-size) + var(--sp_area-min-margin) + 0.5rem);
}
#header h1 { grid-area: h1; }
#header h2 { grid-area: h2; }

/*-----------------------------*/

#header::before {
    grid-column: 1/3;
    margin-inline-start: var(--sp_area-min-margin);
    background-color: hsl(var(--dark-complement));
    height: calc(100% - 1.25rem);
    clip-path: polygon(0 calc(100% - var(--sp_header-logo-adaptive-size)/2), 0 0, 100% 0, 100% calc(100% - var(--sp_header-logo-adaptive-size)/2), 50% 100%);
    background-size: 77.5%;
    background-position-y: calc(50% - 1.25rem);
}

#header h1 a {
    font-family: var(--header-font);
    color: hsl(var(--light-complement));
    text-shadow: none;
}

#header h2 {
    font-family: var(--UI-font);
}
#header h2 span {
    text-shadow: none;
    font-weight: normal;
    color: hsl(var(--light-accent-1));
}

/*-----------------------------*/

#login-status {
    display: flex;
    gap: 0.5em;
    align-items: center;
    justify-content: end;
    font-family: var(--UI-font);
    font-size: 0.825em;
    background-color: hsl(var(--assist-color));
    color: hsl(var(--light-complement));
    padding: 0.325rem 0.5rem;
}

#login-status > a:nth-last-of-type(2):not(.btn) { margin-inline-end: 1.825rem; }

a#account-topbutton {
    display: grid;
    place-content: center;
    border: none;
    height: 100%;
    width: 1.75rem;
    font-size: 1.125em;
    padding: 0;
    margin: 0;
    position: absolute;
    inset-block-start: 0;
    inset-inline-end: 0;
    background-color: hsl(var(--light-accent-1) / 0.175);
    color: hsl(var(--light-accent-3));
}

#account-options {
    width: max-content;
    border: none;
    background: hsl(var(--assist-color)/0.875);
    backdrop-filter: blur(0.1rem);
}

#account-options ul li a {
    box-shadow: inset 0 0.1rem hsl(var(--light-accent-1)/0.125);
    padding: 0.25rem 0.5rem;
    color: hsl(var(--light-accent-1));
    position: relative;
    text-decoration: none;
}
#account-options ul li a:is(:hover,:focus) { color: hsl(var(--assist-color)); }
#account-options ul li a::before {
    content: "";
    background-color: hsl(var(--primary-accent-1));
    position: absolute;
    inset-block-start: 0; inset-inline-end: 0;
    width: 0;
    height: 100%;
    z-index: -1;
    transition: width 0.175s ease-out;
}
#account-options ul li a:is(:hover,:focus)::before { width: 100%; }

/*-----------------------------*/

#search-top-box-form {
    gap: 0;
    height: 100%;
}

#search-top-box-input, #search-top-box-form input[type=submit] {
    padding: 0.25rem;
    height: 100%;
    box-sizing: border-box;
    border-radius: 0;
}

#search-top-box-form input.btn[type=submit] {
    box-shadow: none;
    background: hsl(var(--primary-accent-1));
    border: none;
    font-size: 0;
    width: 1.925rem;
}

#search-top-box {
    --search-mask: url(data:image/svg+xml,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20512%20512%22%3E%3Cpath%20fill%3D%22%23ddd%22%20d%3D%22M505%20442.7L405.3%20343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3%2044-79.7%2044-128C416%2093.1%20322.9%200%20208%200S0%2093.1%200%20208s93.1%20208%20208%20208c48.3%200%2092.7-16.4%20128-44v16.3c0%206.4%202.5%2012.5%207%2017l99.7%2099.7c9.4%209.4%2024.6%209.4%2033.9%200l28.3-28.3c9.4-9.4%209.4-24.6.1-34zM208%20336c-70.7%200-128-57.2-128-128%200-70.7%2057.2-128%20128-128%2070.7%200%20128%2057.2%20128%20128%200%2070.7-57.2%20128-128%20128z%22%2F%3E%3C%2Fsvg%3E);
}
#search-top-box::before {
    content: "";
    display: block;
    width: 1.925rem;
    height: 100%;
    position: absolute;
    top: 0;
    right: 0;
    background-color: hsl(var(--light-accent-1));
    pointer-events: none;
    z-index: 2;
    -webkit-mask-image: var(--search-mask);
    mask-image: var(--search-mask);
    -webkit-mask-size: 1rem;
    mask-size: 1rem;
    -webkit-mask-position: center;
    mask-position: center;
    -webkit-mask-repeat: no-repeat;
    mask-repeat: no-repeat;
}

/*-----------------------------*/
/*------ TOP BAR ------*/
/*-----------------------------*/
#top-bar {
    font-family: var(--UI-font);
    background: hsl(var(--dark-accent));
}

#top-bar div:is(.top-bar, .mobile-top-bar) > ul {
    justify-content: center;
}
#top-bar div.mobile-top-bar > ul > li { flex-grow: 1; }

#top-bar div:is(.top-bar, .mobile-top-bar) > ul > li > a:any-link {
    color: hsl(var(--light-accent-1));
    transition: background-color 0.325s, color 0.125s;
    position: relative;
    font-size: min(calc(0.5em + 1.375vw),1em);
    min-width: max-content;
}
#top-bar div:is(.top-bar, .mobile-top-bar) > ul > li > a:any-link:is(:hover, :focus),
#top-bar div:is(.top-bar, .mobile-top-bar) > ul > li:is(.sfhover, :focus-within) > a:any-link {
    background-color: hsl(var(--assist-color));
    color: hsl(var(--primary-accent-1));
}
#top-bar div:is(.top-bar, .mobile-top-bar) > ul > li > a::before {
    content: "";
    background-color: hsl(var(--primary-accent-1));
    position: absolute;
    inset-block-start: 0; inset-inline-start: 0;
    width: 0; height: 0.175rem;
    transition: width 0.25s ease-out;
}
#top-bar div:is(.top-bar, .mobile-top-bar) > ul > li:is(.sfhover, :focus-within) > a::before { width: 100%; }

#top-bar div:is(.top-bar, .mobile-top-bar) > ul > li > ul {
    border: none;
    box-shadow: none;
    display: flex;
    flex-direction: column;
    background: hsl(var(--assist-color)/0.9125);
    backdrop-filter: blur(0.1rem);
    inset-inline-start: 50%;
    transform: translate(-50%);
    transition: opacity 0.125s ease-out;
}

#top-bar div:is(.top-bar, .mobile-top-bar) ul li ul li a:any-link {
    display: block;
    position: relative;
    padding: 0.625rem;
    text-align: center;
    min-width: max-content;
    box-sizing: border-box;
    width: 100%;
    line-height: normal;
    background: transparent;
    color: hsl(var(--light-accent-1));
    border: none;
    box-shadow: inset 0 0.1rem hsl(var(--light-accent-1)/0.125);
}
#top-bar div:is(.top-bar, .mobile-top-bar) ul li ul li a:any-link:is(:hover,:focus) {
    color: hsl(var(--assist-color))
}
#top-bar div:is(.top-bar, .mobile-top-bar) ul li ul li a:any-link::before {
    content: "";
    background-color: hsl(var(--primary-accent-1));
    position: absolute;
    inset-block-start: 0; inset-inline-start: 0;
    width: 0; height: 100%;
    z-index: -1;
    transition: width 0.175s ease-out;
}
#top-bar div:is(.top-bar, .mobile-top-bar) ul li ul li a:any-link:is(:hover,:focus)::before { width: 100%; }

/*-----------------------------*/
/*------ SIDE BAR ------*/
/*-----------------------------*/

#top-bar div.open-menu a {
    color: hsl(var(--primary-accent-1));
    background-color: hsl(var(--dark-accent));
    border-radius: 0;
    border: none;
    padding: 0.175rem;
    --cut: 0.375rem;
    clip-path: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
}

#side-bar {
    background-color: hsl(var(--assist-color) / 0.9375);
    padding: 0;
    overflow-x: hidden;
}

:is(#interwiki,#side-bar) .side-block {
    color: hsl(var(--light-accent-1));
    padding: 0;
    border: none;
    border-radius: 0;
    box-shadow: none;
    background: transparent;
    margin: 0;
}
#side-bar .side-block.media {
    background: hsl(var(--light-accent-3) / 0.175);
}
#side-bar .side-block.resources {
    background: hsl(var(--primary-accent-1) / 0.175);
}

:is(#interwiki,#side-bar) .heading {
    font-size: 0.775em;
    color: hsl(var(--primary-accent-1));
    border-bottom: solid 0.05rem currentColor;
    text-align: center;
    padding: 0.125rem;
    margin: 0;
}

:is(#interwiki,#side-bar) .menu-item {
    display: flex;
    margin: 0;
    box-shadow: inset 0 -0.1rem hsl(var(--light-accent-1)/0.125);
    font-size: 0;
    font-family: var(--UI-font);
}
#side-bar .menu-item.small { font-size: 0; }

:is(#interwiki,#side-bar) .menu-item a {
    display: grid;
    place-content: center;
    padding: 0.325rem;
    font-weight: normal;
    color: hsl(var(--light-accent-1));
    background: transparent;
    text-decoration: none;
    font-size: 0.875rem;
    border: none;
    flex-grow: 1;
    flex-basis: 2rem;
    position: relative;
    text-align: center;
    transition: color 0.125s ease-out;
}
#side-bar .menu-item.small a { font-size: .75rem; }
:is(#interwiki,#side-bar) .menu-item a:is(:hover,:focus) {
    color: hsl(var(--assist-color));
}
:is(#interwiki,#side-bar) .menu-item a::before {
    content: "";
    background-color: hsl(var(--primary-accent-1));
    position: absolute;
    inset-block-start: 0;
    inset-inline-start: 0;
    width: 0;
    height: 100%;
    z-index: -1;
    transition: width 0.175s ease-out;
}
:is(#interwiki,#side-bar) .menu-item a:is(:hover,:focus)::before { width: 100%; }


#side-bar .close-menu.close-menu {
    background: transparent;
    margin-left: var(--sp_side-bar-width);
}

/*-----------------------------*/
/*------ MAIN AREA ------*/
/*-----------------------------*/

#container {
    display: grid;
    grid-template-columns: minmax(0,1fr);
}
#container::before {
    content: "";
    display: block;
    height: 2.5rem;
    width: 16rem;
    max-width: 100%;
    margin: 0 auto;
    background: hsl(var(--dark-accent));
    clip-path: polygon(0 0, 100% 0, 50% 72.5%);
}

#content-wrap {
    margin-top: 0.25rem;
}

#main-content {
    max-width: var(--main-content-width);
}

.owindow {
    --cut: 1.25rem;
    background-color: hsl(var(--light-accent-1));
    border: none;
    text-align: start;
    clip-path: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
}
.owindow .title {
    cursor: unset;
    text-align: center;
    font-size: 1.125em;
    padding: 0.25rem 0.75rem;
    background-color: transparent
    font-family: var(--UI-font);
    height: auto;
}

/*----------------------------*/

#page-title, .meta-title {
    font-family: var(--header-font);
    font-weight: bold;
    font-size: 2.125em;
    text-align: center;
    color: hsl(var(--primary-accent-3));
    padding: 0;
    border: none;
    display: flex;
    gap: 0.75rem;
    align-items: center;
    justify-content: center;
    line-height: 1.15;
}

:is(#page-title, .meta-title)::before,
:is(#page-title, .meta-title)::after {
    content: "";
    display: block;
    flex-grow: 1;
    height: 1.875rem;
    min-width: 3.75rem;
    background-color: currentColor;
    clip-path: polygon(0 0, 0.875rem calc(50% - 0.125rem), 100% 50%, 0.875rem calc(50% + 0.125rem), 0 100%, 0.675rem 50%);
}
:is(#page-title, .meta-title)::before { transform: scale(-100%);}


/*--------- HR ----------*/

hr {
    --hr-icon-mask: url("data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' data-name='Layer 1' viewBox='0 0 234.9 134.1'%3E%3Cpath d='m178.9 123-10.6-10.6L213.7 67l-45.4-45.4L178.9 11l56 56-56 56z' class='cls-1'/%3E%3Cpath d='m117.5 134-67-67 67-67 67 67ZM71.6 67l45.9 45.8L163.3 67l-45.8-45.8Z' class='cls-1'/%3E%3Cpath d='M56 123 0 67l56-56 10.6 10.6L21.2 67l45.4 45.4L56 123z' class='cls-1'/%3E%3C/svg%3E");
    --hr-icon-size: 2rem;
    margin: 1.375rem 0;
    background: transparent;
    height: var(--hr-icon-size);
    display: grid;
    place-items: center;
    grid-template-columns: 1fr;
    grid-template-rows: 1fr;
}
hr::before, hr::after {
    content: "";
    display: grid;
    grid-column: 1/2;
    grid-row: 1/2;
    background-color: hsl(var(--primary-accent-3));
}
hr::before {
    height: 0.1625rem;
    width: 100%;
    -webkit-mask-image: linear-gradient(90deg, black calc(50% - var(--hr-icon-size)/2 + 0.125rem), transparent calc(50% - var(--hr-icon-size)/2 + 0.125rem), transparent calc(50% + var(--hr-icon-size)/2 - 0.125rem), black calc(50% + var(--hr-icon-size)/2 - 0.125rem));
    mask-image: linear-gradient(90deg, black calc(50% - var(--hr-icon-size)/2 + 0.125rem), transparent calc(50% - var(--hr-icon-size)/2 + 0.125rem), transparent calc(50% + var(--hr-icon-size)/2 - 0.125rem), black calc(50% + var(--hr-icon-size)/2 - 0.125rem));
     clip-path: polygon(50% 0, 100% 50%, 50% 100%, 0 50%)
}
hr::after {
    width: var(--hr-icon-size);
    height: var(--hr-icon-size);
    -webkit-mask-image: var(--hr-icon-mask);
    -webkit-mask-repeat: no-repeat;
    -webkit-mask-size: contain;
    -webkit-mask-position: center;
    mask-image: var(--hr-icon-mask);
    mask-repeat: no-repeat;
    mask-size: contain;
    mask-position: center;
}

/*----------------------------*/

h1,h2,h3,h4,h5,h6 {
    color: hsl(var(--primary-accent-3));
    font-family: var(--header-font);
    font-weight: bold;
    letter-spacing: unset;
}
:where(#page-content) :is(h1,h2,h3,h4,h5,h6) {
    display: flow-root;
    max-width: max-content;
    margin: 0;
    margin-block: 0.25em;
    border-block-end: solid 0.05em currentColor;
    position: relative;
    padding-inline: 0.375rem;
    color: hsl(var(--primary-accent-2));
}
:where(#page-content) :is(h1,h2,h3)::before {
    content: "";
    position: absolute;
    inset-inline-end: 100%;
    inset-block-end: 0;
    height: 50%;
    width: 2.5rem;
    border-block-start: solid 0.05em;
    border-inline-end: solid 0.05em;
    border-color: inherit;
    transform: skew(25deg);
    transform-origin: bottom;
}
:where(#page-content) h2 {
    font-size: 1.425em;
    margin-inline-start: 0.125rem;
}
:where(#page-content) h2::before { width: 1.325rem; }
:where(#page-content) h3 {
    margin-inline-start: .325rem;
    font-size: 1.25em;
    padding-inline: 0.125em;
}
:where(#page-content) h3::before { border-block-start: none; }

:where(#page-content) :is(h4,h5,h6) {
    font-family: var(--UI-font);
    padding: 0;
    margin-inline-start: 0.325rem;
}

:where(#page-content) :is(h1,h4)::after {
    content: "";
    position: absolute;
    border: solid 0.05em;
    border-color: inherit;
    width: 0.375em;
    height: 0.375em;
}
:where(#page-content) h1::after {
    inset-block-start: 50%;
    inset-inline-end: calc(100% + 3.275rem);
    transform: translateY(-50%) rotate(45deg);
}
:where(#page-content) h4::after {
    inset-inline-end: calc(100% + 0.05rem);
    inset-block-start: 100%;
    transform: translateY(-50%) rotate(45deg);
}


:where(#page-content) h6 {
    font-size: 0.875em;
    border: none;
}

/*-----------------------------*/
/*------ RATE MODULE ------*/
/*-----------------------------*/

div.page-rate-widget-box, div.rate-box-with-credit-button {
    background-color: transparent;
    box-shadow: none;
}

.page-rate-widget-box:not(.rate-box-with-credit-button div),
div.rate-box-with-credit-button {
    --cut: 0.875rem;
    min-height: calc(var(--cut)*2);
    font-family: var(--UI-font);
    position: relative;
    padding: 0 calc(var(--cut) * 1.207);
     isolation: isolate;
}
div.page-rate-widget-box .rate-points {
    padding-inline-start: 0.25em;
    text-transform: capitalize;
}
:is(.page-rate-widget-box:not(.rate-box-with-credit-button div),div.rate-box-with-credit-button)::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: var(--sp_rate-module-background);
    z-index: -1;
    clip-path: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
}

.page-rate-widget-box :is(.rateup, .ratedown) a {
    --sp_rate-module-button-hover-color: hsl(var(--primary-accent-1));
    position: relative;
    margin: 0;
    color: var(--sp_rate-module-button-color);
    background-color: var(--sp_rate-module-button-background);
    display: grid;
    place-content: center;
    width: calc(var(--cut)*1.414);
    height: calc(var(--cut)*1.414);
    text-align: center;
    box-sizing: border-box;
    transition: color 0.125s ease-out;
}
.page-rate-widget-box :is(.rateup, .ratedown) a::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    box-sizing: border-box;
    background-color: hsl(var(--light-accent-3) / .175);
    border: solid 0.05em hsl(var(--primary-accent-1));
    transform: rotate(45deg) scale(0.95);
    pointer-events: none;
    opacity: 0;
    transition: opacity 0.125s ease-out;
}
.page-rate-widget-box :is(.rateup, .ratedown) a:is(:hover,:focus)::before { opacity: 1; }

div.page-rate-widget-box .cancel a {
    font-weight: bold;
    --sp_rate-module-active-color: hsl(var(--light-accent-2));
    --sp_rate-module-sub-color: hsl(var(--primary-accent-1));
}

/*----------------------*/

div.rate-box-with-credit-button {
    padding-inline-end: 0;
    margin-inline-end: calc(var(--cut)*.707);
}
div.rate-box-with-credit-button .creditButton {
    display: grid;
    align-items: center;
    width: calc(var(--cut)*1.207);
}
div.creditButton p a {
    width: initial;
    height: 100%;
    aspect-ratio: 1;
    border: none;
    background-color: hsl(var(--primary-accent-1));
    clip-path: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
}

div.modalbox {
    --cut: 1.25rem;
    display: flex;
    flex-direction: column;
    padding: .75rem 1.5rem;
    border: none;
    border-radius: 0;
    box-shadow: none;
    background: hsl(var(--light-accent-1));
    clip-path: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
}
.modalbox hr {
    margin: 0.5rem 0;
}
.modalbox .credit {
    margin: 0;
}

/*-----------------------------*/
/*------ LINK ------*/
/*-----------------------------*/

a:any-link {
    color: hsl(var(--primary-accent-1));
    background-color: transparent;
    transition: background-color 0.175s ease-out;
}
a:any-link:where(:hover,:focus) {
    background-color: hsl(var(--light-accent-3) / .175);
}

:where(#page-content) a:any-link:where(:not(.printuser > a:first-child, .page-rate-widget-box a)) {
    padding-block: 0.025em;
    text-decoration: none;
    border-bottom: solid 0.05em currentColor;
}

a.newpage {
    color: hsl(var(--light-accent-3));
    border-bottom-style: dashed;
}
a:visited {
    color: hsl(var(--primary-accent-2));
}

/*------ Blockquotes, TOC ------*/

blockquote, div.blockquote, #toc {
    background-color: transparent;
    border: none;
    border-inline-start: solid 0.25rem hsl(var(--primary-accent-1));
    padding: 0.125em 1.25em;
}

#toc {
    border-color: hsl(var(--primary-accent-2));
    padding: 0.125rem 0.675rem;
}
#toc .title {
    font-family: var(--UI-font);
    color: hsl(var(--primary-accent-2));
}

/*------ Collapsible ------*/

.collapsible-block {
     --cut: 1.375rem;
}

.collapsible-block-folded a.collapsible-block-link,
.collapsible-block-unfolded-link a.collapsible-block-link {
    display: inline-flex;
    align-items: center;
    position: relative;
    min-height: calc(var(--cut) * 2);
    box-sizing: border-box;
    padding: calc(var(--cut) * .3535) calc(var(--cut) * 1.414);
    padding-inline-start: calc(var(--cut) * 2.414);
    border: none;
    clip-path: polygon(
        0% var(--cut),
        var(--cut) 0%,
        calc(100% -  var(--cut)) 0%,
        100% var(--cut), 
        100% calc(100% -  var(--cut)),
        calc(100% -  var(--cut)) 100%,
        var(--cut) 100%,
        0% calc(100% -  var(--cut))
        );
    background-color: hsl(var(--light-accent-3) / 0.25);
    color: inherit;
    overflow: hidden;
    isolation: isolate;
    transition: color .125s linear;
}
.collapsible-block-folded a.collapsible-block-link:is(:hover,:focus) {
    color: hsl(var(--light-accent-1));
}

.collapsible-block-folded a.collapsible-block-link::before {
    content: "";
    display: block;
    background-color: hsl(var(--dark-accent));
    height: 100%;
    width: calc(var(--cut) * 2);
    clip-path: inherit;
    position: absolute;
    inset-block-start: 0;
    inset-inline-start: 0;
    transition: width 0.175s ease-out;
    z-index: -1;
}
.collapsible-block-folded a.collapsible-block-link:is(:hover,:focus)::before {
    width: 100%;
}

:is(.collapsible-block-folded a.collapsible-block-link, .collapsible-block-unfolded-link a.collapsible-block-link)::after {
    content: "+";
    font-size: 1.375em;
    font-weight: bold;
    font-family: var(--UI-font);
    display: grid;
    place-content: center;
    position: absolute;
    inset-block-start: 0;
    inset-inline-start: 0;
    height: 100%;
    width: calc(var(--cut) * 2);
    background-color: hsl(var(--dark-accent));
    color: hsl(var(--light-accent-1));
    clip-path: inherit;
    transition: background-color 0.125s ease-out;
}

.collapsible-block-folded a.collapsible-block-link:is(:hover,:focus)::after {
    background-color: hsl(var(--primary-accent-1));
}

.collapsible-block-unfolded-link a.collapsible-block-link {
    color: hsl(var(--light-accent-1));
    background-color: hsl(var(--primary-accent-1));
}

.collapsible-block-unfolded-link a.collapsible-block-link::after {
    content: "-";
    background-color: hsl(var(--primary-accent-1));
}
.collapsible-block-unfolded-link a.collapsible-block-link:is(:hover,:focus)::after {
    background-color: hsl(var(--assist-color));
}

/*------ Licensebox ------*/

.licensebox {
    margin-block: 0.75rem;
}
.licensebox .collapsible-block-link {
    margin: 0;
    font-size: 0.875em;
}

/*------ Image Block ------*/

.scp-image-block {
    border: none;
    box-shadow: none;
}

.scp-image-block .scp-image-caption {
    position: relative;
    border: none;
    box-sizing: border-box;
    padding: .375em;
    font-family: var(--UI-font);
    background-color: hsl(var(--assist-color));
    color: hsl(var(--light-complement));
}
.scp-image-block .scp-image-caption:not([class*="center"])::before {
    content: "";
    position: absolute;
    width: 1rem;
    height: 1rem;
    background-color: inherit;
    top: calc(100% - 0.05rem);
    pointer-events: none;
}
.scp-image-block.block-right .scp-image-caption::before {
    right: 0;
    clip-path: polygon(0 0, 100% 0, 100% 100%);
}
.scp-image-block.block-left .scp-image-caption::before {
    left: 0;
    clip-path: polygon(0 0, 100% 0, 0 100%);
}

/*------ Tabs ------*/

.yui-navset .yui-nav {
    isolation: isolate;
    position: relative;
    border: none;
    font-family: var(--UI-font);
    font-size: 0.925em;
    --cut: 1.125rem;
}
.yui-navset .yui-nav::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: var(--sp_tab-background);
    z-index: -1;
    clip-path: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
}
.yui-navset .yui-nav::after {
    content: "";
    position: absolute;
    z-index: -1;
    height: 50%; width: 0.05rem;
    background-color: hsl(var(--primary-accent-3));
    inset-block-end: -0.05rem; inset-inline-start: 0;
    transform-origin: bottom;
    transform: rotate(45deg);
}

.yui-navset .yui-nav li { flex-grow: 1; }

.yui-navset.yui-navset-top .yui-nav li a {
    position: relative;
    border: none;
    padding: calc(var(--cut) * .3535) calc(var(--cut) * 1.414);
    min-height: calc(var(--cut)*2);
    clip-path: polygon( 0% 0%, 0% 0%, 100% 0%, 100% 0%, 100% 100%, 100% 100%, 0% 100%, 0% 100%);
    transition: clip-path 0.15s linear, transform 0.2125s ease-out;
}
.yui-navset.yui-navset-top .yui-nav .selected a {
    clip-path: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
    transform: scale(1.0625);
    z-index: 1;
}
.yui-navset.yui-navset-top .yui-nav li a::before {
    content: "";
    background-color: hsl(var(--primary-accent-1));
    position: absolute;
    inset-block-start: 0; inset-inline-start: 0;
    width: 0; height: 0.225rem;
    transition: width 0.25s ease-out;
}
.yui-navset.yui-navset-top .yui-nav li a:is(:hover,:focus)::before { width: 100%; }



.yui-navset .yui-content {
    background-color: transparent;
    border: none;
    border-inline-start: solid 0.05rem hsl(var(--primary-accent-3));
    padding: 0.225rem 0.75rem;
    position: relative;
}
.yui-navset .yui-content::before {
    content: "";
    position: absolute;
    border: solid 0.05rem;
    border-color: hsl(var(--primary-accent-3));
    width: 0.375rem;
    height: 0.375rem;
    inset-block-start: calc(100% + 0.05rem);
    inset-inline-start: -0.25rem;
    transform: rotate(45deg);
}

/*------ Table ------*/

table.wiki-content-table td {
    border-color: hsl(var(--assist-color));
}

table.wiki-content-table th {
    background-color: hsl(var(--assist-color));
    color: hsl(var(--light-accent-2));
    border-color: hsl(var(--assist-color));
}

/*------ Footnotes ------*/

:is(.footnotes-footer, bibitems) .title {
    font-family: var(--UI-font);
    font-size: 1.25em;
    color: hsl(var(--primary-accent-3));
    border-block-end: solid 0.05rem currentColor;
}

.hovertip {
    --sp_hovertip-background: hsl(var(--assist-color) / 0.9125);
    --sp_hovertip-border: none;
    --cut: 0.675rem;
    backdrop-filter: blur(0.125rem);
    color: hsl(var(--light-complement));
    padding: calc(var(--cut) * .3535) calc(var(--cut) * .707);
    clip-path: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
}

.footnote .f-heading, .equation .e-heading, .reference .r-heading {
    font-family: var(--UI-font);
    color: hsl(var(--light-accent-2));
}

/*-----------------------------*/
/*------  BOTTOM ------*/
/*-----------------------------*/

#main-content .page-tags span {
    border-color: hsl(var(--primary-accent-3));
}

#footer {
    background: hsl(var(--dark-accent));
    padding: 0.325rem;
}

#license-area, #footer-bar {
    color: hsl(var(--light-complement));
    background-color: hsl(var(--assist-color));
    width: 100%;
    box-sizing: border-box;
}
:is(#license-area, #footer-bar) a {
    font-weight: bold;
    color: hsl(var(--primary-accent-1));
}


/*-----------------------------*/
/*------  CUSTOM SYNTAX ------*/
/*-----------------------------*/

.header-center {
    text-align: center;
}
.header-center :is(h1,h2,h3,h4,h5,h6) {
    margin-inline: auto;
}

.city-block {
    --border: 0.175rem;
    box-sizing: border-box;
    width: calc(100% - 2rem);
    margin-inline: auto;
    margin-block: 1.5rem;
    padding: 0.25rem 0.75rem;
    background-color: hsl(var(--primary-complement) / 0.075);
    position: relative;
}

.city-block::before, .city-block::after {
    content: "";
    position: absolute;
    inline-size: calc(100% - 1.125rem);
    block-size: 1.75rem;
    transform: skew(-45deg);
    pointer-events: none;
}
.city-block::before {
    inset-block-start: -0.5rem;
    inset-inline-start: 0.325rem;
    border-inline-start: solid calc(var(--border)*1.414) hsl(var(--primary-accent-1));
    border-block-start: solid var(--border) hsl(var(--primary-accent-1));
}
.city-block::after {
    inset-block-end: -0.5rem;
    inset-inline-end: 0.325rem;
    border-inline-end: solid calc(var(--border)*1.414) hsl(var(--primary-accent-1));
    border-block-end: solid var(--border) hsl(var(--primary-accent-1));
}

.side-alley {
    color: hsl(var(--light-complement));
    font-size: 0.925em;
    --cut: 2.75rem;
    width: min(calc(var(--main-content-width)/2.5), 47.5%);
    min-height: calc(var(--cut) * 2);
    box-sizing: border-box;
    padding: calc(var(--cut) * .25) calc(var(--cut) * .707);
    float: left;
    position: relative;
    margin: 0.25rem 1rem;
    shape-outside: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
    isolation: isolate;
}
.side-alley::before {
    content: "";
    position: absolute;
    top: 0; left: 0;
    width: 100%; height: 100%;
    background-color: hsl(var(--assist-color));
    clip-path: polygon( 0% var(--cut), var(--cut) 0%, calc(100% - var(--cut)) 0%, 100% var(--cut), 100% calc(100% - var(--cut)), calc(100% - var(--cut)) 100%, var(--cut) 100%, 0% calc(100% - var(--cut)) );
    z-index: -1;
}

.side-alley:where(:not(.right)) {
    margin-left: clamp(calc(var(--main-content-width)/-4.5), calc((90vw - 95%)/-2), 0rem);
}
.side-alley:where(.right) {
    margin-right: clamp(calc(var(--main-content-width)/-4.5), calc((90vw - 95%)/-2), 0rem); 
    float: right;
}

.side-alley :is(h1,h2,h3,h4,h5,h6) {
    color: hsl(var(--light-accent-2));
}

/*-----------------------------*/
/*------ MOBILE QUERY ------*/
/*-----------------------------*/
@media screen and (max-width: 767px) {
    #top-bar div.mobile-top-bar > ul > li {
        position: static;
    }
    #top-bar div.mobile-top-bar > ul > li > ul {
        min-width: unset;
        inset-inline-start: 0;
        transform: none;
        width: 100%;
        flex-wrap: wrap;
        flex-direction: row;
    }
    #top-bar div.mobile-top-bar ul li ul li {
        width: initial;
        flex-basis: 50%;
        flex-grow: 1;
    }
    #top-bar div.mobile-top-bar ul li ul li a:any-link {
        padding: 0.925rem 0.675rem;
    }

}

@media screen and (max-width: 37.5rem) {
    :root {
         --sp_header-height: var(--s-header-mobile-height);
    }

    #header::before {
        margin-inline-start: 0;
        align-self: center;
        height: calc(var(--sp_header-height)*.75);
        width: calc(var(--sp_header-height)*.375);
        clip-path: polygon(0 0, 100% 50%, 0 100%);
        background-size: 72.5%;
        background-position: 0 50%;
    }
    #header :is(h1,h2) { margin-inline-start: calc(var(--sp_header-height)*0.375 + 0.5rem); }
    div[id*="header-extra-div"] {
        -webkit-mask-size: cover;
        mask-size: cover;
    }

    .side-alley {
        float: none;
        margin: 0.5rem auto;
        width: 92.5%;
    }
}
