/*
Superintendent Theme by L200

Based on Commission of Unusual Cargo Theme by CityTosat, based on Aces and Eights Theme by rounderhouse, SCP Sigma 9 Theme created by Aelanna and Dr Devan, and MC&D Theme created by Randomini.

Special thanks to Lekter e Denevola
*/


@import url('https://fonts.googleapis.com/css?family=Basic|IBM+Plex+Mono:400,700');
@import url('https://fontlibrary.org//face/melissa');
@import url('https://fontlibrary.org//face/poorfish');
@import url('https://fontlibrary.org//face/dancing');
@import url('https://fontlibrary.org//face/alex-brush');
@import url('https://fontlibrary.org//face/bilbo');
@import url('https://fontlibrary.org//face/subotype');
@import url('https://fontlibrary.org//face/miama-nueva');
@import url('https://fontlibrary.org//face/vshandprinted');

/*  LINKS  */

a {
  color: #496039;
}
a:visited {
  color: #525B4B;
}

.page-tags a {
  color: #000000;
  padding: 0.1em 0.2em;
}

/* Hyperlink colors */

:root {
    --hyperlink: #383838;
    --hyperlink-visited: #262626;
    --body-font: 'IBM Plex Mono', '新宋体', 'Noto Serif SC', 'Noto Sans SC', monospace;
    --header-font: 'IBM Plex Mono', '新宋体', 'Noto Serif SC', 'Noto Sans SC', sans-serif;
    --title-font: 'IBM Plex Mono', '新宋体', 'Noto Serif SC', 'Noto Sans SC', monospace;
    --mono-font: "IBM Plex Mono", '新宋体', 'Noto Serif SC', 'Noto Sans SC',  monospace;
}

#header {
    background: none; /* this terminates the default header image */
}

#header:before {
    display:block;
    position: absolute;
    width: 120px;
    height: 120px;
    z-index: -1;
    top: 15%;
    left: 2%;
    content: "";
    margin-left: -0.5em;
    background: url("https://scpsandboxcn.wdfiles.com/local--files/pic-trans/SBP-header.png");
    background-repeat: no-repeat;
    background-size: 110px 109px;
    background-position: 0px 0px 0px;
    z-index: 300;
}

#header h1 a::before {
    color: #222222;
    content: var(--header-title, "文件集");
    font-size: 150%;
}

#header h2::before {
    color: #222222;
    content: "巴西异常监管局";
    font-weight: bold;
}


/* BODY */
body {
    background: linear-gradient(rgba(255, 255, 255, 0.5), rgba(255, 255, 255, 0.5)), url('https://scpsandboxcn.wdfiles.com/local--files/pic-trans/SBP-background.jpg');
    font-family: var( --body-font);

}

.main-body-div {
    width: 92.7%;
    line-height: 20px;
    padding-left: 25px;
    padding-right: 25px;
    vertical-align: top;
    transition: all .7s;
    background: linear-gradient(rgba(255,255,255,.5), rgba(255,255,255,.5)), url("https://scpsandboxcn.wdfiles.com/local--files/pic-trans/SBP-background.jpg");
}

#header h1 a {
    color: transparent;
    font-size: 100%;
    padding: 60px 0 25px;
    text-shadow: none;
}

div#container-wrap {
    background: url("") top left repeat-x !important;
}
/* Headers being swapped around from Sigma: the top text is the smaller one for this theme */

#header h2 span {
    font-size: 175%;
    color: transparent;
    text-shadow: none;
}

/* minor color and style tweaks */

table.wiki-content-table {
    border: 1px solid rgba(50, 50, 50, 0.2);
}

table.wiki-content-table th {
    padding: 0.3em 0.7em;
    background-color: rgba(240, 240, 240, 0.2);
}

#search-top-box-input, #search-top-box input.empty,
#search-top-box-form input[type=submit] {
    transition: 0.2s linear;
}

#search-top-box-input, #search-top-box input.empty {
    background-color: transparent;
    border: 1px solid #000;
    border-radius: 0;
    color: #000;
}

#search-top-box-input:hover, #search-top-box-input:focus,
#search-top-box input.empty:hover, #search-top-box input.empty:focus {
    outline: 0;
    color:#fff;
    border: 1px solid #000;
    background-color: #000000;
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}

#search-top-box-form input[type=submit] {
    outline: 0;
    height: 1.65em;
    border: none
    border-radius: 0;
    color: #000;
    background: none;

    position: relative;
    top: -1px;

    box-shadow: none;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: none;
    border-radius: 0;
    background: none;
    color: #000;
}

#search-top-box-form input[type=submit]:active {
    padding: 0 0.5em;
    border: none;
    border-radius: 0;
    color: #000;
    background: #fff;
}

#login-status, #login-status a {
    color: #000;
    border-color: #000;
}
/* stuff with body h groups */

h2, h3, h4, h5, h6 {
    font-family: var(--title-font);
}

h1, #page-title {
    color: #000;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: heavy;
    font-family: var(--title-font);
}
/* remove the blockquote border, adds a shadow and a gradient over the new paper background */
blockquote, div.blockquote, .yui-content blockquote, .yui-content .blockquote, .yui-content .code {
    border: thin solid #ADADAD;
    padding: .5em;
    box-shadow: 0 0.2rem 1.2rem rgba(0,0,0,0.2);
    background: linear-gradient(rgba(255, 255, 255, 0.3), rgba(255, 255, 255, 0.5)), url("https://scpsandboxcn.wdfiles.com/local--files/pic-trans/SBP-report.jpg");
}

/* inputs */
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid #000000;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #636363;
    color: #fff;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #38492C !important; /* top bar hover background color */
}
/* -=- TOP BAR -=- */

#top-bar > div > ul {
    background: linear-gradient(to right, transparent 25%, #496039 50%, transparent 75%, transparent 100%);
}

#top-bar ul li a {
    color: #ffffff;
    text-shadow: 0rem 0rem 0.3125rem #000000;
}

#top-bar ul li:hover a,
#top-bar ul li:hover ul li a {
    background-color: #303F26;
}

#top-bar ul li:hover a:hover {
    background: #303F26;
}

#top-bar .open-menu a {
    background-color: #606060;
    border-color: #303F26 !important;
    color: #ffffff;
}

/* #side-bar: Color changes, removing border, modifying font sizes, and changing general appearance.
Make the left menu look like it's "written" in the left margin */

#side-bar .side-block {
    filter: grayscale(100%);
    padding: 10px;
    border: none;
    border-radius: 10px;
    border-style: solid;
    border-color: transparent; /* no border */
    box-shadow: none !important; /* no shadow */
    background: none !important; /* no background */
    margin-bottom: 15px;
    color: black;
}

/* -=- TOP BAR -=- */

#top-bar > div > ul {
    background: linear-gradient(to right, transparent 25%, #496039 50%, transparent 75%, transparent 100%);
}

#top-bar ul li ul {
    border: 0.0625rem solid #000;
}

#top-bar ul li a {
    color: #ffffff;
    text-shadow: 0rem 0rem 0.3125rem #000000;
}

#top-bar ul li:hover a,
#top-bar ul li:hover ul li a {
    background-color: #465C37;
}

#top-bar ul li:hover a:hover {
    background: #8c465a;
}

/* stuff for social media bar */

#side-bar img {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

#side-bar .heading {
    font-family: var(--body-font);
    font-size: 2em;
    font-weight: bold;
}

#side-bar .menu-item {
    font-family: var(--body-font);
    padding: .5em;
    font-weight: normal;
    box-shadow: none !important;
    margin-bottom: 15px;
    color: black;
}

#side-bar div.menu-item a {
    font-weight:normal;
}

 #side-bar a:visited {
     color: #430;
}
/* #top-bar: Color changes, removing border, modifying font sizes, and changing general appearance */
#top-bar li a{
    text-align: center;
    font-family: var(--body-font);
    display: block;
    font-size: 1.2em;
    margin: 0;
    padding: 1px .5em;
    text-decoration: none;
}

a {
    color: #530;
    text-decoration: none;
    background: transparent;
}

/* Adds the "letter" class that helps align collapsibles with the associated blockquote */
div.letter {
    margin: 1em 3em;
    padding: 1em .5em;
}
/* collapsible color */

/* side-bar collapsible stuff */
.collapsible-block-link {
    color: #4A603B;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #111111;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #111111;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #555555;
}
@media (max-width: 479px) {
    div.letter {
     margin: 1em 0;
     padding: .5em .25em;
    }
}
@media (min-width: 480px) and (max-width: 580px) {
    div.letter {
     margin: 0.5em;
     padding: .25em 0;
    }
}

/* New stamp divs */

.stamp {
     transform: rotate(-13.8deg);
     position: absolute;
}

.right {
     right: 0;
}

.center {
     left: 250px;
}

.left {
     left: 0;
}


.signbox {
   margin: 1em;
   padding: 0.5em;
   border: 1px dashed;
   font-size: 3em;
   width: 75%;
   height: 75px;
   display: flex;
   justify-content: center;
   align-items: center;
   text-align: center;
}

.signbox p { margin: 0 }

.MelissaMedium { font-family: 'MelissaMedium' }
.PoorFishRegular { font-family: 'PoorFishRegular' }
.DancingScriptRegular { font-family: 'DancingScriptRegular' }
.AlexBrushRegular { font-family: 'AlexBrushRegular' }
.BilboSwashCapsRegular { font-family: 'BilboSwashCapsRegular' }
.SubotypeSteady { font-family: 'SubotypeSteady' }
.MiamaNuevaMedium { font-family: 'MiamaNuevaMedium' }
.vSHandprintedMedium { font-family: 'vSHandprintedMedium'}


.photo {
  background: #fff;
  float:right;  
  clear:both;
  margin: 1rem;
  padding: 1rem;
  padding-bottom: 40px;
  box-shadow: 0 0.2rem 1.2rem rgba(0,0,0,0.3);
  max-width: 100%;
  height: auto;
  width: 30%;
  display: inline-block;
  margin-top: 2rem;
  filter: grayscale(100%);
  transform: rotate(-2.5deg);
}

.report {
   display: inline-block;
   border: solid 1px #444444;
   background: linear-gradient(rgba(255, 255, 255, 0.3), rgba(255, 255, 255, 0.5)), url("https://scpsandboxcn.wdfiles.com/local--files/pic-trans/SBP-report.jpg");
   margin-left: auto;
   margin-right: auto;
   width: auto;
   padding: 10px;
   font-family: 'Courier';
}

.classified {
    transform: rotate(18.5deg);
    color: #BA1616;
    font-size: 3rem;
    font-weight: 700;
    font-family: 'Courier';
    filter: opacity(50%);
    position: absolute;
    display: inline-block;
    padding: 15px;
    background-color: white;
    box-shadow:inset 0px 0px 0px 10px #BA1616;
    -webkit-mask-image: url('https://scpsandboxcn.wdfiles.com/local--files/pic-trans/SBP-classified.png');
    -webkit-mask-size: 944px 604px;
    mix-blend-mode: multiply;
}

/* YUI-TABS */ /*taken from -RU, credit needed. Likely made by Arbelict*/
@font-face {
    font-family: var( --body-font);
}

.yui-navset .yui-nav li a em {
  font-family: var( --body-font);
  border: none;
}

.yui-navset .yui-nav .selected em {
 padding: 0.25em 0.75em !important;
}

.yui-navset .yui-nav li {
  margin-right: -25px !important;
  margin-bottom: 0 !important;
}

.yui-navset .yui-nav li a {
  border-radius: 20% 0 0 0;
  transition: 0.5s;
  background-color: #FFD59B;
  padding-right: 20px;
  transition-property: margin, padding;
}

.yui-navset .yui-nav li a:hover {
  margin-top: -5px;
  padding-bottom: 5px;
  transition-duration: 1s;
}
/* This removes the border around tab images. */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: transparent;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #291f1f;
}
.yui-navset li {
    line-height: normal;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
    border-color: none;
    border-width: 0;
    bottom: 0;
    left: 0;
    padding: 0.25em 0.75em;
    position: relative;
    right: 0;
    top: -1px;
}
.yui-navset .yui-content{
    background-image: url('https://scpsandboxcn.wdfiles.com/local--files/pic-trans/SBP-tab.jpg');
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color:#FFD59B;
    background-image: url(../images/yuitabs.png);
