}

.owindow.owait .content p::before {
    content: "保存世界中 . . . ";
    font-size: var(--global-font-size);
}

.owindow.owait .content::after {
    background: linear-gradient(to bottom, LawnGreen 0%, LawnGreen 30%, SaddleBrown 30%, SaddleBrown 100%, Brown 100%);
}
