/*
    SCP Sigma 9: Taisho 150 Theme
    [2020 Wikidot Theme]
    Created by Mishary
    Edited by ukwhatn
    SCP Sigma 9 was Created as for the SCP Foundation by Aelanna and Edited for SCP財団 by Dr Devan, NanimonoDemonai
    The font Gentium Basic is under Open Font License
    CC BY-SA 3.0
*/
/*
  Sigma9 Customizable Header
  --author: ukwhatn
  --source: http://scp-jp-sandbox3.wikidot.com/draft:3396310-168-9905
  --license: CC BY-SA 3.0 ( http://creativecommons.org/licenses/by-sa/3.0 )
*/
div#container-wrap {
    background: linear-gradient(180deg, #ebe0ce 22%, #954039 22%, #954039 86.5%, #ebe0ce 86.5%, #ebe0ce 100%) top left repeat-x;
    background-size: 100px 162px;
}
#header h1, #header h2{
    direction: rtl;
    float: unset;
    margin-left: auto;
    width: 280px;
    margin-right: auto;
}
div#header h1 a{
    text-shadow: unset;
}
div#header h1 a:before {
    font-family: serif;
    direction: rtl;
    unicode-bidi: bidi-override;
    content: "大正百伍拾年";
    color: #ebe0ce;
}
div#header h1 a span {
    font-size: 0px;
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    font-family: serif;
    content: "皇紀二七二一年";
    direction: rtl;
    unicode-bidi: bidi-override;
    font-weight: bold;
    color: #ebe0ce;
    padding: 19px 0;
    margin-right: 15px;
    white-space: pre;
}
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px;
    background: unset;
}

#top-bar a {
    color: #6a1435;
}
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #ebe0ce;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid #ddd;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #fdd2b9;
    color: #da003d;
}
/* SEARCHBOX */

#search-top-box-input {
    border: solid 1px #ebe0ce;
    border-radius: 5px;
    color: #ebe0ce;
    background-color: #932E40;
    box-shadow: unset;
}

#search-top-box-form input[type=submit]{
    color: #ebe0ce;
    background: #932E40;
}
div#search-top-box {
    top: 7px;
    width: 100%;
    right: 100px
}

div#search-top-box form {
    display: inline-flex;
    justify-content: flex-end
}

div#search-top-box form input[type="text"],
div#search-top-box form input[type="text"]:hover {
    background-color: transparent;
    display: inline-block;
    height: 1.1rem;
    width: 8rem;
    transition: width .25s;
    outline: double #000;
}
#search-top-box-input:hover, #search-top-box-input:focus{
    border: solid 1px #000;
}

div#search-top-box form input[type="text"]:not(:focus),
div#search-top-box form input[type="text"]:not(:focus):not(:hover) {
    background-color: transparent;
    color: transparent;
    cursor: pointer;
    display: inline-block;
    width: 22px;
    height: 22px;
    box-sizing: border-box;
    border-radius: 24px;
    box-shadow: none;
    background-position: center;
    background-size: 100%;
    background-repeat: no-repeat;
    background-image: url("data:image/svg+xml;charset=utf8,%3C%3Fxml%20version%3D%221.0%22%20encoding%3D%22UTF-8%22%20standalone%3D%22no%22%3F%3E%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%2213%22%20height%3D%2213%22%20viewBox%3D%220%200%203.44%203.44%22%3E%3Cg%20fill%3D%22%23932e40%22%3E%3Cpath%20d%3D%22M.53%202.249V.132l1.057%201.059v2.116zM2.91%202.249V.132L1.852%201.191v2.116zM1.587%200h.265v1.19h-.265M1.587%203.307h.265v.133h-.265%22%2F%3E%3C%2Fg%3E%3C%2Fsvg%3E");
}

div#search-top-box form input[type="submit"] {
    display: none;
}

#search-top-box-input {
    border: solid 1px #000;
    border-radius: 5px;
}
div#search-top-box::after {
    content: "☜搜索";
    display: inline-flex;
}
/* LOGIN */
#login-status{
    top: 7px;
    right: 0px;
}
#login-status, #login-status a, #login-status ul a {
    color: #6a1435;
}
#login-status .printuser {
    margin: 0;
    font-size: 0;
    position: absolute;
    top: unset;
    right: -14px;
    width: 100px;
    padding: 0;
}
#login-status .printuser img.small {
    background: none !Important;
    margin: 0;
    width: 20px;
    height: 20px;
    border: solid 1px #000;
    border-radius: 10px;
    outline: double #000;
    padding: 0;
}
#my-account{
    display:none;
}
#account-topbutton, #account-options{
    background-color: #ebe0ce;
    border: 1px solid #5c3b54;
}
#account-topbutton{
    padding: unset;
}
#login-status>a[href="javascript:;"] {
    margin: 0;
    text-align: center;
    font-size: 0;
    text-decoration: none;
    background: none;
    position: absolute;
    right: 29px;
    width: 22px;
    height: 22px;
    top: -1px;
    border: 1px solid #000;
    border-radius: 10px;
    outline: double 3px #000;
    outline-offset: -1px;
}
#login-status>a.login-status-create-account.btn {
    right: 25px;
}
#login-status>a.login-status-sign-in.btn {
    right: 60px;
}
#login-status>a[href="javascript:;"]:before {
    display: inline-block;
    font-family: serif;
    font-style: bold;
    font-size: 1rem;
    margin: auto;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    transform: translate(0, 0);
}
#login-status #account-topbutton:before{
    content:"詳";
}
#login-status .login-status-sign-in:before{
    content:"入";
}
#login-status .login-status-create-account:before {
    content:"登";
}
#login-status .login-status-sign-in #text, #login-status .login-status-create-account #text{
    font-size:0;
}
#login-status span {
    font-size: 0;
}

/* SIDE */
#side-bar div.menu-item img {
    display: unset;
}

#toc, #side-bar .side-block{
    border: solid 1px #000;
    position: relative;
    outline: double 3px #000;
    border-radius: 10px;
    outline-offset: -1px;
    background: #fff !important;
    box-shadow: unset;
}
#toc:after, #side-bar .side-block::after {
    content: "";
    border: solid 2px #000;
    position: absolute;
    top: -6px;
    right: -6px;
    width: calc(100% + 8px);
    height: calc(100% + 8px);
    border-radius: unset;
    z-index: -1;
}
#side-bar .heading {
    color: #243150;
    border-bottom: solid 1px #600;
    padding-left: 15px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #243150;
}

/* RATE */
.page-rate-widget-box {
    outline: double #243150;
    outline-offset: -1px;
    border-radius: 10px;
    border: solid 1px #243150;
    box-shadow: none;
}
.page-rate-widget-box .rate-points{
    background-color: transparent !important;
    border: unset;
    color: #243150;
}
.rateBox .page-rate-widget-box .rate-points, #action-area .page-rate-widget-box .rate-points{
    color: #243150;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: transparent;
    border: unset;
    font-weight: bold;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a{
    color: #243150;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover{
    background-color: #243150;
    color: #fff;

}
.page-rate-widget-box .cancel {
    background-color: transparent;
    border: unset;
}
.page-rate-widget-box .cancel a {
    color: #243150;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 10px 10px 0;
    background-color: #243150;
    color: #fff;
}
#page-content .rate-box-with-credit-button {
    outline: double #243150;
    outline-offset: -1px;
    background-color: transparent;
    border-radius: 10px;
    border: solid 1px #243150;
    box-shadow: none;
}
#page-content .rate-box-with-credit-button .page-rate-widget-box {
    outline: none;
    border: none;
}
#page-content .rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}
#page-content .rate-box-with-credit-button .creditButton p a {
    color: #243150;
    border-radius: 0 10px 10px 0;
    border-left-color: #243150;
}
#page-content .rate-box-with-credit-button .creditButton p a:hover {
    background-color: #243150;
    color: #fff;
}
#page-content .creditButtonStandalone p a {
    outline: double #243150;
    outline-offset: -1px;
    background-color: transparent;
    color: #243150;
    border-radius: 10px;
    border: solid 1px #243150;
    box-shadow: none;
}
#page-content .creditButtonStandalone p a:hover {
    background-color: #243150;
    color: #fff;
}
/* BODY */
body {
    font-family: serif;
}
h1, #page-title{
    color: #DA003D;
    font-family: serif;
}
.taisho1{
    border: solid 1px #000;
    position: relative;
    outline: double 3px #000;
    border-radius: 10px;
    outline-offset: -1px;
    background: #fff;
    box-shadow: unset;
    padding: 3px;
}
.taisho1::before {
    content: "";
    border: solid 2px #000;
    position: absolute;
    top: -6px;
    right: -6px;
    width: calc(100% + 8px);
    height: calc(100% + 8px);
    border-radius: unset;
    z-index: -1;
}
.Oradano{
  font-family: OradanoGSRR, serif;
}


/* TAB */
.yui-nav, .yui-nav li {
    margin: 2px;
    padding: 2px;
}
.yui-navset .yui-nav li, .yui-navset .yui-navset-top .yui-nav li {
    margin: 0 0.16em 0 0;
    padding: 1px 0 0;
    zoom: 1;
    border: solid 1px #000;
    position: relative;
    outline: solid 1px #000;
    border-radius: 10px;
    outline-offset: -1px;
    background: transparent;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    background-color: transparent;
    border: unset;
    background-image: unset;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
    border: unset;
}
.yui-navset .yui-nav .selected, .yui-navset .yui-navset-top .yui-nav .selected {
    border: solid 1px #000;
    position: relative;
    outline: double 3px #000;
    border-radius: 10px;
    outline-offset: -1px;
    background: transparent;
    margin: 0 4px -1px 2px;
}
.yui-navset .yui-nav a:hover, .yui-navset .yui-nav a:focus{
    background: transparent;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
    background: transparent;
    color: #000;
    border: unset;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a em {
    border: unset;
}
.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav{
    border: unset;
}
.yui-navset .yui-content {
    border: solid 1px #000;
    position: relative;
    outline: double 3px #000;
    border-radius: 10px;
    outline-offset: -1px;
    margin: 2px;
    margin-top: 4px;
}
[[/code]]
[[code type="css"]]
