[[code type="CSS"]]

@import url('https://fonts.googleapis.com/css2?family=ZCOOL+QingKe+HuangYou&display=swap');

@font-face {
    font-family: "CalacasFont";
    src: url('https://scp-resources.s3-us-west-2.amazonaws.com/fonts/Raven+Scream.ttf');
}

:root {
    --bg-calacas: url('http://merlin-scp.wdfiles.com/local--files/theme%3Acalaca-team/bgcalacas');
    --header-text: "骷髅小队";
    --bg: #111111;
    --bg-medium: #424242;
    --fg: #efefef;
    --fg-muted: #666;
    --fg-accent-light: #e3bc7d;
    --fg-accent: #e4d1b2;
    --fg-accent-dark: #dbcfbb;
    --fg-accent-2-light: #e3bc7d;
    --fg-accent-2: #e4d1b2;
    --fg-accent-2-dark: #dbcfbb;
}

div#container-wrap:before {
    content: "";
    display: none;
    width: 100%;
    height: 45px;
    position: absolute;
    background-color: #6b6b6b;
}

#login-status>*,
#login-status>*>*,
.printuser {
    color: var(--fg);
}

body,
div#container-wrap {
    background: var(--bg-calacas) center no-repeat, linear-gradient(var(--bg), #2e2d2d, var(--bg) 20%, var(--bg-medium), var(--bg)) repeat-x, var(--bg);
    background-size: 40vw 40vw, 1px 70%;
    color: var(--fg);
}

div#content-wrap {
    margin-top: 3em;
}


/* #content-wrap {
    background: linear-gradient(var(--bg-medium), var(--bg)) repeat-x, var(--bg);
    background-size: 1px 30%;
    color: var(--fg);
} */

h1,
h2,
h3,
h4,
h5,
h6,
div#page-title {
    color: var(--fg-accent);
}

a,
#side-bar a {
    color: var(--fg-accent);
    transition: 0.2s;
}

#side-bar a {
    color: var(--fg-accent-2)
}

a:visited,
#side-bar a:visited {
    color: var(--fg-accent-dark);
}

#side-bar a:visited {
    color: var(--fg-accent-2-dark)
}

a:hover,
#side-bar a:hover,
a:focus,
#side-bar a:focus {
    color: var(--fg-accent-light);
}

#side-bar a:hover,
#side-bar a:focus {
    color: var(--fg-accent-2-light);
}

del {
    color: #660000;
}

ins {
    color: #006600;
}


/* header */

#header {
    background: none;
    padding-top: 40px;
    position: relative;
}

#header>h1,
#header>h2 {
    position: absolute;
    left: 0;
}

#header h1 {
    display: block;
    height: 130px;
    max-height: 130px;
    width: 100%;
    max-width: 550px;
    position: relative;
    top: 32px;
    padding-top: 0;
    margin-left: 15rem;
}

#header h2 {
    display: none;
}

#header>h1::before {
    content: "";
    display: block;
    width: 15rem;
    height: 9.5rem;
    position: absolute;
    left: -15rem;
    background: url('http://lafundacionscp.wdfiles.com/local--files/theme%3Ateam-calacas/calacaz.png') no-repeat center;
    background-size: contain;
    bottom: 0;
}

#header h1 a {
    /* display: block; */
    /* margin: 0;
    padding: 0; */
    height: 130px;
    max-height: 130px;
    width: 100%;
    max-width: 550px;
    padding: 0;
    position: relative;
}

#header>h1>a>span {
    display: block;
    visibility: hidden;
}

#header>h1>a>span::after {
    font-family: 'CalacasFont', 'ZCOOL QingKe HuangYou', BauhausLTDemi, Arial, Helvetica, sans-serif;
    letter-spacing: 10px;
    font-size: 2rem;
    height: 5rem;
    width: 800px;
    position: absolute;
    content: var(--header-text);
    visibility: visible;
    top: 3rem;
    left: 0;
    display: block;
}


/* top bar */

#top-bar {
    margin-top: 60px;
    border-bottom: 1px solid var(--fg-muted);
    padding-top: 4px;
    padding-bottom: 4px;
}

#top-bar a {
    transition: unset;
}

#top-bar ul li ul {
    border-color: var(--fg-accent-dark);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top-color: var(--fg-accent-dark);
    border-color: var(--fg-accent-dark);
}

#top-bar ul li ul a,
#top-bar a:hover {
    color: var(--fg-accent);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    border-color: var(--fg-accent-dark);
    color: var(--fg-accent);
    background-color: var(--bg-medium);
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    border-color: var(--bg-medium);
    color: var(--bg);
    background-color: var(--fg-accent);
}


/* side bar */

#side-bar .side-block {
    background-color: transparent !important;
    border: 1px solid var(--fg-accent-2-light);
    box-shadow: 0 2px 6px var(--fg-accent-2-dark);
}

#side-bar .heading {
    color: var(--fg-accent-2-light);
    border-bottom: 1px solid var(--fg-accent-2-light);
}

#side-bar .menu-item img {
    display: none;
}

#side-bar .menu-item::before {
    display: inline;
    position: relative;
    top: -1px;
    font-size: 10px;
    content: "> ";
    color: var(--fg-accent-2-dark);
}


/* side bar: social media links workaround */

#side-bar .side-block[style="background-color: #e5e5ff;"] .menu-item::before {
    content: "";
}

#side-bar .side-block[style="background-color: #e5e5ff;"] img {
    display: inline;
}


/* side bar: user resources workaround */

#side-bar .side-block[style="background-color: #fff0f0;"] div:nth-child(2) {
    display: none;
}

#side-bar .collapsible-block-folded {
    background: none;
}
#side-bar .collapsible-block-folded .collapsible-block-link {
    margin-left: 0;
}
#side-bar .collapsible-block-folded::before {
    display: inline;
    position: relative;
    top: -1px;
    font-size: 10px;
    content: "> ";
    color: var(--fg-accent-2-dark);
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: var(--fg-accent-2-light);
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: var(--fg-accent-2-dark);
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: 1px solid var(--fg-accent-2-light);
}


/* rating module */

.page-rate-widget-box .rate-points {
    background-color: var(--fg-accent-2-dark) !important;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
    background-color: var(--fg-accent-2-dark);
    color: var(--bg);
    border: 0;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .cancel {
    background-color: var(--bg);
    border: 0;
    border-left: 1px solid var(--fg-accent-2-light);
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .cancel a {
    border: 0;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background-color: var(--fg-accent-2-dark);
    color: var(--bg);
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover,
.page-rate-widget-box .cancel a:hover {
    background-color: var(--fg-accent-2-light);
    color: white;
}


/* footnote hovers */

.hovertip {
    width: 20em;
    padding: 0.5em;
    border: 1px solid var(--fg-accent-2-light) !important;
    border-radius: 0.5em;
    background-color: #000000ee !important;
    box-shadow: 0 0 6px 1px #000000aa;
    z-index: 100;
}

.hovertip .f-heading {
    color: var(--fg-accent-2-light);
}


/* inputs */

input.text,
input.button,
#search-top-box-input,
#search-top-box-form input[type=submit],
div.buttons input,
input.button,
button,
file,
a.button {
    background: transparent;
    color: var(--fg);
    border: 1px solid var(--fg-accent-2-dark);
    border-radius: 0;
    font-size: 1em;
    outline: 0;
}

div.buttons input:hover,
div.buttons input:focus,
input.button:hover,
input.button:focus,
button:hover,
button:focus,
file:hover,
file:focus,
a.button:hover,
a.button:focus {
    border-color: var(--fg-accent-2-light);
    background: transparent;
    color: var(--fg);
}

input.text:hover,
input.button:hover,
input.text:focus,
input.button:focus,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    color: var(--fg);
    background: transparent;
    border: 1px solid var(--fg-accent-2-light);
    outline: 0;
}


/* wikidot page elements */

#page-title {
    border-color: var(--fg-muted);
}

table.wiki-content-table th,
table.wiki-content-table td,
#toc,
blockquote,
div.blockquote,
.scp-image-block .scp-image-caption {
    border-color: var(--fg-muted);
    background-color: transparent;
}

table.wiki-content-table th {
    background-color: var(--bg-medium);
}

hr {
    background-color: var(--fg-muted);
}

.code {
    border: 1px dashed var(--fg-muted);
    background-color: transparent;
    font-family: "Consolas", "Courier New", "Roboto Mono", monospace;
    font-size: 14px;
}


/* user login */

#login-status {
    color: var(--bg);
}

#login-status a {
    border: 0;
}


/* footer */

#footer,
#license-area {
    background: var(--fg-accent);
    color: #111;
}

#license-area {
    background: var(--fg-accent-dark);
}

#footer a,
#license-area a {
    color: #fff;
}


/* code highlighter stuff */

.hl-identifier,
.hl-code,
.hl-brackets {
    color: var(--fg);
}

.hl-var {
    color: #21ff00;
}

.hl-special {
    color: #ffff00;
}

.hl-reserved {
    color: #00b2ff;
}

.hl-number,
.hl-string {
    color: #ff0020;
}


/* tabs */

.yui-navset .yui-nav a,
.yui-navset .yui-nav a em {
    border-color: var(--bg-medium);
    transition: unset;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: var(--fg-accent);
}

.yui-navset .yui-nav a,
.yui-navset .yui-nav a:focus,
.yui-navset .yui-nav a:hover {
    color: var(--fg);
    background: var(--bg);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: var(--bg);
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border: 0;
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    background-color: var(--bg);
    border-color: var(--fg-accent);
}


/* popups */

.owindow {
    background-color: var(--bg);
    border-color: var(--fg-accent);
}

.title.modal-header {
    background-color: var(--bg);
}

.owindow .button-bar a {
    background-color: transparent;
    border-color: var(--fg-accent);
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: transparent;
    border-color: var(--fg-accent-light);
}


/* editor */

textarea {
    font-family: "Verdana", sans-serif;
    padding: 2px;
    font-size: 100%;
    border: 1px solid var(--fg-accent-2);
    background-color: var(--bg);
    color: var(--fg);
    outline: 0;
}

textarea:hover,
textarea:focus {
    border-color: var(--fg-accent-2-light);
}

.change-textarea-size a {
    background: transparent;
    color: var(--fg);
    border-color: var(--fg-accent-2)
}

.change-textarea-size a:hover,
.change-textarea-size a:focus {
    border-color: var(--fg-accent-2-light);
    text-decoration: none;
}

.edit-help-34 a {
    color: var(--fg-accent-2);
}

.edit-help-34 a:visited {
    color: var(--fg-accent-2-dark);
}

.edit-help-34 a:hover,
.edit-help-34 a:visited:hover {
    color: var(--fg-accent-2-light);
}

input.text,
input.button,
#search-top-box-input,
#search-top-box-form input[type=submit],
div.buttons input,
input.button,
button,
file,
a.button {
    background: transparent;
    color: var(--fg);
    border: 1px solid var(--fg-accent-2-dark);
    border-radius: 0;
    font-size: 1em;
    outline: 0;
}

div.buttons input:hover,
div.buttons input:focus {
    border-color: var(--fg-accent-2-light);
    background: transparent;
    color: var(--fg);
}

#lock-info {
    background: transparent;
    border: 0;
}

#lock-info #lock-timer {
    color: var(--fg-accent-2-light);
}


/* home page elements */

.content-panel,
.content-panel .panel-body,
.international-entry:nth-child(even),
.alternate:nth-child(even) {
    box-shadow: unset;
    background: #141414;
    color: var(--fg);
}

.news-block .news-title>p,
.news-block .news-content>p,
.feature-block .feature-title>p,
.feature-block .feature-subtitle>p {
    color: var(--fg);
}

div[style="text-align: center; color: #600;"] {
    color: var(--fg-accent) !important;
}


/* mobile rules */

@media (max-width: 630px) {
    body,
    div#container-wrap {
        background: var(--bg-calacas) center no-repeat, linear-gradient(var(--bg), #2e2d2d, var(--bg) 20%, var(--bg-medium), var(--bg)) repeat-x, var(--bg);
        background-size: 40rem 40rem, 1px 70%;
        color: var(--fg);
    }
    #side-bar,
    #side-bar:target {
        border: 0;
        background: var(--bg);
    }
    #search-top-box {
        top: 174px;
    }
}

[[/code]]
