[[module CSS]]

/* 时间矫正部门版式 */


/* 顶栏 */

#header {
    background-image: url('https://scpfoundation.net/local--files/theme:temporary-infallibility/logo_quiet.svg');
    background-size: 12%;
}
#header h2 span::before {
    content: '时间矫正部门';
    color: rgb(240, 240, 192);
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
}
#header h2 span {
    color: transparent;
    text-shadow: none;
}

/* 现行顶栏 */

#extra-div-1 {
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 8.7rem;
  background: url('https://scpfoundation.net/local--files/theme:temporary-infallibility/texture_header.png') repeat-x;
  background-size: contain;
}
#extra-div-2 {
  display: block;
  position: absolute;
  top: 10.2rem;
  left: 0;
  width: 100%;
  height: 5rem;
  background: url('https://scpfoundation.net/local--files/theme:temporary-infallibility/texture.png') repeat-x;
  background-size: 35rem;
  z-index: -1;
  -webkit-mask-image: -webkit-gradient(linear, left top, left bottom, from(rgba(0,0,0,1)), to(rgba(0,0,0,0)));
  opacity: 0.6;
}

/* 搜索颜色 */

#search-top-box-input {
  background-color: #2b0a0a;
}

#search-top-box-form input[type="submit"] {
  background-image: linear-gradient(to bottom, #583d3d, #633, #300);
}

/* Дополнительное */

.future {
  background: #000 url('https://files.scpfoundation.net/local--files/draft:kardalak/futuredeep001.png') bottom no-repeat;
  float: center;
  border: solid 2px #000;
  padding: 1px 15px;
  box-shadow: 0 1px 3px rgba(0, 0, 0, .2);
  font-size: 120%;
  color: white;
  margin: 2rem 0;
}

.wide-spoiler .collapsible-block {
    margin: 16px 0;
    border: 1px solid #dcdcdc;
    background: white;
    box-shadow: 0 2px 3px #ccc;
    transition: all 0.3s ease;
}
.wide-spoiler .collapsible-block:hover {
    border: #505052 solid 1px;
    box-shadow: 0 2px 3px #ccc;
}
.wide-spoiler .collapsible-block-content {
    margin: 16px 24px;
}
.wide-spoiler .collapsible-block-unfolded-link {
    text-align: center;
}
.wide-spoiler .collapsible-block-folded {
    text-align: center;
}
.wide-spoiler .collapsible-block-link {
    font-weight: bold;
    text-transform: uppercase;
    text-align: center;
    padding: 18px 24px;
    display: block;
}
/* 以下部分于spoiler删除spoiler边框 */
.wide-spoiler .collapsible-block .collapsible-block {
    border: none;
    box-shadow: none;
}
/* 这个是于spoiler删除spoiler内部的空格 */
.wide-spoiler .collapsible-block-content .collapsible-block-content {
    margin: 0px;
}

.left {
   margin: 10px 0px;
   padding: 10px 15px; 
   border-left: solid 3px maroon;
   background-color: #ECEFF1;
}

/* 移动端 */

@media (max-width: 767px) {
#header {
  background-size: 20%;
}
#extra-div-2 {
  height: 6.7rem;
}
}
[[/module]]

