[[collapsible show="源代码" hide="源代码"]]
[[code type="CSS"]]
/*
   深海恐惧症（Sigma）版式
   [2022 Wikidot Theme]
   作者：TheDarkArtist
   深海恐惧症版式的 Sigma-9 版本，以及半影的拓展版式。
*/

:root {
   --lgurl: url(https://scp-wiki.wdfiles.com/local--files/theme%3Athalassophobia-sigma/logo);
   --header-title: "72 号深海站";
   --header-subtitle: "守护深海于光明之下";
   --accentColor: #E8BA56;
   --fnColor: #00AABD;
}

#header h1 a::before{
     font-weight: bold;
     color: var(--accentColor);
     text-shadow: 2px 0 0 #000, -2px 0 0 #000, 0 2px 0 #000, 0 -2px 0 #000, 2px 2px #000, -2px -2px 0 #000, 2px -2px 0 #000, -2px 2px 0 #000;
}

#header h2::before{
     font-weight: bold;
     color: var(--accentColor);
     text-shadow: 2px 0 0 #000, -2px 0 0 #000, 0 2px 0 #000, 0 -2px 0 #000, 2px 2px #000, -2px -2px 0 #000, 2px -2px 0 #000, -2px 2px 0 #000;
}

body {
    background: linear-gradient(-90deg, #020411, #000000, #020411, #000000);
    color: var(--accentColor);
}

::selection {color: #020411;}

div#container-wrap {
     background-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Athalassophobia-sigma/gradient);
}

div#extra-div-1 {
    background: none;
}

 #header::before{
     opacity: 80%;
}

#page-title {
    display: none;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: var(--accentColor);
}

blockquote,
div.blockquote,
#toc {
    background-color: transparent;
    border: solid 1px #3a3a40;
    box-shadow: 0px 0px 0px -0.1px var(--accentColor);
}

a,
a.newpage,
#side-bar a,
#sidebar a:visited {
    color: #00AABD;
    transition: none;
}

a:visited {
    color: rgb(1, 131, 157);
    transition: none;
}

a:hover, a.newpage:hover, a:visited:hover, #side-bar a:visited:hover {
    color: #00AABD;
    background: transparent;
    text-decoration: underline;
}

.fncon {
    background: linear-gradient(-90deg, #010507, #000000, #001012, #020411);
    border: solid 1px #3a3a40;
}

/* ---- Link Modifier ---- */
 .yui-navset .yui-nav a,
 .yui-navset .yui-navset-top .yui-nav a{
     color: var(--accentColor);
     background-color: #010507;
     border-bottom: 3px solid var(--accentColor);
}

 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
     color: #010507;
     background-color: var(--accentColor);
}

 .yui-navset .yui-content {
    background: transparent;
    border-bottom: 3px dotted var(--accentColor);
}

.darkbox {
    background-color: #010507;
    border-left: solid 0.26rem #6E4B00;
    border-right: solid 0.26rem #6E4B00;
}

.lightbox {
    border-left: solid 0.26rem #BA7F00;
    border-right: solid 0.26rem #BA7F00;
}

.hovertip {
    font-size: .9rem;
    background-color: #010507 !important;
    border: solid 1px #BA7F00 !important;
}

.footnotes-footer {
    background-color: #010507;
    box-shadow: -0.24rem 0px 0px 0px #6E4B00;
}

.footnotes-footer .title {
    color: var(--accentColor);
}

.scp-image-block {
    border: solid 8px #010507;
    border-bottom: solid 0px #010507;
    box-shadow: 0px 0.26rem 0px 0px var(--accentColor);
    box-sizing: border-box;
    background: linear-gradient(-90deg, #080808, #001012, #080808, #001012);
}

.scp-image-block .scp-image-caption {
    background-color: #010507;
    border: solid 4px #010507;
    color: var(--accentColor);
    font-size: 0.84rem;
}

.page-rate-widget-box {
     border: solid 1px #3a3a40;
     background-color: #010507;
}

div.page-rate-widget-box .rate-points {
    background-color: #010507 !important;
    color: var(--accentColor) !important;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #010507;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: var(--accentColor);
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #000000;
    color: #00AABD;
}

.page-rate-widget-box .cancel {
    background-color: #010507;
}

.page-rate-widget-box .cancel a {
    color: var(--accentColor);
}

.page-rate-widget-box .cancel a:hover {
    background: #000000;
    color: #00AABD;
}

.page-tags span {
    border-top: 1px solid var(--accentColor);
}

#page-content .wiki-content-table tr th {
    border: solid 1px var(--accentColor);
    color: #010507;
    background-color: var(--accentColor);
}

#page-content .collapsible-block-folded, #page-content .collapsible-block-unfolded-link {
    background: #010507;
    border: solid 1px #3a3a40
}

#page-content .collapsible-block-link {
    color: var(--accentColor);
}

#page-content .collapsible-block-unfolded-link {
    box-shadow: 0px -0.26rem 0px 0px var(--accentColor);
}

.sidebox {
    background-color: #010507;
}

@media (max-width: 1290px) {
    .sidebox::after {
        background-color: #010507;
    }
}

::-webkit-scrollbar-track {
  background: #010507;
}

::-webkit-scrollbar-thumb {
  background: #00AABD;
}

#top-bar, #top-bar a {
     color: var(--accentColor);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
   border-top-color: #010507;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #010507;
    color: var(--accentColor);
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--accentColor);
    color: #010507;
}

#search-top-box-form > input[type=submit] {
    border: solid 1px var(--accentColor);
    background: #010507!important;
    color: var(--accentColor);
}

.scale, .level-text, .class-text, .itemnum {
   color: var(--accentColor)!important;
}

.obj-text {
   color: #010507;
}

div.scale {
    --woedbar-lvl1-color: var(--accentColor);
    --woedbar-lvl2-color: var(--accentColor);
    --woedbar-lvl3-color: var(--accentColor);
    --woedbar-lvl4-color: var(--accentColor);
    --woedbar-lvl5-color: var(--accentColor);
    --woedbar-lvl6-color: var(--accentColor);
    --woedbar-class-bar-color: var(--accentColor);
}

div#side-bar{
    background: #010507;
}

#side-bar .heading{
    color: var(--accentColor);
    border-bottom: solid 1px var(--accentColor);
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources {
    background-color:#010507;
}

#top-bar div.open-menu a {
    background-color: #010507;
    border: solid 1px #010507;
}

#side-bar .collapsible-block-unfolded {
    color: #00AABD;
}

.fnnum:hover::after {
    color: #010507;
}

.fncon::before {
    color: #010507;
}

div#side-bar .collapsible-block-unfolded-link,
div#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #00AABD;
    border-color: #00AABD;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #00AABD;
}
[[/code]]
[[/collapsible]]
[[/iftags]]



