[[module CSS]]
:root {
    --header-title: "ABYSSAL AREA-7";
    --header-subtitle: "OPUS EST MAIOR NAVI";
    --colorTwo: #00AABD;
}

/* ----------- */
.bt {font-family: 'Josefin Sans', sans-serif; text-transform: uppercase; color: #00aabd;}
h4 {color: #00aabd;}
.email h6 {color: #00aabd; border-bottom: #00aabd 1px solid;}

hr {background-color: transparent; border-bottom: 1px solid #00aabd;}
.darkbox {background-color: transparent;}
.darkbox hr {border-color: #6e4b00;}

/* ---------- */
.document {
    background: white;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
    margin: 26px auto 0; max-width: 550px;
    min-height: 300px;
    padding: 24px;
    padding-top: 25px;
    position: relative;
    width: 80%;
    color: #333333;
    --fnColor: #333399;
}

.document:before,
.document:after {
    content: "";
    height: 98%;
    position: absolute;
    width: 100%;
    z-index: -1;
}

.document:before {
    background: #fafafa;
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.2);
    left: -5px;
    top: 4px;
    transform: rotate(-2.5deg);
}

.document:after {
    background: #f6f6f6;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.2);
    right: -3px; top: 1px;
    transform: rotate(1.4deg);
}

.document hr {border-bottom: 1px solid #333333;}
.document .bt {color: #333333;}
.document a {color: #333399;}

.document .fnnum:hover::after {color: #fefefe;}
.document .fncon {background: #fefefe;}
.document .fncon::before {color: #fefefe;}

.document h1 {margin-bottom: 0px}
.document *::selection {
    background-color: #333399;
    color: white;
}

/* ----------- */
::selection {color: #020408;}
.page-rate-widget-box {box-shadow: -3px 0px 0px 0px var(--accentColor), 3px 0px 0px 0px var(--accentColor);}


/* ---------- */
.mobile {display: none;}
@media (max-width: 768px) {
    .mobile {
        display: unset;
    }
    .pc {
        display: none;
    }
}
[[/module]]
