[[collapsible show="+ 源代码" hide="- 源代码"]]
[[code type="CSS"]]
/*
   Thalassophobia Theme
   [2021 Wikidot Theme]
   by Fish^12
          Glory to the Homeland.
*/

@import url('https://fonts.googleapis.com/css2?family=Anonymous+Pro&family=Miriam+Libre:wght@700&display=swap');

::selection {
  background: #1745a9;
}

body {
    background: linear-gradient(-90deg, #010507, #000000, #001012, #020411);
    background-size: 150% 1%;
    animation: gradient 10s ease infinite;
}

@keyframes gradient {
    0% {
        background-position: 0% 50%;
    }
    50% {
        background-position: 100% 50%;
    }
    100% {
        background-position: 0% 50%;
    }
}

div#container-wrap {
    background-image: url('https://scp-wiki.wdfiles.com/local--files/theme%3Athalassophobia/thalassophobiagradient.png');
}

:root {
   --theme-base: "black-highlighter";
   --theme-id: "thalassophobia";
   --theme-name: "Thalassophobia Theme";

   --logo-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Athalassophobia/thalassophobiaheader.svg");
   --header-title: "DEEP SEA SITE-72";
   --header-subtitle: "Abyssus Custodiens Sub Lumine";

   --body-font: 'Miriam Libre', sans-serif;
   --header-font: var(--body-font), sans-serif;
   --title-font: var(--body-font), sans-serif;
   --mono-font: 'Anonymous Pro', monospace;
   --UI-font: var(--body-font);

/* THEME ASSIGNMENTS */
   --sea-black: 1, 5, 7;
   --black: 0, 0, 0;
   --bronze: 186, 127, 0;
   --dark-bronze: 110, 75, 0;
   --light-bronze: 232, 186, 86;
   --sky-blue: 0, 170, 189;
   --deep-blue: 10, 81, 128;
   --dark-blue: 5, 41, 66;
   --neon-blue: 0, 229, 255;

        --dark-gray-monochrome: 12, 13, 14;
        --black-monochrome: 1, 5, 7;
        --light-pale-gray-monochrome: 232, 186, 86;
        --bright-accent: var(--pale-accent);

     --swatch-primary: var(--dark-bronze);
     --swatch-primary-darker: var(--bronze);
     --swatch-primary-darkest: var(--bronze);
        --swatch-background: var(--swatch-menubg-black-color);
     --swatch-border-color: var(--light-bronze);

        --swatch-text-light: var(--light-pale-gray-monochrome);
        --swatch-text-general: var(--swatch-text-light);

        --swatch-menubg-color: var(--swatch-menubg-black-color);
        --swatch-menubg-extra-dark-color: var(--black-monochrome);
        --swatch-menutxt-general-color: var(--swatch-menutxt-light-color);

        --swatch-secondary-color: var(--swatch-menubg-dark-color);
        --swatch-tertiary-color: var(--swatch-menubg-medium-dark-color);
        --swatch-alternate-color: var(--swatch-primary);

        --swatch-text-secondary-color: var(--sea-black);
        --swatch-text-tertiary-color: var(--light-bronze);

        --swatch-topmenu-bg-color: var(--sea-black);

    }
:root:lang(cn) {
   --header-title: "72号深海站";
   --header-subtitle: "守护深海于光明之下";
}

    #header {
    --search-icon-color: var(--light-bronze);
    --search-icon-hover-color: var(--light-bronze);

    --search-focus-textbox-text-color: var(--light-bronze);
    --search-textbox-text-color: var(--sky-blue)

    --login-line-divider-color: var(--light-bronze);
    --login-username-color: var(--sky-blue);
    --login-myaccount-color: var(--light-bronze);
    --login-dropdown-text-color: var(--light-bronze);
    --login-dropdown-text-hover-color: var(--light-bronze);
    }

    #top-bar {
        --dropdown-bg-color: var(--swatch-menubg-extra-dark-color), 0.9;
     --mobile-topmenu-sidebar-button-color: var(--sky-blue);

     --topmenu-category-color: var(--light-bronze);
     --topmenu-category-hover-color: var(--light-bronze);
     --dropdown-links-color: var(--light-bronze);
     --dropdown-links-hover-color: var(--light-bronze);
    }

    #side-bar, .side-block {
    --sideblock-heading-border-color: var(--deep-blue);
    --sideblock-heading-text-color: var(--sky-blue);

   --sidebar-bg-color: var(--sea-black);

    --sidebar-border-color: var(--dark-blue), 0.08;
    --sidebar-subtest-color: var(--dark-blue);

    --sidebar-links-text: var(--sky-blue);
    --sidebar-links-hover-bg-color: var(--sky-blue);
    --sidebar-links-hover-text-color: var(--sea-black);
 }

    #search-top-box-input {
        background-color: rgb(var(--sky-blue));
    }

    hr {
     background-color: rgb(var(--light-bronze));
}

:root {
        --link-color: var(--sky-blue);
        --hover-link-color: var(--sky-blue);
        --visited-link-color: var(--sky-blue);
        color: rgb(var(--swatch-text-general));
    }

    #main-content {
    --footnotes-footer-title-text-color: var(--sea-black);
    --hoverblock-header-txt: var(--sea-black);

    --tabs-hover-bg: var(--bronze);
    --tabs-selected-bg: var(--dark-gray-monochrome);
    --tabs-selected-txt: var(--light-bronze);
    --tabs-selected-outline: var(--dark-bronze);
    }

input[type=checkbox]:checked {
    outline: 0 solid rgb(var(--bronze));
    box-shadow: 0 0 0 .125rem rgb(var(--bronze));
}

input[type=checkbox]:checked:after, input[type=checkbox]:checked:before {
    background-color: rgba(var(--bronze),1);
}

.pager .current, .pager .current:before {
    background-color: rgb(var(--bronze));
}

input[type=radio] {
    background: rgb(var(--bronze));
    box-shadow: 0 0 0 .0625rem rgb(var(--bronze));
}

table.page-history tr:nth-of-type(2) td:not(:nth-of-type(7))::before {
    background-color: rgb(var(--bronze));
}
[[/code]]
[[/collapsible]]
[[/iftags]]


