[[module css]]

@import url('https://fonts.googleapis.com/css2?family=Abril+Fatface&display=swap');

:root {
  --color-1: #fff;
  --color-2: #5a5e8e;
  --color-3: #999;
}

h1 {
  color: var(--color-2);
}

/* 画像ブロック */

.scp-image-block {
  border: none;
  box-shadow: 0 5px 10px var(--color-3);
  border-radius: 10px;
  overflow: hidden;
}

.scp-image-block .scp-image-caption {
  color: var(--color-1);
  border-top: none;
  background: var(--color-2);
}

/* ヘッダー */

#top-bar .mobile-top-bar {
  width: 100%;
  max-width: 100vw;
}

div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: relative;
    background: transparent;
    background-size: cover, cover;
    background-position: 50% 50%, 50% 50%;
    z-index: 9;
}
div#container-wrap {
    background-image: none;
}
div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: var(--color-2);
    box-shadow: 0 10px 10px var(--color-3);
    z-index: 9;
}

#header {
    text-align: left;
    top: 0;
    width: 100%;
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    background: transparent;
    background-size: auto 140px;
}

#search-top-box {
    position: absolute;
    top: 30px;
    bottom: auto;
    right: 9px;
    width: 250px;
    text-align: right;
}
#search-top-box-input {
    display: none;
    border: solid 1px /*var(--2-color)*/ transparent;
    border-radius: 5px;
    color: var(--color-2);
    background-color: var(--color-2);
    box-shadow: /*3px 3px 5px var(--2-color)*/ none;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px /*var(--color-2)*/ transparent;
    color: var(--color-1);
    background-color: var(--color-2);
    box-shadow: /*3px 3px 5px var(--2-color)*/ none;
}
#search-top-box-form input[type=submit] {
    border: solid 1px /*var(--2-color)*/ transparent;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: var(--color-1);
    background-color: var(--color-2);
    background: var(--color-2);
    box-shadow: /*3px 3px 5px var(--2-color)*/ none;
    cursor: pointer;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px /*var(--color-2)*/ transparent;
    color: var(--color-2);
    text-shadow: none;
    background-color: var(--color-2);
    background: var(--color-2);
    box-shadow: /*3px 3px 5px var(--2-color)*/ none;
}

#login-status {
    color: var(--color-2);
    font-size: 90%;
    z-index: 30;
}
#login-status a {
    background: transparent;
    color: var(--color-2);
}
#login-status ul a {
    color: var(--color-1);
    background: transparent;
}
#account-topbutton {
    display: none;
    background: var(--color-2);
    color: var(--color-1);
}
.printuser img.small {
    margin-right: 1px;
}

#header h1 {
    margin-left: auto;
    margin-right: auto;
    padding: 0;
    float: center;
    max-height: 40px;
}
#header h2 {
    margin-left: 0;
    padding: 0;
    padding-left: 0;
    clear: center;
    float: center;
    font-size: 105%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    text-align: left;
    margin: 0;
    padding: 20px 0 0;
    line-height: 100%;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Odibee Sans', cursive;
    font-size: 100%;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h1 a:after {
    content: '';
    display: block;
    top: 0;
    left: 0;
    width: 100%;
    height: 140px;
    position: absolute;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
