#header h1 a::before {
    content: "THANATOMANIA";
    font-size: 30px;
    color: var(--color-1);
    text-align: center;
    letter-spacing: 0;
    font-family: 'Abril Fatface', cursive;
    text-shadow: /*.05em .05em 0 var(--color-1), -.05em .05em 0 var(--color-1), .05em -.05em 0 var(--color-1), -.05em -.05em 0 var(--color-1), .05em 0 0 var(--color-1), -.05em 0 0 var(--color-1), 0 .05em 0 var(--color-1), 0 -.05em 0 var(--color-1)*/ 0px 5px 10px var(--color-3);
    display: block;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    opacity: 1;
    z-index: 1;
}
/* トップメニュー */
#top-bar {
    width: 100%;
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0;
    z-index: 20;
    font-size: 90%;
}
#top-bar ul {
    float: left;
    display: flex;
    width: 100%;
}
#top-bar li {
    flex: 1;
    margin: 0;
}
#top-bar a {
    color: var(--color-1);
    background: transparent;
    border-radius: 5px;
    transition: all .2s ease-in-out;
}
#top-bar ul li {
    border: 0;
    position: relative;
}
#top-bar ul li ul {
    display: block;
    border: solid 1px var(--color-2);
    background: var(--color-2);
    box-shadow: 0 3px 3px var(--color-3);
    border-top: 0;
    border-radius: 5px;
    transition: all .2s ease-in-out;
}
#top-bar ul li a {
    border-left: none;
    border-right: none;
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
    border-radius: 5px;
    transition: all .2s ease-in-out;
}
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: var(--color-1);
    color: var(--color-2);
    border-left: none;
    border-right: none;
    border-radius: 5px;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: none;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--color-1);
    text-decoration: none;
}
#top-bar ul li ul {
    border-width: 0 1px 1px 1px;
    width: auto;
}
#top-bar ul li ul li, #top-bar ul li ul li.sfhover,
#top-bar ul li ul li, #top-bar ul li ul li:hover {
    border-width: 0;
}
#top-bar ul li ul li a {
    border-width: 0;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: var(--color-2);
}
.top-bar ul li:last-of-type ul {
    right: 0;
}

/* COMMON */
blockquote, div.blockquote {
    border-width: 0;
    padding: 0.1px 1em;
    background: var(--color-1);
    box-shadow: 0 5px 10px var(--color-3);
    border-radius: 5px;
}

#content-wrap {
    position: relative;
    margin: 2em auto 0;
    max-width: 1040px;
    min-height:1800px;
    height: auto !important;
    height: 1850px;
}

h1, #page-title {
    color: var(--color-2);
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}
h1 {
    margin-top: 0.7em;
    padding: 0;
    font-weight: bold;
}
h2, h3, h4, h5, h6, h7 {
    margin: 0.5em 0 0.4em;
    padding: 0;
    letter-spacing: 1px;
}
#page-title {
    border-color: var(--color-2);
}
ul {
    /* list-style-image: url(http://d3g0gp89917ko0.cloudfront.net/v--3b8418686296/common--theme/shiny/images/bullet.gif); */
    list-style: square;
}
#top-bar ul {
    /* list-style-image: none; /* IE ONLY! IF list-style-image IS SET */
}

a {
    color: var(--color-2);
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: var(--color-2);
    text-decoration: none;
    background: transparent;
}
a:hover {
    text-decoration: underline;
    background-color: transparent;
}

hr {
    background: var(--color-2);
}

/* BODY */

body {
    background-color: var(--color-1);
    font-size: 0.80em;
}

/* SIDE MENU */
#side-bar .side-block {
    border: 2px solid var(--color-2);
    border-radius: 5px;
    box-shadow: none;
    background: var(--color-1) !important;
}
#side-bar .side-block a {
    color: var(--color-2);
}
#side-bar .side-block .heading {
    color: var(--color-2);
    border-bottom: solid 1px var(--color-2);
    font-weight: bold;
}
#side-bar .side-block hr {
    background: var(--color-2);
}
#side-bar div.menu-item a {
    font-weight: bold;
    color: var(--color-2);
}
#side-bar div.menu-item.inactive a {
    color: #999;
}
#side-bar div.menu-item .sub-text {
    color: #666;
}
#side-bar div.menu-item.sub-item {
}
#side-bar .collapsible-block-folded {
    background: url(http://www.scp-wiki.net/local--files/nav:side/expand.png) 0 2px no-repeat;
}
#side-bar .collapsible-block-link {
    font-weight: bold;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px var(--color-2);
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: var(--color-2);
}

@media (max-width: 767px) {
#side-bar {
    background-color: var(--color-1);
}
#side-bar:target .close-menu {
   background: none;
}
}

/* Ratingモジュール */

.page-rate-widget-box {
    background-color: var(--color-2) !important;
   box-shadow: none;
}
div.page-rate-widget-box .rate-points {
    background-color: var(--color-2);
    border: solid 1px var(--color-2);
    color: var(--color-1);
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: var(--color-2);
    border-top: solid 1px var(--color-2);
    border-bottom: solid 1px var(--color-2);
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: var(--color-1);
    color: var(--color-2);
    transition: all .2s ease-in-out;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: var(--3-color);
    color: var(--1-color);
    text-decoration: none;
    transition: all .2s ease-in-out;
}
.page-rate-widget-box .cancel {
    background-color: var(--1-color);
    border: solid 1px var(--1-color);
}
.page-rate-widget-box .cancel a {
    background: transparent;
    color: var(--3-color);
}
.page-rate-widget-box .cancel a:hover {
    background: var(--color-2);
    color: var(--color-1);
    text-decoration: none;
}
.creditButton {
    background-color: var(--color-2) !important;
    box-shadow: none !important;
    border-color: var(--color-2) !important;
}
.creditButton a {
    color: var(--color-1) !important;
}

/* タブ */

@keyframes open {
  from {
    max-height: 200vh;
  }
  to {
    max-height: 0vh;
  }
}

.yui-navset {
  background-color: var(--border-color);
  border-radius: 10px;
  box-shadow: 0 5px 10px var(--color-3);
  overflow: hidden;
}
.yui-navset .yui-content {
  border: none;
  background-color: var(--color-1);
  word-break: break-word;
  transition: all ease-in-out calc(1s / 4);
}
.yui-navset .yui-content div[id^="wiki-tab-"] {
  animation: open calc(1s / 2) ease-out 0s 1 reverse both running;
  overflow-y: scroll;
}
.yui-navset .yui-content li:nth-last-child(1) {
  box-shadow: none;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
  margin: 0;
  margin-top: -1px;
  line-height: 100%;
  display: inline-block;
  width: calc(100% + .16em);
  max-width: none !important;
  height: 100%;
  border: none;
  background-color: var(--color-2);
  background-image: none;
  color: var(--color-1);
  transition: all ease-in-out calc(1s / 4) calc(1s / 4);
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
  margin: 0;
  margin-top: -1px;
  width: calc(100% + .16em);
  max-width: none !important;
  border: none;
  background: var(--color-1);
  color: var(--color-2);
  transition: all ease-in-out calc(1s / 4);
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
  margin: 0;
  margin-top: -1px;
  width: calc(100% + .16em);
  max-width: none !important;
  border: none;
  background: var(--color-2);
  text-decoration: none;
  color: var(--color-1);
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
  text-decoration: none;
  border: none;
  background-color: var(--color-1);
}
.yui-navset li {
  margin: 0;
  border: none;
  line-height: 141%;
  word-break: break-all;
  flex: 1;
}
.yui-navset ul {
  border: none;
  display: flex;
  justify-content: space-between;
  flex-direction: column;
}
.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
  border: none;
  margin: 0;
  white-space: pre-wrap;
}
.yui-navset li:nth-last-child(1) {
  box-shadow: 0 0 10px var(--color-3);
}
.yui-navset .yui-nav .selected {
  margin: 0 .16em -1px 0;
  border: none;
}

