+ CSS theme code

[[code type="css"]]


/*
    Powered on 2021/01/19 18:35:49
    [2021 Wikidot Theme]
    Created by c_bonefish
    Media Query edited by thd-glasses
    Logo Image: https://pixabay.com/photos/starry-sky-night-sky-stars-night-2675322/
    Header Image: https://pixabay.com/illustrations/banner-header-sky-night-stars-1557834/
    CC BY-SA 3.0
*/

/* Header */

div#container-wrap {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Athe-flowing-night-sky/background.png);
}

#header h1 a span {
    color: #fff;
    text-shadow: 3px 3px 5px #000;
}

#header h2 span {
    font-size:0px;
    padding: 4px;
}

#header h2:after {
    content: "Drifting through the Milky way Flowing across the Night Sky";
    font-weight: bold;
    color: #FFD9FF;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
    white-space: normal;
}

#header {
    background-image: url(https://scp-wiki.wikidot.com/local--files/theme:the-flowing-night-sky/logo.png);
}

/* Color */

#page-title {
    font-size: 2.1em;
    color: #073763; 
}

h1, h2, h3, h4, h5, h6 {
    font-weight: bold;
}

h1 {
    color: rgb(16, 60, 128);
}

h2 {
    color: rgb(41, 93, 174);
}

h3 {
    color: rgb(82, 128, 202);
}

h4 {
    color: rgb(143, 177, 231);
}

h5 {
    color: rgb(183, 204, 238);
}

h6 {
    color: rgb(216, 230, 253);
}

a:link {
    color: rgb(66, 90, 129);
}

a:visited {
    color: rgb(171, 171, 240);
}

/* Side bar */

#side-bar {
    background: #ffffff;
}

#side-bar .side-block {
    background: #ffffff;
    border: 5px solid #002662;
    border-radius: 10px 0;
}

#side-bar .heading {
    color: #000000;
    border-bottom: solid 1px #000000;
}

div.open-menu a {
    color: rgb(28, 69, 135) !important;
    border-color: rgb(28, 69, 135) !important;
}

#side-bar div.menu-item.inactive a {
    color: #000000;
}

#side-bar div.menu-item .sub-text {
    color: #000000;
}

#side-bar a:link {
    color: rgb(147, 132, 187);
}

#side-bar a:visited {
    color: rgb(197, 197, 255);
}

/* side-bar collapsible stuff */
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #000000;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #000000;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #000000;
}

/* Rate Module */

.page-rate-widget-box {
    margin-right: 0;
    border: 2px solid #073763;
    border-radius: 5px;
    box-shadow: none;
}

.page-rate-widget-box > span {
    border: none
}

.page-rate-widget-box .rate-points {
    background: #073763 !important;
    border: 2px solid #073763 !important;
    color: #fff;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #fff;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #073763;
    color: #FFD9FF;
}

.page-rate-widget-box .cancel {
    background: #073763;
    border: 2px solid #073763;
    color: #FFD9FF;
}

.page-rate-widget-box .cancel a {
    color: #FFD9FF;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #073763;
    border-radius: 0;
    color: #FFD9FF;
}

ul.creditRate {
    margin-right: 0;
}

div.rate-box-with-credit-button {
    background: #073763;
    border: 2px solid #073763;
    box-shadow: none;
}



/* Tab */

div.yui-navset div.yui-content {
    background: rgb(250, 250, 250);
}

div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #fefefe;
    color: #000000;
}

div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: rgb(232, 244, 255);
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: rgb(32, 18, 77);
    color: #ffffff;
}

/* blockquote */

blockquote, div.blockquote {
    margin: 1px;
    background: #ffffff;
}

[[/code]]
[[/iftags]]
