[[code type="css"]]

/*
    Powered on 2021/01/19 18:35:49
    [2021 Wikidot Theme]
    Created by c_bonefish
    Media Query edited by thd-glasses
    Logo Image: https://pixabay.com/photos/starry-sky-night-sky-stars-night-2675322/
    Header Image: https://pixabay.com/illustrations/banner-header-sky-night-stars-1557834/
    CC BY-SA 3.0
*/

/* Header */

div#container-wrap {
    background: url(https://scpko.wdfiles.com/local--files/theme:the-flowing-night-sky/background.png) center top repeat-x;
}

#header h1 a span {
    font-size: 0px;
}

#header h1 a:before {
    content: "SCP 재단";
    color: #fff;
    text-shadow: 3px 3px 5px #000;
}

#header h2 span {
    font-size:0px;
    padding: 4px;
}

#header h2:after {
    content: "저 밤하늘 은하수 따라 흘러가며";
    font-weight: bold;
    color: #FFD9FF;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
    white-space: pre;
}

#page-content {
  font-size: 0.9em;
}

#header {
    background: url(https://scpko.wikidot.com/local--files/theme:the-flowing-night-sky/logo.png) 10px 40px no-repeat;
    background-position: 1em 3em;
    background-size: 97px 97px;
}

/* Color */

#page-title {
    font-size: 2.1em;
    color: #073763; 
}

h1 {
    color: rgb(16, 60, 128);
    font-weight: bold;
}

h2 {
    color: rgb(41, 93, 174);
    font-weight: bold;
}

h3 {
    color: rgb(82, 128, 202);
    font-weight: bold;
}

h4 {
    color: rgb(143, 177, 231);
    font-weight: bold;
}

h5 {
    color: rgb(183, 204, 238);
    font-weight: bold;
}

h6 {
    color: rgb(216, 230, 253);
    font-weight: bold;
}

a:link {
    color: rgb(66, 90, 129);
}

a:visited {
    color: rgb(171, 171, 240);
}

/* Side bar */

#side-bar {
    background: #ffffff;
}

#side-bar .side-block {
    background: #ffffff;
    border: 5px solid #002662;
    border-radius: 10px 0;
}

#side-bar .heading {
    color: #000000;
    border-bottom: solid 1px #000000;
}

div.open-menu a {
    color: rgb(28, 69, 135) !important;
    border-color: rgb(28, 69, 135) !important;
}

#side-bar div.menu-item.inactive a {
    color: #000000;
}

#side-bar div.menu-item .sub-text {
    color: #000000;
}

#side-bar a:link {
    color: rgb(147, 132, 187);
}

#side-bar a:visited {
    color: rgb(197, 197, 255);
}

/* side-bar collapsible stuff */
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #000000;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #000000;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #000000;
}

/* Rate Module */

.page-rate-widget-box {
    margin-right: 0;
    border: 2px solid #073763;
    border-radius: 5px;
    box-shadow: none;
}

.page-rate-widget-box > span {
    border: none !important;
}

.page-rate-widget-box .rate-points {
    background: #073763 !important;
    border: 2px solid #073763 !important;
    color: #fff;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #fff;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #073763;
    color: #FFD9FF;
}

.page-rate-widget-box .cancel {
    background: #073763;
    border: 2px solid #073763 !important;
    color: #FFD9FF !important;
}

.page-rate-widget-box .cancel a {
    color: #FFD9FF;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #073763;
    border-radius: 0;
    color: #FFD9FF;
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: #073763 !important;
    border: 2px solid #073763 !important;
    box-shadow: none !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: #073763 !important;
    color: #FFD9FF !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #FFD9FF;
    border-radius: 0 3px 3px 0;
    color: #073763 !important;
}

/* Tab */

div.yui-navset div.yui-content {
    background: rgb(250, 250, 250);
}

div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #fefefe;
    color: #000000;
}

div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: rgb(232, 244, 255);
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: rgb(32, 18, 77);
    color: #ffffff;
}

/* blockquote */

blockquote {
    margin: 1;
    background: #ffffff;
}

blockquote p {
    padding: 1px;
    background: #ffffff;
    border-radius: 5px;
}

/* Mobile Media Query */
 @media (max-width: 479px) {
     #header {
         background-position: 0 5.5em;
         background-size: 57px 57px;
    }
     #header h1 a {
         font-size: 100%;
    }
     #header h1, #header h2 {
         margin-left: 66px;
    }
}
/* Small Mobile Media Query */
 @media (max-width: 385px) {
     #header {
         background-position: 5% 5.5em;
    }
     #header h1, #header h2 {
         margin-left: -webkit-calc(66px + 5%);
         margin-left: -moz-calc(66px + 5%);
         margin-left: calc(66px + 5%);
    }
}
/* Note Media Query */
 @media (min-width: 480px) and (max-width: 580px) {
     #header {
         background-position: 0.5em 4.5em;
         background-size: 66px 66px;
    }
     #header h1 a {
         font-size: 120%;
    }
     #header h1, #header h2 {
         margin-left: 80px;
    }
}
/* Mini Tablet Media Query */
 @media (min-width: 581px) and (max-width: 767px) {
     #header {
         background-position: 1em 4em;
         background-size: 77px 77px;
    }
     #header h1 a {
         font-size: 140%;
    }
     #header h1, #header h2 {
         margin-left: 93px;
    }
}
/* Tablet Media Query */
 @media (min-width: 768px) and (max-width: 979px) {
     #header {
         background-position: 1em 4em;
         background-size: 88px 88px;
    }
     #header h1 a {
         font-size: 160%;
    }
     #header h1, #header h2 {
         margin-left: 106px;
    }
}
[[/code]]
[[/iftags]]
