[[code type="css"]]
/*
    The Greatest Education CSS Theme
    [2021 Wikidot Theme]

    Logo by Jun0201

    Created by c_bonefish and Denevola
    CC BY-SA 3.0
*/

@import url('https://fonts.googleapis.com/css2?family=Vollkorn+SC&display=swap');

/* Header */
div#container-wrap {
    background: url('https://scpko.wikidot.com/local--files/theme:the-greatest-education/background.png') center top repeat-x;
    background-size: 800px;
}

#header {
    height: 200px;
    background-image: url('https://scpko.wikidot.com/local--files/theme:the-greatest-education/logo.png');
    background-position: 0 50%;
    background-repeat: no-repeat;
    background-size: 128px auto;
}

#header h1,
#header h2 {
    margin-left: 145px;
}

#header h1 a span,
#header h2 span {
    display: none;
}

#header h1 {
    max-height: 125px;
}

#header h1 a {
    padding-top: 105px;
    color: #2C77C1;
    font-family: 'Vollkorn SC', sans-serif;
    font-size: 70px;
    font-weight: normal;
    letter-spacing: -5px;
    text-shadow: 1px 0 #fff, -1px 0 #fff, 0 1px #fff, 0 -1px #fff, 1px 1px #fff, -1px 1px #fff, 1px -1px #fff, -1px -1px #fff;
}

#header h1 a::before {
    content: "Celeste";
}

#header h2 {
    font-size: 20px;
}

#header h2::before {
    display: block;
    max-height: 0;
    margin: 0;
    padding: 19px 0;
    color: #2C77C1;
    font-family: 'Nanum Gothic', sans-serif;
    font-weight: bold;
    line-height: 0;
    text-indent: 4px;
    text-shadow: 1px 0 #fff, -1px 0 #fff, 0 1px #fff, 0 -1px #fff, 1px 1px #fff, -1px 1px #fff, 1px -1px #fff, -1px -1px #fff;
    content: "여러분께 기적을 나눠드립니다";
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 40px;
    color: #2C77C1;
    z-index: 2;
    text-shadow: 1px 0 #fff, -1px 0 #fff, 0 1px #fff, 0 -1px #fff, 1px 1px #fff, -1px 1px #fff, 1px -1px #fff, -1px -1px #fff;
}

#login-status a {
    color: #2C77C1;
    font-weight: bolder;
    text-shadow: 1px 0 #fff, -1px 0 #fff, 0 1px #fff, 0 -1px #fff, 1px 1px #fff, -1px 1px #fff, 1px -1px #fff, -1px -1px #fff;
}

#account-topbutton {
    border-color: #2C77C1;
}

#login-status ul a {
    color: #2C77C1 !important;
}

#search-top-box {
    position: absolute;
    top: 160px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-form {
    display: inline-flex;
}

#search-top-box-form > input {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 5px;
}

#search-top-box-input {
    background-color: #4ABFD3;
    border: solid 1px #92FFFF;
    color: #92FFFF;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 1px 1px 5px rgb(0 0 0 / 50%);
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: #46AFF0;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px #92FFFF;
    color: #92FFFF;
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: #449ad0
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 199px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: #ffffff;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #46aff0;
    border-color: transparent;
    color: #ffffff;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #1d8fd6;
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: rgba(0, 0, 0, .4);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgba(0, 0, 0, .1);
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border-top: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}
.c_topbar_arrow {
    color: #92ffff;
}

/* Page Elements */
#page-title {
    color: #2B6ECF;
    font-size: 2.1em;
    font-weight: bold;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: #2B6ECF;
    font-weight: bold;
}

body {
    background-color: #ffffff;
    color: #000000;
}

a:link {
    color: #53538F;
}

a:visited {
    color: #9191DB;
}

.hovertip {
    background-color: #ffffff !important;
    border: 2px solid #24FCFF !important;
    color: #000000 !important;
}

.scp-image-block {
    border: 10px solid #f4f7ff;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
}

.scp-image-block .scp-image-caption {
    padding-top: 1em;
    background: #f4f7ff;
    border-top: none;
    font-size: 85%;
}

.scp-image-block img {
    box-shadow: 0 0 1px black;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: #ffffff;
}

::-webkit-scrollbar-thumb {
    background: #B2D4FF;
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: #D6F0FF;
}

textarea::-webkit-scrollbar-track {
    background: #ffffff;
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block {
    background-color: #ffffff !important;
    border: 2px solid #80DCFF !important;
    border-radius: 10px 0 !important;
}

#side-bar .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #000000;
    color: #000000;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #38ADC1;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #000000;
}

#side-bar .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: hue-rotate(200deg);
}

#side-bar .collapsible-block-folded a:link {
    filter: hue-rotate(-200deg);
}

#side-bar a:link {
    color: #269BAF;
}

#side-bar a:visited {
    color: #4ABFD3;
}

#side-bar a:link:hover,
#side-bar a:visited:hover {
    color: #4ABFD3;
}

.scpnet-interwiki-frame {
    filter: invert(1) hue-rotate(245deg) saturate(.8);
}

div.open-menu a {
    border-color: #000000 !important;
    color: #000000 !important;
}

/* Tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    border: 1px solid gainsboro;
    border-bottom: none;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 4px solid transparent;
    color: gray;
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid #2b6ecf;
    color: #2b6ecf;
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: #FDFDFF;
    border: 1px solid gainsboro;
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    border: 2px solid #334a66;
    border-radius: 5px;
    box-shadow: none;
}

.page-rate-widget-box > span {
    border: none !important;
}

.page-rate-widget-box .rate-points {
    background: #D4F4FA !important;
    color: #000000;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #ffffff;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #334a66;
    color: white;
}

.page-rate-widget-box .cancel {
    background: #d4f4fa;
}

.page-rate-widget-box .cancel a {
    color: rgba(0, 0, 0, .3);
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #334a66;
    border-radius: 0;
    color: #d4f4fa;
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: #d4f4fa !important;
    border: 2px solid #334a66 !important;
    box-shadow: none !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: none !important;
    color: #000000 !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #00B4DB;
    border-radius: 0 3px 3px 0;
    color: #d4f4fa !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: #ffffff;
    border-color: #5AFFFF;
}

.owindow .modal-header {
    background-color: #ffffff;
}

.owindow .modal-body img {
    background-color: #ffffff !important;
}

.owindow .title {
    background-color: #ffffff;
}

.owindow a,
.owindow a:visited {
    color: #6EE3F7;
}

.owindow .active {
    color: #000000;
}

.owindow .content.modal-body img {
    background-color: #269BAF !important;
}

.owindow .button-bar a {
    background-color: #ffffff;
    color: #000000;
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: #ffffff !important;
    border-color: #80DCFF;
    color: #000000;
}

table.page-files .highlight {
    background-color: #ECFFFF;
}

input.text,
input.text:focus,
input.text:hover {
    background-color: #ffffff !important;
    border-color: #80DCFF;
    color: #000000;
}

textarea {
    background-color: #FEFFFF;
    color: #000000;
}

.change-textarea-size a {
    background-color: #ffffff;
    color: #000000;
}

#lock-info {
    background-color: #ffffff;
}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: #FAFAFF;
    border-style: dotted;
}

/* etc. */
@media (min-width: 581px) and (max-width: 767px) {
    #header {
        background-size: 100px auto;
    }

    #header h1,
    #header h2 {
        margin-left: 110px;
    }
}

@media (min-width: 480px) and (max-width: 580px) {
    #header {
        background-size: 90px auto;
    }

    #header h1,
    #header h2 {
        margin-left: 100px;
    }

    #header h1 {
        max-height: 120px;
    }

    #header h1 a {
        padding-top: 110px;
        font-size: 55px;
    }

    #header h2 {
        font-size: 15px;
    }
}

@media (min-width: 321px) and (max-width: 479px) {
    #header {
        background-size: 80px auto;
        background-position-x: 5px;
    }

    #header h1,
    #header h2 {
        margin-left: 90px;
    }

    #header h1 {
        max-height: 120px;
    }

    #header h1 a {
        padding-top: 110px;
        font-size: 55px;
    }

    #header h2 {
        font-size: 15px;
    }
}

@media (max-width: 320px) {
    #header {
        background-size: 80px auto;
        background-position-x: 5px;
    }

    #header h1,
    #header h2 {
        margin-left: 90px;
    }

    #header h1 {
        max-height: 110px;
    }

    #header h1 a {
        padding-top: 105px;
        font-size: 40px;
        letter-spacing: -2px;
    }

    #header h2 {
        font-size: 12px;
    }
}
[[/code]]

[[/iftags]]
