/* The Track Changers Theme */
/* 2022 Wikidot Theme */
/* By Mercuresphere */
/* Based on Basalt Theme by Liryn & Placeholder McD and Old Money Theme by Woedenaz  */
/* CC BY-SA 3.0 */


/* ----BEGIN---- */

:root {
    /* S-CSS-P Integration */
    /* If you're making a new CSS theme, please include the following three variables at minimum. */
    --theme-base: "sigma9";
    /* must be either "black-highlighter" or "sigma9" */
    --theme-id: "the-track-changers";
    /* set this to the URL of your theme's page - eg for "component:ar-theme", set it to "ar-theme" */
    --theme-name: "The Track Changers Theme";
    /* set this to your theme's full name */

    /* Header */
    --title: " 变轨者";
    --subtitle: " 点燃烛火 划破夜幕";
    --logo: url("https://fastly.jsdelivr.net/gh/Crimone/crimson-anemone@2.4.2/files/huanian-logo.svg");
    /* Standard Colors */
    --white-monochrome: 252, 252, 252;
    /* white */
    --pale-gray-monochrome: 244, 244, 244;
    /* v light gray for blockquotes and stuff */
    --light-pale-gray-monochrome: 244, 244, 244;
    /* very light pale gray for misc. use */
    --very-light-gray-monochrome: 215, 215, 215;
    --light-gray-monochrome: 215, 215, 215;
    /* light accent gray for login status */
    --gray-monochrome: 185, 150, 80;
    /* gray */
    --dark-gray-monochrome: 115, 94, 55;
    /* dark accent gray for sidebar background */
    --black-monochrome: 26, 25, 24;
    /* black */
    --pale-accent: 252, 236, 135;
    --bright-accent: 252, 236, 135;
    /* bright red */
    --medium-accent: 207, 159, 45;
    /* medium red */
    --dark-accent: 70, 56, 33;
    /* dark red */
    --newpage-color: 221, 102, 17;
    /* pale orange */
    --swatch-primary: var(--bright-accent);
    --swatch-primary-darker: var(--medium-accent);
    --swatch-primary-darkest: var(--dark-accent);
    /* Primary Text Colors */
    /* Primary Menu Colors */
    --swatch-menubg-color: var(--black-monochrome);
    /* Primary Theme Colors */
    --swatch-background: var(--black-monochrome, 26, 25, 24);
    /* ===SECONDARY & TERTIARY COLORS=== */
    /* Rating Module Colors */
    --rating-module-button-color: var(--bright-accent);
    --rating-module-button-plus-color: 45, 65, 25;
    --rating-module-button-negative-color: 70, 35, 30;
    --rating-module-button-cancel-color: 50, 45, 50;
    --rating-module-button-credit-color: 50, 45, 50;
    --rating-module-text-color: var(--bright-accent);
    /* Background and Header Colors */
    --background-gradient-color: var(--dark-accent, 70, 56, 33);
    --header-gradient-color-bottom: var(--dark-accent);
    --header-gradient-color-middle: var(--black-monochrome);
    --header-gradient-color-top: var(--black-monochrome);
    --gradient-header: linear-gradient(30deg, rgb(var(--header-gradient-color-bottom)) 0%, rgb(var(--header-gradient-color-middle)) 90%, rgb(var(--header-gradient-color-top)) 100%);
    --scrollbar-width: 0.5rem;
    /* header measurements */
    --header-height-on-desktop: 5rem;
    --header-height-on-mobile: 5rem;
    --topbar-height-on-desktop: 5rem;
    --topbar-height-on-mobile: 5rem;
    --header-background-image-size: 100% var(--header-height-on-desktop);
    scrollbar-color: rgba(var(--dark-gray-monochrome), 1) rgba(var(--black-monochrome), 1);
    --gradient-background: linear-gradient(to bottom, rgba(var(--background-gradient-color), 1) 0, rgba(var(--background-gradient-color), 0) 40rem);
