/* ===header=== */

div#extra-div-1 {
    background: none;
}

div#extrac-div-1, div#extrac-div-2 {
    z-index: 1;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: var(--header-height-on-desktop);
    padding: none;
    pointer-events: none;
}

#extrac-div-1 {
    background-image: var(--gradient-header);
    background-size: var(--header-background-image-size) 100%;
}

#extrac-div-2 {
    background-image: linear-gradient(45deg, rgba(var(--swatch-primary), 0.4) 0%, rgba(var(--swatch-primary-darker), 0.1) 30%, rgba(var(--header-gradient-color-top)) 100%);
    background-size: var(--header-background-image-size) 100%;
    -webkit-mask-image: var(--old-money-mask-image);
    mask-image: var(--old-money-mask-image);
    -webkit-mask-size: 4rem;
    mask-size: 4rem
}

div#extra-div-3 {
    position: fixed;
    z-index: -1;
    top: 5rem;
    left: 0;
    width: 100%;
    height: 40rem;
    pointer-events: none;
    -webkit-mask-image: var(--old-money-mask-image);
    mask-image: var(--old-money-mask-image);
    -webkit-mask-size: 3rem;
    mask-size: 3rem;
}

div#extra-div-3::before {
    content: "";
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 40rem;
    background-attachment: fixed;
    background-image: linear-gradient(100deg, rgba(var(--swatch-primary), 0.2) 0%, rgba(var(--swatch-primary-darkest), 1) 30%, rgba(var(--swatch-primary-darkest), 1) 100%);
    background-size: 100vw 40rem;
    -webkit-mask-image: var(--gradient-background);
    mask-image: var(--gradient-background);
}

/* ===body background=== */

body::before {
    content: "";
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 40rem;
    background-image: var(--gradient-background);;
    background-size: 100vw 40rem;
    -webkit-mask-image: var(--gradient-background);
    mask-image: var(--gradient-background);
}

/* ===selection=== */

::-moz-selection {
    color: inherit;
    background: rgba(var(--swatch-primary,180,35,35),.25);
    text-shadow: none
}

::selection {
    color: inherit;
    background: rgba(var(--swatch-primary,180,35,35),.25);
    text-shadow: none
}

/* ===title=== */

#page-title, .meta-title, h1, h2, h3, h4, h5, h6 {
    -webkit-background-clip: text;
    background-clip: text;
    background-color: rgb(var(--swatch-primary-darker));
    background-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Aold-money-theme/goldfoil.png");
    background-size: cover;
    background-blend-mode: hard-light;
    -webkit-text-fill-color: transparent;
    font-family: 'Work Sans', '华文新魏', sans-serif;
}

/* ===hr=== */

hr {
    display: flex;
    position: relative;
    height: 0.35rem;
    border-top: none;
    background: rgba(0, 0, 0, 0);
}

hr::before {
    content: " ";
    position: absolute;
    top: 0.1125rem;
    left: 0;
    width: 100%;
    height: 0.125rem;
    border-top: none;
    background: linear-gradient(10deg, rgba(var(--bright-accent), 0.15) 0%, rgba(var(--bright-accent), 1) 50%, rgba(var(--bright-accent), 0.15) 100%);
    background-position: center center;
    -webkit-filter: blur(0.04rem);
    filter: blur(0.04rem);
}

hr::after {
    content: " ";
    position: absolute;
    top: 0;
    left: 50%;
    width: 10%;
    height: 0.35rem;
    transform: translateX(-50%);
    background-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Aold-money-theme/OldMoney_Divider.png");
    background-repeat: no-repeat;
    background-position: center center;
    background-size: cover;
}

/* ===yui-tabs=== */

.yui-navset .yui-nav a:hover, .yui-navset .yui-nav a:focus {
    background-color: rgb(var(--medium-accent));
}

.yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:active {
    color: black;
    background-color: rgb(var(--bright-accent));
}

.yui-navset .yui-nav .selected, .yui-navset .yui-navset-top .yui-nav .selected {
    color: black;
    background-color: rgb(var(--bright-accent));
}

.yui-navset .yui-nav .selected a {
    color: black;
}

.scp-image-block img, .scp-image-block .scp-image-caption {
    outline: solid 1px rgb(var(--bright-accent));
}

/* ===side bar=== */

#side-bar .side-block {
    background: linear-gradient(180deg, rgba(10, 10, 10, 1) 0%, rgb(var(--dark-gray-monochrome)) 100%);
    border: solid 2px rgb(var(--dark-gray-monochrome));
}

#side-bar .side-block.resources {
    background: linear-gradient(180deg, rgba(10, 10, 10, 1) 0%, rgb(var(--dark-gray-monochrome)) 100%) !important;
    border: solid 2px rgb(var(--dark-gray-monochrome));
}

#side-bar .side-block.media {
    background-color: rgb(5 5 5) !important;
    border-bottom: solid 2px rgb(10, 10, 10);
    border-top: solid 2px rgb(10 10 10);
}

/* ===links=== */

a {
    color: rgb(var(--bright-accent));
}

a, a:visited {
    -webkit-text-decoration: underline .09375em;
    -webkit-text-decoration-color: transparent;
    text-decoration: underline .09375em;
    text-decoration-color: transparent;
    transition: text-decoration-color .15s cubic-bezier(.4, 0, .2, 1), color .15s cubic-bezier(.4, 0, .2, 1), -webkit-text-decoration-color .15s cubic-bezier(.4, 0, .2, 1);
}

a:visited {
    color: rgb(var(--medium-accent));
}

a, a.newpage {
    background: transparent;
}

a.newpage {
    color: rgb(var(--newpage-color));
}

a:active, a:hover {
    -webkit-text-decoration: underline .09375em;
    -webkit-text-decoration-color: currentColor;
    background-color: initial;
    color: rgb(var(--dark-gray-monochrome));
    text-decoration: underline .09375em;
    text-decoration-color: initial;
    transition: text-decoration-color .15s cubic-bezier(.4, 0, .2, 1), color .15s cubic-bezier(.4, 0, .2, 1), -webkit-text-decoration-color .15s cubic-bezier(.4, 0, .2, 1);
}

a:focus-within {
    -webkit-text-decoration: underline .09375em;
    -webkit-text-decoration-color: currentColor;
    background-color: initial;
    color: rgb(var(--dark-gray-monochrome));
    text-decoration: underline .09375em;
    text-decoration-color: initial;
    transition: text-decoration-color .15s cubic-bezier(.4, 0, .2, 1), color .15s cubic-bezier(.4, 0, .2, 1), -webkit-text-decoration-color .15s cubic-bezier(.4, 0, .2, 1);
}

/* ===credit info module===*/

.modalbox {
    background: linear-gradient(rgb(var(--dark-accent)) 51px, rgb(var(--dark-accent)) 51px, rgb(var(--dark-gray-monochrome))) !important;
}

/* ===position adjustment===*/

#search-top-box-form > input[type=submit], #search-top-box-form > input[type=submit]:hover, #search-top-box-form > input[type=submit]:focus, #search-top-box-form > input[type=submit]:target {
    margin-top: 0;
}

#extra-div-1::before {
    top: 0.7rem;
}

#extra-div-2::before {
    top: 2.8rem;
}

@media (max-width: 1020px) {
    #extra-div-1::before {
        top: 0.3rem;
        font-size: calc(0.85 * 27.45px);
    }
}

@media (max-width: 1020px) {
    #extra-div-2::before {
        top: 2rem;
        font-size: calc(0.85 * 1.22em);
    }
}

/* ===rate box===*/

#page-content .rate-box-with-credit-button {
    border: none;
}

#page-content .page-rate-widget-box, #page-content .rate-box-with-credit-button {
    background: linear-gradient(to top, rgba(var(--swatch-primary), 0.25) 0, rgba(var(--swatch-primary-darker), 0.15) 100%) !important;
}

#page-content .rate-box-with-credit-button > .page-rate-widget-box {
    background: none !important;
}

#page-content .page-rate-widget-box .rate-points {
    background-color: transparent !important;
    color: #F5F5F5 !important;
    border: none;
}

.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background: transparent !important;
}

.page-rate-widget-box .cancel {
    background-color: transparent !important;
}

#page-content .page-rate-widget-box, #page-content .rate-box-with-credit-button {
    box-shadow: 0px 0px 8px 4px rgba(var(--bright-accent), 0.2);
}

#page-content .rate-box-with-credit-button > .page-rate-widget-box {
    box-shadow: none;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: transparent;
}

#page-content .rate-box-with-credit-button .creditButton {
    padding-left: 3px;
}

/* ===breadcrumbs=== */

#breadcrumbs, .pseudocrumbs {
    display: flex;
    position: relative;
    grid-area: breadcrumbs;
    flex-wrap: wrap;
    align-items: center;
    justify-content: flex-start;
    width: -webkit-fit-content;
    width: -moz-fit-content;
    width: fit-content;
    margin: -1em 0 1em;
    padding: 0 .75em 0 0;
    font-size: .85em;
    line-height: 2;
    font-family: var(--UI-font);
}

#breadcrumbs a, #breadcrumbs a:visited, .pseudocrumbs a, .pseudocrumbs a:visited {
    display: flex;
    z-index: 0;
    position: relative;
    margin-right: .75em;
    padding: 0 .5em;
    transition: color .15s cubic-bezier(.4, 0, .2, 1);
}

#breadcrumbs a:active, #breadcrumbs a:hover, .pseudocrumbs a:active, .pseudocrumbs a:hover, #breadcrumbs a:focus-within, .pseudocrumbs a:focus-within {
    color: rgb(var(--black-monochrome));
    text-decoration: none;
}

#breadcrumbs a:before, #breadcrumbs a:visited:before, .pseudocrumbs a:before, .pseudocrumbs a:visited:before {
    z-index: -1;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    transform: skew(-25deg);
    background: transparent;
    box-shadow: .0625rem .0625rem 0 0 rgba(var(--medium-accent), 1), .25rem .0625rem 0 0 rgba(0, 0, 0, 0.5), .25rem .0625rem 0 0 rgba(var(--medium-accent), 1), .3125rem .0625rem 0 0 rgba(var(--medium-accent), 1);
    content: "";
    transition: box-shadow .15s cubic-bezier(.4, 0, .2, 1), background-color .15s cubic-bezier(.4, 0, .2, 1);
}

#breadcrumbs a:active:before, #breadcrumbs a:hover:before, .pseudocrumbs a:active:before, .pseudocrumbs a:hover:before, #breadcrumbs a:focus-within:before, .pseudocrumbs a:focus-within:before {
    background-color: rgb(var(--swatch-primary));
    box-shadow: 0 0 0 .25rem rgba(var(--swatch-primary), 1), 0 0 0 0 rgba(var(--dark-accent), 0), 0 0 0 0 rgba(var(--black-monochrome), 0), 0 0 0 0 rgba(var(--swatch-primary), 0);
}

/* ===wiki-notes=== */

div.wiki-note {
    --CRSV: 1;
    --MONO: 0;
    --CASL: 1;
    --note-color: 255,255,136;
    position: relative;
    width: min(calc(90% / 2),90vw);
    margin: 2em auto;
    padding: 1.25em 1.25em .5em;
    border: none;
    border-radius: .5em .25em/0 0 30% 75%;
    background-image: linear-gradient(180deg,rgba(0,0,0,.1),rgba(0,0,0,.1)),linear-gradient(180deg,hsla(0,0%,100%,0),hsla(0,0%,100%,.2));
    background-size: 100% 1.5em,contain;
    background-repeat: no-repeat,repeat;
    background-color: rgb(var(--note-color));
    background-blend-mode: overlay,normal;
    font-family: 'Fira Code','仿宋', monospace;
    color: black;
}

div.wiki-note:after,div.wiki-note:before {
    z-index: -1;
    position: absolute;
    bottom: 1.5em;
    width: 70%;
    height: 55%;
    max-height: 40%;
    transform: scale(1.1);
    box-shadow: inset 0 -.5em 1.05em rgba(0,0,0,.3),0 .5em 1.05em rgba(0,0,0,.3);
    content: ""
}

div.wiki-note:before {
    left: 1.35em;
    transform: skew(-15deg) rotate(-6deg)
}

div.wiki-note:after {
    right: 1.125em;
    transform: skew(15deg) rotate(6deg)
}

@media (max-width: 425px) {
    div.wiki-note {
        width: min(65vw);
    }
}

/* ===non savingpages module=== */
body[class="wait"] #odialog-shader-iframe, body[class="wait"] #odialog-shader {
    display: none !important;
}

#odialog-container {
    position: fixed !important;
}

body[class="wait"] #odialog-container {
    z-index: -1;
}

body[class="wait"] #odialog-container #owindow-1 {
    display: none !important;
}

body[class="wait"] a[name="page-top"][target] + div #main-content #action-area div#lock-info {
    background-color: rgb(var(--dark-accent));
    border: 1px solid rgb(var(--bright-accent));
}

div#lock-info {
    font-size: 0 !important;
    margin: .8rem 0 !important;
    padding: 0.4rem .8rem !important;
}

div#lock-info::before {
    content: '您握有一个独占的15 分钟锁定，这将会阻止其他人编辑您正在作业的页面。\A这个锁定将在闲置 ';
    display: inline;
    font-size: .82rem;
    white-space: pre-wrap;
}

div#lock-info > strong {
    font-size: .82rem;
}

div#lock-info > br {
    display: none;
}

div#lock-info::after {
    content: ' 秒后失效。';
    display: inline;
    font-size: .82rem;
}

body[class="wait"] a[name="page-top"][target] + div #main-content #action-area div#lock-info::before {
    content: '保存页面中……\A若此提示已经显示了很长时间，建议将编辑框中的内容复制到剪切板然后刷新本页重新编辑。';
    display: inline;
    font-size: .82rem;
    color: rgb(var(--bright-accent));
    white-space: pre-wrap;
}

body[class="wait"] a[name="page-top"][target] + div #main-content #action-area div#lock-info::after {
