[[module css]]
#page-title {
    display: none;
}
.ps-t {
    border-bottom: 1px solid #bbb;
}
.ps-t h1 {
    direction: rtl;
    unicode-bidi: bidi-override;
    transform: translateX(calc(6em - 100%));
    width: 100%;
    padding: 0 0 0.25em;
    margin: 0;
    font-weight: normal;
}
.colors_container {
    width: 100%;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
       -moz-box-orient: vertical;
       -moz-box-direction: normal;
        -ms-flex-direction: column;
            flex-direction: column;
    margin: 0 auto;
    font-weight: 700;
}

.colors_container > .colors,
.colors_container > .subcolors {
    width: 100%;
    -webkit-flex-shrink: 0;
        -ms-flex-negative: 0;
            flex-shrink: 0;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-flex-wrap: wrap;
        -ms-flex-wrap: wrap;
            flex-wrap: wrap;
}

.colors > .color,
.subcolors > .color {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: horizontal;
    -webkit-box-direction: normal;
    -webkit-flex-direction: row;
       -moz-box-orient: horizontal;
       -moz-box-direction: normal;
        -ms-flex-direction: row;
            flex-direction: row;
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
       -moz-box-flex: 2;
        -ms-flex-positive: 2;
            flex-grow: 2;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
        -ms-flex-pack: center;
            justify-content: center;
    -webkit-box-align: end;
    -webkit-align-items: flex-end;
       -moz-box-align: end;
        -ms-flex-align: end;
            align-items: flex-end;
    padding: 0.5rem;
    margin: 0.5rem;
}

.colors > .color > .sub,
.subcolors > .color > .sub,
.colors > .color > .sub > .css-variable,
.subcolors > .color > .sub > .css-variable {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
       -moz-box-orient: vertical;
       -moz-box-direction: normal;
        -ms-flex-direction: column;
            flex-direction: column;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
        -ms-flex-pack: center;
            justify-content: center;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    width: 100%;
}

.colors > .color {
    height: 7rem;
    -webkit-flex-basis: -webkit-calc((100%/2) - 2rem);
        -ms-flex-preferred-size: calc((100%/2) - 2rem);
            flex-basis: -moz-calc((100%/2) - 2rem);
            flex-basis: calc((100%/2) - 2rem);
}

.colors > .color.one {
    background-color: rgba(var(--blue-accent), 1);
}

.colors > .color.two {
    background-color: rgba(var(--beige-accent), 1);
}
.subcolors > .color {
    height: 4rem;
    font-size: 75%;
    text-align: center;
    -webkit-flex-basis: -webkit-calc((100%/6) - 2rem);
        -ms-flex-preferred-size: calc((100%/6) - 2rem);
            flex-basis: -moz-calc((100%/6) - 2rem);
