}

.colors > .color.one {
    color: #f5f5f5;
}
.subcolors > .color.one,
.subcolors > .color.two {
    color: #333;
}

.subcolors > .color.one {
    background-color: rgba(var(--light-blue-accent), 1);
}

.subcolors > .color.two {
    background-color: rgba(var(--vivid-blue-accent), 1);
}

.subcolors > .color.three{
    background-color: rgba(var(--content-bg-color), 1);
}

