[!--
[[code type="CSS"]]
/*
  CSS Theme for Toheisha 
  --author: RTa_technology
  --source: http://rtas.wikidot.com/n-theme:toheisha
  --license: CC BY-SA 3.0 ( http://creativecommons.org/licenses/by-sa/3.0 )
*/
@import url('https://fonts.googleapis.com/css?family=Noto+Sans+JP|Noto+Serif+JP&display=swap');
:root {
  --blue-accent: 36, 49, 81;
  --beige-accent: 235, 224, 206;
  --light-blue-accent: 171, 191, 239;
  --vivid-blue-accent: 64, 102, 148;
  --content-bg-color: 228, 227, 225;
}
::selection {
  background: #243151;
  background: rgb(var(--blue-accent));
  color: #ebe0ce;
  color: rgb(var(--beige-accent));
}
#page-title, h1, h2, h3, h4, h5, h6, #top-bar, #page-content {
  font-family: 'Noto Serif JP';
}
body {
  background-color: #e4e3e1;
  background-color: rgb(var(--content-bg-color));
}
div#container-wrap {
  background: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A4053112-11-ljto/header.png) no-repeat;
  background-position-x: center;
  background-position-y: 36px;
  background-size: auto 180px;
}
div#main-content {
  position: static;
}
#extra-div-1 {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 30px;
  border: #ebe0ce solid 3px;
  border: rgb(var(--beige-accent)) solid 3px;
  border-right: 0;
  border-left: 0;
  background: #243151;
  background: rgb(var(--blue-accent));
}
#extra-div-2 {
  position: absolute;
  top: 217px;
  left: 0;
  width: 100%;
  height: 30px;
  border: #ebe0ce solid 3px;
  border: rgb(var(--beige-accent)) solid 3px;
  border-right: 0;
  border-left: 0;
  background: #243151;
  background: rgb(var(--blue-accent));
  box-shadow: 0 0 20px 15px rgb(0 0 0 / 0.25);
}
#header {
  z-index: 20;
  height: 220px;
  background-image: url(data:image/svg+xml;base64,);
  background-position-x: center;
  background-position-y: 75px;
  background-size: auto 100px;
}
#top-bar {
  top: 220px;
}
@media (max-width: 767px) {
  .mobile-top-bar {
    top: 0px;
  }
}
#top-bar ul li a {
  padding-top: 15px;
  padding-bottom: 15px;
}
#top-bar .top-bar>ul>li.sfhover>a, #top-bar .top-bar>ul>li:hover>a, #top-bar .mobile-top-bar>ul>li.sfhover>a, #top-bar .mobile-top-bar>ul>li:hover>a {
  background: #ebe0ce;
  background: rgb(var(--beige-accent));
  color: #243151;
  color: rgb(var(--blue-accent));
  text-decoration: none;
}
#top-bar ul li ul {
  border: unset;
  box-shadow: 0px 6px 10px 0px rgba(0, 0, 0, .5);
}
#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
  padding-top: 5px;
  padding-bottom: 5px;
  border: none;
  background: #eadfcd;
  color: #243151;
  color: rgb(var(--blue-accent));
}
#top-bar ul li.sfhover a, #top-bar ul li:hover a {
  border-color: transparent;
}
#top-bar ul li.sfhover ul li.sfhover a:hover {
  background: #243151;
  background: rgb(var(--blue-accent));
  color: #ebe0ce;
  color: rgb(var(--beige-accent));
  text-decoration: none;
}
#header h1 {
  margin: 30px 0 0;
  width: 100%;
  height: 60px;
  text-align: center;
  opacity: 0;
}
#header h1 a {
  padding: 80px 0 80px;
}
#header h2 {
  display: none;
}
#side-bar {
  display: none;
}
.c-side {
  position: absolute;
  top: 1.5em;
  left: 1em;
  float: left;
  clear: left;
  margin: 0 0 0 1em;
  padding: 0;
  width: 13rem;
}
@media (max-width: 767px) {
  .c-side {
    left: -20rem;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
  }
  #u-side:target~.c-side {
    position: fixed;
    top: 0;
