@import url('https://fonts.googleapis.com/css?family=Noto+Serif+SC&display=swap');
:root {
  --blue-accent: 36, 49, 81;
  --beige-accent: 235, 224, 206;
  --light-blue-accent: 171, 191, 239;
  --vivid-blue-accent: 64, 102, 148;
