/* SEARCHBOX */

#search-top-box-input {
  border: solid 1px #ebe0ce;
  border: solid 1px rgb(var(--beige-accent));
  border-radius: 5px;
  background-color: #932E40;
  box-shadow: unset;
  color: #ebe0ce;
  color: rgb(var(--beige-accent));
}
#search-top-box-form input[type=submit] {
  display: none;
}
div#search-top-box {
  top: 7px;
  right: 100px;
  width: 100%;
}
div#search-top-box form {
  position: relative;
  display: inline-flex;
  justify-content: flex-end;
}
div#search-top-box form input[type="text"], div#search-top-box form input[type="text"]:hover {
  display: inline-block;
  width: 8rem;
  height: 1.1rem;
  background-color: #243151;
  background-color: rgb(var(--blue-accent));
  transition: width .25s;
}
#search-top-box-input:hover, #search-top-box-input:focus {
  border: solid 1px #000;
  background-color: #243151;
  background-color: rgb(var(--blue-accent));
  color: #ebe0ce;
  color: rgb(var(--beige-accent));
}
#search-top-box-input:-webkit-autofill, #search-top-box-input:-webkit-autofill:hover, #search-top-box-input:-webkit-autofill:focus {
  -webkit-box-shadow: 0 0 0px 1000px #243151 inset !important;
  -webkit-box-shadow: 0 0 0px 1000px rgb(var(--blue-accent)) inset !important;
  color: #ebe0ce !important;
  color: rgb(var(--beige-accent)) !important;
  -webkit-transition: background-color 9999s, -webkit-box-shadow 9999s, color 9999s, -webkit-text-fill-color 9999s !important;
  transition: background-color 9999s, -webkit-box-shadow 9999s, color 9999s, -webkit-text-fill-color 9999s !important;
  -webkit-text-fill-color: #ebe0ce !important;
  -webkit-text-fill-color: rgb(var(--beige-accent)) !important;
}
div#search-top-box form input[type="text"]:not(:focus), div#search-top-box form input[type="text"]:not(:focus):not(:hover) {
  position: relative;
  top: 0;
  left: 1px;
  display: inline-block;
  box-sizing: border-box;
  width: 22px;
  height: 22px;
  outline: solid #000;
  outline-width: 1px;
  outline-offset: 2px;
  border-radius: 24px;
  background-color: transparent;
  background-image: url("data:image/svg+xml;charset=utf8,%3C%3Fxml%20version%3D%221.0%22%20encoding%3D%22UTF-8%22%20standalone%3D%22no%22%3F%3E%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%2213%22%20height%3D%2213%22%20viewBox%3D%220%200%203.44%203.44%22%3E%3Cg%20fill%3D%22%23932e40%22%3E%3Cpath%20d%3D%22M.53%202.249V.132l1.057%201.059v2.116zM2.91%202.249V.132L1.852%201.191v2.116zM1.587%200h.265v1.19h-.265M1.587%203.307h.265v.133h-.265%22%2F%3E%3C%2Fg%3E%3C%2Fsvg%3E");
