[[code type="css"]]
@import url(https://scp-jp.wdfiles.com/local--code/theme%3Atoyoho-web/2);
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+JP:wght@500&display=swap');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnBatang.css');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnJamoNovel.css');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnDotumBold.css');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnPilgiBold.css');
@font-face {
    font-family: 'Diphylleia';
    src: url('https://fastly.jsdelivr.net/gh/projectnoonnu/noonfonts_JAMO@1.0/Diphylleia-Regular.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
}

/*

Code Lisence
    Powered on 2020/02/19 15:01:35
    [2020 Wikidot Theme]
    Created by snoj
    Edited by Nanigashi Sato & ukwhatn & RTa_technology
    CC BY-SA 3.0

*/

/* Header */

div#header h1 a:before {
    content: "토오노 요괴보호구";
    font-family: MS ゴシック, UnDotumBold, Microsoft JhengHei, PMingLiU;
}

div#header h2:after {
    content: "遠野妖怪保護区 공식 웹사이트";
    font-family: MS ゴシック, UnDotumBold, PMingLiU, Microsoft JhengHei;
}

/* Body */

#page-content .shushuInner h1, h2, h3, h4, h5, h6 {
    font-family: "ヒラギノ明朝 ProN W6", "UnBatang",  "HiraMinProN-W6", "HG明朝E", "ＭＳ Ｐ明朝", "MS PMincho", "MS 明朝", serif;
}

/* Toyoho */

.toyoho-caption {
    font-family: '方正姚体', 'UnJamoNovel';
}

.toyoho-block .header {
    font-family: "ヒラギノ明朝 ProN W6", "UnBatang",  "HiraMinProN-W6", "HG明朝E", "ＭＳ Ｐ明朝", "MS PMincho", "MS 明朝", serif;
    word-break: keep-all;
}

.rt {
  user-select: none;
}

.beauty {
  font-family: 'Utsukushi', 'Diphylleia' !important;
}

