/*
    Turbo Vision Dark Theme
    [2022 Wikidot Theme Variation]
    By JackalRelated
       Based on Turbo Vision Theme by JakdragonX and Croquembouche
*/

@import url('https://fonts.googleapis.com/css2?family=Jost&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Michroma&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC&display=swap');
@import url('https://scp-wiki.wdfiles.com/local--files/theme:turbo-vision-dark/stylesheet.css');

/* LINK COLORS ADJUSTMENT */
a {
  color: #0B9D85;
}

a.footnoteref {
  color: #073C34;
}

a:hover{
  background-color: #18BFA3;
}

a:visited {
    color: #00B39B;
}

a:visited:hover {
    color: #000000;
}

/* BODY SHENANIGANRY(!) */
body {
  font-family: "Jost", "IBM Plex Mono", "Noto Sans SC", sans-serif;
  background-color: #01020D;
  image-rendering: initial;
}

.scp-image-block .scp-image-caption {
   background-color: #ADADAD;
   color: #000000;
}

hr {
  margin: 1em 0em 1em 0em;
}

#page-title {
  border-color: #18BFA3;
}

#main-content {
  color: #EDEDED;
}

h1,
h2,
h3,
h4,
h5,
h6,
#page-title {
  font-family: "Michroma", "Mx437 IBM VGA 8x16", "Noto Sans SC", monospace;
  color: #18BFA3;
}

h1,
#page-title {
  font-weight: bold;
}

/* HEADER (I HARDLY KNOW HER) */
div#container-wrap {
  background: url("https://scp-wiki.wdfiles.com/local--files/theme:turbo-vision-dark/tb-bga.png")
    top left repeat-x;
}

div#header {
  background: url("https://scp-wiki.wdfiles.com/local--files/theme:turbo-vision-dark/logo-new.png")
      3.675em 1.25em no-repeat;
  background-size: auto 100px;
}

#account-options {
  background-color: #000000;
}

#login-status ul a {
  color: #BCBCBC;
}

#login-status ul a::first-letter {
  color: #038A74;
}

#login-status ul li a:hover {
  background-color: #0B9D85 !important;
  color: #000000;
  font-style: oblique;
}

#login-status ul li a:hover::first-letter {
  color: #004037;
}

#login-status {
  border: 2px solid #EDEDED;
  padding: 0.5ex 1ex 0.5ex 1ex;
  color: #EDEDED;
  background: #001133;
}

#login-status a {
  background: transparent;
  color: #0B9D85;
}

#login-status a:hover {
  background: #038A74 !important;
  color: #EDEDED;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type="submit"],
#search-top-box-form input[type="submit"]:hover,
#search-top-box-form input[type="submit"]:focus {
  border: #EDEDED solid 2px;
  background: #001133;
  color: #EDEDED;
}

#search-top-box input.empty {
  color: #001133;
}

/* SIDE BAR - SERVING MOSCOW MULES */
#side-bar .side-block,
#interwiki .side-block {
  box-shadow: 0.5em 0.85em 0 0 #1E1E24;
}

#side-bar .side-block.media {
  background: #001133;
}

#side-bar div.menu-item a,
#side-bar .collapsible-block-link,
#interwiki div.menu-item a,
#interwiki .collapsible-block-link {
  color: #18BFA3;
}

#side-bar div.menu-item a:hover,
#side-bar .collapsible-block-link:hover,
#interwiki div.menu-item a:hover,
#interwiki .collapsible-block-link:hover {
  padding: 0 0.5rem 0 0.5rem;
  font-style: oblique;
  color: #000000;
}

#side-bar .side-block,
#side-bar .side-block .heading p,
#interwiki .side-block,
#interwiki .side-block .heading p {
  background-color: #000000;
  color: #EDEDED;
}

#side-bar .side-block.resources,
#side-bar .side-block.resources .heading p {
  background-color: #001133;
}

#side-bar {
  background-color: #01020D;
}

#side-bar .menu-item,
#interwiki .menu-item {
  color: #18BFA3;
}

/* TOP BAR STOOL*/
#top-bar ul li a {
  font-family: "Terminus" ,"Mx437 IBM VGA 8x16", "Noto Sans SC", monospace;
  padding: 0 1ex 0 1ex;
  color: #B3B3B3;
}

#top-bar ul li a:hover,
#top-bar ul li:hover a {
  padding: 0 1.5ex 0 1.5ex;
  background-color: #18BFA3 !important;
  color: #000000;
}

#top-bar ul li a:hover::first-letter,
#top-bar ul li:hover a::first-letter {
  color: #004037;
}

#top-bar ul li a:hover::before,
#top-bar ul li a:hover::after {
  background-color: #00D6AF;
  color: #B3B3B3;
}

#top-bar ul li a:visited:hover {
  color: #000000 !important;
}

#top-bar ul li a::first-letter {
  color: #0B9D85;
}

#top-bar .open-menu a {
  border: 2px solid #EDEDED;
  border-radius: 0;
  background-color: #00021B;
  color: #B3B3B3 !important;
}

#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
  background-color: #000000 !important;
  color: #B3B3B3;
  padding-left: 1ex;
}

#top-bar ul li a:visited,
#top-bar ul li.sfhover ul li a:visited,
#top-bar ul li.sfhover ul li a:visited,
#top-bar ul li.sfhover ul li a:visited:hover,
#top-bar ul li:hover ul li a:visited:hover {
  color: #B3B3B3 !important;
}

#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover {
  font-style: oblique !important;
}

#top-bar ul li.sfhover ul li a::first-letter, #top-bar ul li:hover ul li a::first-letter {
  color: #0B9D85;
}

/* YUI TABS, A LOT OF TABS */
div.yui-navset div.yui-content {
  box-shadow: 0.5em 1em 0 0 #1E1E24;
}

div.yui-navset div.yui-content blockquote,
div.yui-navset div.yui-content div.blockquote {
  box-shadow: 0.5em 1em 0 0 #282834;
  font-family: "Hermit", "Jost", "Noto Sans SC", sans-serif;
}

div.yui-navset div.yui-content h1 {
  color: #073C34;
  font-weight: bold;
}

div.yui-navset div.yui-content h2,
div.yui-navset div.yui-content h3,
div.yui-navset div.yui-content h4,
div.yui-navset div.yui-content h5,
div.yui-navset div.yui-content h6 {
  color: #000000;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
  font-family: "Terminus", "Mx437 IBM VGA 8x16", "Noto Sans SC", monospace;
}

div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
  background: #ADADAD;
  border: #444444 solid 2px;
  border-bottom: transparent 0;
}

div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
  background: #09E381;
  border: #EDEDED solid 2px;
  border-bottom: transparent 0;
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
  background: #00005E;
  border: #444444 solid 2px;
  color: #EDEDED;
  border-bottom: transparent 0;
}

div.yui-navset div.yui-content hr,
blockquote hr,
div.blockquote hr{
  background-color: #073C34;
  margin: 2em 0 2em 0;
}

table.wiki-content-table {
  box-shadow: 0.5em 1em 0 0 #3E3E51;
}

table.wiki-content-table td {
  border: 1px solid #000000;
}

/* RATING MODULE */

#page-content .page-rate-widget-box {
  font-family: "Terminus", "Mx437 IBM VGA 8x16", "Noto Sans SC", monospace;
  background-color: #01020D;
}

div.page-rate-widget-box .rate-points {
  background-color: #01020D;
  border: 2px solid #01020D;
  color: #B3B3B3;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #B3B3B3;
  border: 2px solid #01020D;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  color: #01020D;
  font-family: "Mx437 IBM VGA 8x16", monospace;
  font-weight: 400;
}

.page-rate-widget-box .rateup a:hover {
  color: #1A9400;
}

.page-rate-widget-box .ratedown a:hover {
  color: #D60000;
}

.page-rate-widget-box .cancel {
  border: 2px dashed #01020D;
  background-color: #01020D;
}

.page-rate-widget-box .cancel a {
  color: #B3B3B3;
}

.page-rate-widget-box .cancel a:hover {
  background-color: transparent;
  color: #D60000;
}

/* EDIT MODULE EOU */
#lock-info {
  color: #000000;
}

/* BOTTOM SHIT YOU PROLLY WOULDN'T HAVE NOTICED */
#license-area {
  color: #828282;
}

.hovertip {
  background-color: #01020D !important;
  color: #EDEDED !important;
  border: solid 1px #EDEDED !important;
}

.footnotes-footer {
  background-color: #ADADAD;
  color: #000000;
  padding: 0.4rem 0.15rem 0.5rem 0.4rem;
  border-left: 1rem solid #073C34;
}

.footnotes-footer a {
  color: #073C34;
}

.footnotes-footer a:hover {
  background-color: #127354;
  color: #ADADAD;
}

.footnotes-footer .title {
  margin: 0 0 0.5em 0;
  font-family: "Michroma", "Noto Sans SC", sans-serif;
  color: #073C34;
}

.page-tags a:hover {
  padding: 0 1ex 0 1ex;
}

/* FIXING MOBILE SHIT */

@media (max-width: 767px) {
  #top-bar ul li {
    height: 1.45em;
    font-size: 85%;
  }

  #top-bar ul li ul li {
    height: 1.25em;
    font-size: 1rem;
    padding: 0 0 1ex 0;
    background-color: #000000;
  }
}

/* CUSTOM COLORS DIVS :) */
:root {
  --turbo-block-shadow: 0.5em 1em 0 0 #1E1E24;
}

.turbo-block {
  font-family: "Hermit", "Noto Sans SC", sans-serif;
