/* 2021 Wikidot Theme
by Dr Hormress & Universe647
based on Simple Yonder Theme by EstrellaYoshte */
/* ========UNFORGETTABLE======== */

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@300;400;500;700&subset=cyrillic,cyrillic-ext,greek,greek-ext,latin-ext');
@import url('https://fonts.googleapis.com/css2?family=Raleway:wght@300;400;500;700&subset=cyrillic,cyrillic-ext,greek,greek-ext,latin-ext');
@import url('https://fonts.googleapis.com/css2?family=M+PLUS+1p:wght@300;400;500;700&subset=cyrillic,cyrillic-ext,greek,greek-ext,latin-ext');
@import url('https://fonts.googleapis.com/css2?family=Anonymous+Pro:wght@300;400;500;700&subset=cyrillic,cyrillic-ext,greek,greek-ext,latin-ext');
@import url('https://fonts.googleapis.com/css2?family=BIZ+UDGothic:wght@400;700&subset=cyrillic,cyrillic-ext,greek,greek-ext,latin-ext');

#page-content {
    font-size: 0.87rem;
}

body {
    color: #000;
    font-family: "Noto Sans JP", sans-serif;
    background-color: #FFF;
}

.code pre, .code p, .code, tt, #edit-page-form * {
    font-family: "Anonymous Pro", "BIZ UDGothic", monospace;
}

.page-source{
    font-family: "Anonymous Pro", "BIZ UDGothic", monospace;

}

a {
    color: #AAA;
}

a:visited {
    color: #AAA;
}

a.newpage {
    color: silver;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #000;
    font-family: "Raleway", "M PLUS 1p", sans-serif;
    font-weight: bold;
    filter: blur(0.5px);
}

#page-title,
div.meta-title {
    font-family: "Raleway", "M PLUS 1p";
    filter: blur(0.5px);
    color: #000;
    font-weight: bold;
    font-size: 2.4em;
    border: none;
}

@media (min-width: 768px) {
    #main-content {
        margin: 0 2em 0 22em;
    }
}

/* ---- HEADER ---- */

div#container-wrap {
    background-image: linear-gradient(to bottom, #FFF 0, #FFF 148px, #000 151px, #FFF 154px, #FFF 155px);
    background-size: auto 160px;
    background-repeat: repeat-x;
}

#header h1, div#header::before {
    filter: blur(2px);
}

#header {
    background-image: none;
}

div#header::before {
    content: "";
    position: absolute;
    top: 0;
    left: -3px;
    width: 150px;
    height: 150px;
    background-image: linear-gradient(-45deg, transparent, #FFF), url("https://scp-jp-storage.wdfiles.com/local--files/file%3A8260379-66-pxvf/minimal.svg");
    background-size: 6.7rem, 6.7rem;
    background-position: 3px 29px, 3px 29px;
    background-repeat: no-repeat, no-repeat;
}

@media (max-width: 767px) and (min-width: 480px) {
    div#header::before {
      background-size: 5.4rem, 5.4rem;
      background-position: 0 44px, 0 44px;
    }
}

@media (max-width: 479px) {
    div#header::before {
      background-size: 4.2rem, 4.2rem;
      background-position: 0 58px, 0 58px;
    }
}

#login-status {
    filter: drop-shadow(0 0 5px #000);
}

#login-status,
#login-status a {
    color: #000;
    font-weight: bold;
}

#account-topbutton{
    border: 0px;
}

#search-top-box {
    bottom: 0;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: none;
    background: #000;
    box-shadow: 0 0 5px #000;
    border-radius: 3px;
    color: #FFF;
}

#search-top-box input.empty {
    color: #DDD;
}

div#header h1 a {
    font-family: "Raleway", "M PLUS 1p", sans-serif;
    font-weight: bold;
    color: #000;
    text-shadow: none;
}

div#header h1 a::before {
    content: "反ミーム部門";
}

div#header h1 a span {
    display: none;
}

div#header h2 span {
    font-family: "Raleway", "M PLUS 1p", sans-serif;
    font-weight: bold;
    color: #000;
    padding: 17px 0;
    text-shadow: none;
    white-space: pre;
    filter: blur(1px);
}

#top-bar div > ul {
    background-color: #000;
    box-shadow: 0 0 5px #000;
    border-radius: 3px;
}

#top-bar div > ul > li > a {
    border-radius: 3px;
    border: solid 2px #000;
}

#top-bar div > ul > li:hover > a,
#top-bar div > ul > li.sfhover > a {
    color: #000;
    background-color: #FFF;
    border: solid 2px #000;
    box-shadow: inset 0 0 3px #000;
}

#top-bar div > ul > li > ul {
    background-color: #000;
    box-shadow: 0 0 5px #000;
    border-radius: 3px;
    border: none;
}

#top-bar div > ul > li:hover > ul > li > a,
#top-bar div > ul > li.sfhover > ul > li > a {
    background-color: transparent;
    border: solid 2px #000;
    color: #FFF;
    border-radius: 3px;
}

#top-bar div > ul > li:hover > ul > li > a:hover,
#top-bar div > ul > li.sfhover > ul > li > a:hover {
    background-color: #FFF;
    color: #000;
    box-shadow: inset 0 0 3px #000;
}

/* ---- SIDE BAR ---- */

#top-bar .open-menu a,
#side-bar::before {
    position: fixed;
    bottom: 0.5em;
    left: 0.5em;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    background-color: #FFF !important;
    color: #000 !important;
    border-radius: 3px;
    border: solid 2px #000 !important;
    filter: blur(1px);
}

#top-bar .open-menu a:hover,
#side-bar:hover::before {
    text-decoration: none;
    box-shadow: 0 0 20px 3px rgba(153,153,153,1);
}

:is(#side-bar, #interwiki) a,
:is(#side-bar, #interwiki) a.newpage {
    color: #000;
}

:is(#side-bar, #interwiki) img {
    filter: grayscale(100%) blur(0.5px);
}

#side-bar .collapsible-block-folded {
    background-image: none;
}

:is(#side-bar, #interwiki) .side-block {
    box-shadow: inset 0 0 3px #000, 0 0 5px #000;
    border: solid 2px #000;
    background-color: #FFF !important;
    border-radius: 5px;
}

:is(#side-bar, #interwiki) .side-block .heading,
#side-bar .collapsible-block-unfolded-link {
    border: none;
    filter: drop-shadow(0 0 5px #000);
    margin-left: -10px;
    margin-right: -10px;
}

/* #side-bar .side-block .heading::after,
#side-bar .collapsible-block-unfolded-link::after {
    content: "";
    display: block;
    position: absolute;
    top: 6px;
    left: 2px;
    z-index: -1;
    width: 75%;
    height: 2px;
    border-radius: 1px;
    background-color: #000;
} */

:is(#side-bar, #interwiki) .side-block .heading p,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    display: inline-block;
    background-color: #000;
    font-size: 12px;
    margin-top: 0;
    padding: 1px;
    padding-left: 3px;
    padding-right: 3px;
    color: #FFF;
    border-radius: 3px;
    width: fit-content;
    width: -webkit-fit-content;
    width: -moz-fit-content;
}

@media (max-width: 767px) {
    #side-bar {
        background-color: #000;
    }

    #side-bar:target {
        border: none;
    }
}

/* ---- YUI TABS ---- */

.yui-navset {
    border-radius: 5px;
    box-shadow:none;
    background-color: transparent;
}

.yui-navset .yui-nav {
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    background-color: transparent;
    margin: 0;
    padding: 0;
    border: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav a em {
    padding: 0.25em .75em;
    top: 0px;
    margin-bottom: 0px;
    border: none;
}

.yui-navset .yui-nav .selected {
    margin: 0px;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover,
.yui-navset .yui-nav .selected a {
    background-image: none;
    background-color: #FFF;
    border: solid 1px transparent;
    border-radius: 5px;
    height: 2em;
    color: #000;
    box-shadow: inset 0 0 3px #AAA;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: transparent;
    background-image: none;
    border: solid 1px transparent;
    border-radius: 5px;
    height: 2em;
    color: #000;
    box-shadow: none;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background-image: none;
    background-color: transparent;
    box-shadow: inset 0 0 3px #EEE;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover,
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-image: none;
}

.yui-navset .yui-nav a {
    background-image: none;
}

.yui-navset .yui-nav li {
    margin: 0px;
    padding: 0px;
    background-image: none;
}

.yui-navset .yui-content {
    border: solid 1px transparent;
    border-radius: 5px;
    box-shadow: inset 0 0 3px #AAA;
    background-color: #FFF;
}

.darkstyle .yui-navset {
    border-radius: 5px;
    box-shadow: 0 0 5px #000;
    background-color: #000;
