    border-radius: 5px;
}

.scp-image-block .scp-image-caption {
    background-color: #FFF;
    border-top: none;
    color: #000;
}

.scp-image-block img {
    border-radius: 3px;
    box-shadow: inset 0 0 3px #DDD;
    background-color: #FFF;
}

.darkstyle .scp-image-block {
    border: none;
    padding: 6px;
    background-color: #000;
    box-shadow: 0 0 5px #000;
    box-sizing: border-box;
    border-radius: 5px;
}

.darkstyle .scp-image-block .scp-image-caption {
    background-color: #000;
    border-top: none;
    color: #FFF;
}

.darkstyle .scp-image-block img {
    border-radius: 3px;
    box-shadow: inset 0 0 3px #000;
    background-color: #FFF;
}

/* ---- QUOTE BLOCK & CODE BLOCK ---- */

blockquote,
div.blockquote,
.blockquote,
.code,
#toc {
    background-color: #FFF;
    border: solid 1px transparent;
    border-radius: 5px;
    box-shadow: inset 0 0 3px #AAA;
}

.darkstyle blockquote,
.darkstyle div.blockquote,
.darkstyle .blockquote,
.darkstyle .code,
.darkstyle #toc {
    background-color: #FFF;
    border: solid 2px #000;
    border-radius: 5px;
    box-shadow: inset 0 0 3px #000, 0 0 5px #000;
}

/* ---- WIKI CONTENT TABLE ---- */

#page-content table.wiki-content-table {
    border-collapse: separate;
}

#page-content .wiki-content-table th {
    box-shadow: inset 0 0 3px #AAA;
    background-color: #EEE;
    border: solid 1px #FFF;
    font-family: "Raleway", "M PLUS 1p";
    border-collapse: separate;
}

#page-content .wiki-content-table td {
    box-shadow: inset 0 0 3px #DDD;
    background-color: #FFF;
    border: solid 1px #FFF;
    border-collapse: separate;
}

#page-content .wiki-content-table tr:nth-child(1) > *:nth-child(1) {
    border-top-left-radius: 5px;
}

#page-content .wiki-content-table tr:nth-child(1) > *:nth-last-child(1) {
    border-top-right-radius: 5px;
}

#page-content .wiki-content-table tr:nth-last-child(1) > *:nth-child(1) {
    border-bottom-left-radius: 5px;
}

#page-content .wiki-content-table tr:nth-last-child(1) > *:nth-last-child(1) {
    border-bottom-right-radius: 5px;
}

#page-content .darkstyle table.wiki-content-table {
    border-collapse: separate;
    border: solid 1px #000;
    border-radius: 5px;
    background-color: #000;
    box-shadow: 0 0 5px #000;
}

#page-content .darkstyle .wiki-content-table th {
    box-shadow: inset 0 0 3px #000;
    background-color: #000;
    color: #FFF;
    border: solid 1px #000;
    font-family: "Raleway", "M PLUS 1p";
    border-collapse: separate;
}

#page-content .darkstyle .wiki-content-table td {
    box-shadow: inset 0 0 3px #000;
    background-color: #FFF;
    border: solid 1px #000;
    border-collapse: separate;
}
