[[code type="css"]]
/*
Commission of Unusual Cargo Theme by CityToast

Based on Aces and Eights Theme by rounderhouse, SCP Sigma 9 Theme created by Aelanna and Dr Devan, and MC&D Theme created by Randomini.
*/

@import url(https://scp-wiki.wdfiles.com/local--code/theme%3Aunusual-cargo/1);

@import url('https://fonts.googleapis.com/css?family=Caladea|IM+Fell+DW+Pica|IM+Fell+DW+Pica+SC|Tangerine');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnPenheulim.css');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnJamoBatang.css');
@import url('https://scpko.wdfiles.com/local--files/korean-font/조선100년체.css');
@import url('https://manggwan.wdfiles.com/local--files/font%3Ahuiwen-mincho/huiwen-mincho.css');/*p*/

/* Hyperlink colors */
:root {
    --hyperlink: #824565;
    --hyperlink-visited: #63173f;
}

#header h1 a::before {
    font-family: 'IM Fell DW Pica SC', '조선100년체', 'huiwen-mincho', serif;
    padding: 35px 0 25px;
    font-weight: normal;
    content: "정당하고 명예로운";
    color: #000000;
}

#header h2 span {
    font-size: 175%;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    font-family: 'Tangerine', 'UnPenheulim', cursive;
    font-size: 230%;
    letter-spacing: -10px;
    font-style: italic;
    content: "특이화물";
    color: #000000;
    padding: 0px 20px 00px;
}
#header h2 span::after {
    font-family: 'IM Fell DW Pica SC', '조선100년체', 'huiwen-mincho', serif;
    font-size: 100%;
    font-weight: normal;
    content: "판무처";
    color: #000000;
    padding-left: 9em;
    float: left;
    width: 5.6em;
}

/* Media stuff for mobile/responsive; these changes occur under 565px */
@media (min-width: 566px) and (max-width: 801px) {
    #header h1 a::before {
     content: "정당하고 명예로운";
     padding: 35px 0 25px 45px;
    }
}
@media (max-width: 565px) {

    #header h1 a::before {
     content: "정당하고 명예로운";
     padding: 35px 0 25px 40px;
    }

}

/* stuff with body h groups */

h2, h3, h4, h5, h6 {
    font-family: 'IM Fell DW Pica SC', '조선100년체', 'huiwen-mincho', serif;
}

h1, #page-title {
    color: #000;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: heavy;
    font-family: 'IM Fell DW Pica SC', '조선100년체', 'huiwen-mincho', serif;
}


/* stuff for social media bar */

#side-bar .heading {
    font-family: 'Tangerine', 'UnPenheulim', cursive;
    font-size: 1.5em;
    font-weight: bold;
    font-style: italic;
}

#side-bar .menu-item {
    font-family: 'Caladea', 'UnJamoBatang', serif;
    padding: .5em;
    font-weight: normal;
    box-shadow: none !important;
    margin-bottom: 15px;
    color: black;
}

/* #top-bar: Color changes, removing border, modifying font sizes, and changing general appearance */
#top-bar li a{
    text-align: center;
    font-family: 'Caladea', 'UnJamoBatang', serif;
    display: block;
    font-size: 1.2em;
    margin: 0;
    padding: 1px .5em;
    text-decoration: none;
}

.UC_footing h2 {
    font-family: 'Tangerine', 'UnPenheulim', cursive;
    font-size: 2.1em;
    font-style: italic;
}

textarea {
   font-family: monospace;
}

input#edit-page-title.text {
    font-family: 'IM Fell DW Pica SC', '조선100년체', 'huiwen-mincho', serif;
}
