[[code type="css"]]

/*
   Valravn's Theme
   [2022 Wikidot Theme]
   by Kilerpoyo
          an extension of the Present Malice Theme by JackalRelated
*/

@import url('https://scp-wiki.wikidot.com/local--files/theme:valravn/nocurves.css');
@import url('https://scp-wiki.wikidot.com/local--files/theme:valravn/odinspear.css');
@import url('https://scp-wiki.wikidot.com/local--files/theme:valravn/comic-runes.css');

:root {

	--theme-id: "valravn";
	--theme-name: "Valravn Corporation Theme";

	--logo-image: url("http://scp-wiki.wikidot.com/local--files/theme:valravn/valwhite") !important;
	--header-title: "VALRAVN CORPORATION";
	--header-subtitle: "We feast on the Battlefield.";

	/* Valravn theme palette */
	--nordic-seaweed: 1, 58, 64;
	--pax-vikinga: 17, 95, 105;
	--black-raven: 20, 20, 20;
	--white-raven: 234, 238, 239;
	--blue-raven: 12, 185, 204;
	--mistral-mithril: 151, 144, 102;
	--sundowner-silver: 146, 166, 165;
	--monsoon-aurora: 20, 180, 204;
	--jotun-blue: 8, 143, 143;
	--dirty-mithril: 134, 127, 91;
	--bright-mithril: 118, 108, 50;

	/* Typefaces */
	--header-font: 'comic_runesregular', 'nocurvesboustrophedonregular';
	--decorative-font: 'odins_spearregular', Verdana;
	--decorative-font2: 'odins_spear_ragged_hollowRg', Verdana;

	/* Replace Present Malice vars with Valravn vars */
	--light-stars-blue: var(--mistral-mithril);
	--medium-stars-blue: var(--nordic-seaweed);
	--dark-stars-blue: var(--pax-vikinga);
	--chris-blood: var(--pax-vikinga);
	--umbrella-red: var(--pax-vikinga);
	--barry-magnum: var(--white-raven);
	--werskers-shades: var(--black-raven);
	--jills-shoulderpads: var(--nordic-seaweed);

	/* Extra tweaks */
	--dark-accent: var(--nordic-seaweed);
	--visited-link-color: var(--pax-vikinga);
	--link-color: var(--jotun-blue);
	--rating-module-button-plus-color: var(--monsoon-aurora);
	--swatch-topmenu-bg-color: var(--mistral-mithril);
	--swatch-topmenu-border-color: var(--mistral-mithril);

}

#top-bar {
	--topmenu-category-hover-bg: var(--bright-mithril);
	--topmenu-hover-border-color: var(--dirty-mithril);
	--dropdown-bg-color: var(--nordic-seaweed), 0.9;
}

#header {
	--swatch-headerh1-color: var(--white-raven);
	--login-username-color: var(--white-raven);
}

#side-bar {
	--sidebar-resources-bg-color: var(--nordic-seaweed);
	--sidebar-media-bg-color: transparent;
}

#main-content {
	--tabs-bg: var(--nordic-seaweed);
	--tabs-selected-bg: var(--pax-vikinga);
	--tabs-selected-outline: var(--pax-vikinga);
	--tabs-hover-bg: var(--mistral-mithril);
	--tables-header-bg: var(--mistral-mithril);
	--footnotes-footer-bg-color: var(--white-raven);
	--tables-body-bg: var(--sundowner-silver), 0.25;
}

.yui-navset .yui-content {
	background-color: rgba(var(--white-raven));
}

#footer {
	--footer-bg-color: var(--mistral-mithril);
}

/* Paper note component */

.papernote {
	background-color: rgba(var(--white-raven));
	color: rgba(var(--black-raven)) !important;
	padding: 0.1rem 0.5rem 0.5rem 0.5rem;
	box-shadow: 1px 1px 3px 2px rgb(0 0 0 / 30%);
	margin-left: 0.5em;
	margin-right: 0.5em;
}

/* Image block component */
.scp-image-block{
	color: rgba(var(--white-raven)) !important;
}

/* Simple terminal component */

.danke {
	padding: 5px;
	margin-bottom: 10px;
	font-family: monospace;
	font-size: 1.1em;
}

.agent {
	background-color: rgba(var(--black-raven));
	border: 3px solid rgba(var(--monsoon-aurora));
	color: rgba(var(--monsoon-aurora));
}

.site {
	background-color: rgba(var(--black-raven));
	border: 3px solid rgba(var(--monsoon-aurora));
	color: rgba(var(--monsoon-aurora));
}

