[[code type="css"]]

/* Vanguard Theme by MisterFrown */

/* Fonts */

@import url('https://fonts.googleapis.com/css2?family=Zilla+Slab:ital,wght@0,300;0,400;0,700;1,300;1,400;1,700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Alegreya+SC:ital,wght@0,400;0,500;0,700;0,800;0,900;1,400;1,500;1,700;1,800;1,900&display=swap');

/* Main Root Block */

:root {

--theme-base: "black-highlighter";
--theme-id: "vanguard";
--theme-name: "Vanguard Theme";

--logo-image: url("https://scp-wiki.wikidot.com/local--files/grigori-karpin-s-author-page/vanguard%20transparent.png");
--header-title: "ANGUARD";
--header-subtitle: "SHELTER, NORMALIZE, INFORM";
--header-h2-font-size: 1.1em;
--header-font: 'Zilla Slab', serif;

--medium-accent: 0, 102, 0;
--dark-accent: 0, 153, 75;
--bright-accent: 102, 205, 170;
--white-monochrome: 240, 255, 255;
--gray-monochrome: var(--dark-accent);

--swatch-secondary-color: 248, 248, 255;
--swatch-tertiary-color: var(--dark-accent);

--link-color: var(--dark-accent);
--visited-link-color: var(--bright-accent);
--hover-link-color: var(--medium-accent);
--newpage-color: var(--medium-accent);

--rating-module-bg-color: var(--swatch-secondary-color);
--rating-module-bottom-border-color: var(--dark-accent);

--header-height-on-desktop: 11rem;
--header-height-on-mobile: 11rem;
--background-gradient-color: var(--dark-accent);
--background-gradient-distance: 18rem;
--diagonal-stripes: none;

--gradient-topmenu: linear-gradient(180deg, rgb(var(--medium-accent)) 0%, rgb(var(--dark-accent)) 5%, rgb(var(--dark-accent)) 95%, rgb(var(--medium-accent)) 100%);

--toggle-border-color: rgb(var(--dark-accent));
--toggle-icon-color: rgb(var(--dark-accent));
--toggle-roundness: 1%;

--zero: 0;

}

/* Main Content Block */

#main-content {

--tabs-bg: var(--dark-accent);

}

/* Header Code modified from SCP-6500 */

#header::before {

left: -9.5rem;
filter: brightness(125%);
opacity: 1;

}

@media (max-width: 767px) {

#header::before {

left: -7rem;

}

#header h1 {

top: 0.5em;
left: 0.5em;

}

}

/* Header */

#extra-div-2 {

height: var(--header-height-on-desktop);
width: 100%;
position: absolute;
top: 0;
left: 0;
pointer-events: none;
background-color: rgb(72,209,204);
background-image: linear-gradient(180deg, rgba(72,209,204,1) 4%, rgba(153,230,179,1) 48%, rgba(253,253,150,1) 80%);

}

@media (max-width: 400px) {

#extra-div-2 {

background-size: calc(var(--header-height-on-desktop) * 3);

}

}

/* Image Block */

:is(div.image-block, div.scp-image-block) :is(.image-caption, .scp-image-caption) {

color: #ffffff;
background: rgb(var(--dark-accent));
font-family: var(--header-font);

}

/* Divs */

:is(blockquote, div.blockquote, [class*="blockquote"]) {

background-color: rgb(var(--swatch-secondary-color));
border-left: 5px solid rgb(var(--dark-accent));

}

/* Spans */

.vantitle {

font-family: 'Copperplate Gothic', 'Alegreya SC', sans-serif;
color: #1328B4;
font-size: 280%;
font-weight: bold;

}

.twosideblock {

margin-left: auto;
margin-right: auto;
width: 90%;
background: rgb(var(--swatch-secondary-color));
border-left: solid 3.5px rgb(var(--dark-accent));
border-right: solid 3.5px rgb(var(--dark-accent));
margin-bottom: 7px;
padding-right: 15px;
padding-left: 15px;
padding-top: 5px;
padding-bottom: 5px;
box-shadow: 0px 0px 2px rgb(var(--bright-accent));

}

[[/code]]

[[code type="css"]]

/* Dark Mode */

:root {

--medium-accent: 0, 102, 0;
--dark-accent: 50,205,50;
--bright-accent: 102,205,170;
--white-monochrome: 19, 42, 74;
--gray-monochrome: var(--dark-accent);

--swatch-secondary-color: 12, 26, 46;

--rating-module-text-color: var(--swatch-text-light);

--swatch-text-light: var(--light-pale-gray-monochrome);
--swatch-text-general: var(--swatch-text-light);
--swatch-menutxt-general-color: var(--swatch-menutxt-light-color);
--swatch-menutxt-light-color: var(--swatch-text-light);

--gradient-topmenu: linear-gradient(180deg, rgb(var(--medium-accent)) 0%, rgb(var(--dark-accent)) 2%, rgb(var(--dark-accent)) 98%, rgb(var(--medium-accent)) 100%);

--hoverblock-bg: var(--white-monochrome);
--hoverblock-txt: var(--swatch-text-light);
--hoverblock-header-bg: var(--dark-accent);
--hoverblock-header-txt: var(--white-monochrome);

}

#main-content {

--ui-button-bg-color: var(--rating-module-bg-color);
--ui-icon-color: var(--medium-accent);
--ui-icon-hover-color: var(--dark-accent);
--ui-button-txt: var(--swatch-text-light);
--ui-button-hover-txt: var(--swatch-text-light);

}

#header {

--login-username-color: var(--swatch-text-light);
--login-myaccount-color: var(--swatch-text-light);
--search-icon-color: var(--swatch-text-light);
--login-dropdown-text-hover-color: var(--swatch-text-light);
--login-line-divider-color: var(--swatch-text-light);

}

#top-bar {

--dropdown-links-hover-color: var(--swatch-text-light);
--topmenu-category-hover-color: var(--swatch-text-light);
--topmenu-category-color: var(--swatch-text-light);
--mobile-topmenu-sidebar-button-color: var(--swatch-text-light);

}

#interwiki .heading p,
#side-bar .heading p,
#side-bar .collapsible-block .collapsible-block-link {

color: rgb(var(--dark-accent));

}

.bibitems,
.footnotes-footer {

background-color: rgb(var(--swatch-secondary-color));

}

.bibitems .bibitem::after,
.footnotes-footer a[href*="javascript"]::before {

background-color: rgb(var(--swatch-secondary-color));

}

.vantitle {

color: rgb(var(--dark-accent));

}

[[/code]]

