[[module css]]
@import url("https://nu-scptheme.github.io/Black-Highlighter/css/min/normalize.min.css");
@import url("https://nu-scptheme.github.io/Black-Highlighter/css/min/black-highlighter.min.css");



:root {
  --logo-image: url("http://scp-ukrainian.wdfiles.com/local--files/theme%3Avaz-sbu/vaz-sbu-emblem.png");

 /* Standard Colors */
    --white-monochrome: 252, 252, 252;
    /* white */
    --pale-gray-monochrome: 244, 244, 244;
    /* v light gray for blockquotes and stuff */
    --light-gray-monochrome: 170, 170, 170;
    /* light accent gray for login status */
    --gray-monochrome: 66, 66, 72;
    /* gray */
    --dark-gray-monochrome: 48, 48, 52;
    /* dark accent gray for sidebar background */
    --black-monochrome: 12, 12, 12;
    /* black */
    --pale-accent: 164, 194, 236;
    --bright-accent: 164, 194, 236;
    /* bright blue */
    --medium-accent: 92, 149, 230;
    /* pale blue */
    --dark-accent: 39, 84, 149;
    /* v pale blue */
    --newpage-color: 99, 190, 255;

  /* Primary Theme Colors */
    --swatch-primary: var(--bright-accent);
    --swatch-primary-darker: var(--medium-accent);
    --swatch-primary-darkest: var(--dark-accent);

    /* Primary Menu Colors */
    --swatch-menubg-color: var(--pale-gray-monochrome);
    --swatch-menubg-dark-color: var(--medium-accent);
    --swatch-menubg-hover-color: var(--gray-monochrome);
    --swatch-menutxt-light-color: var(--white-monochrome);
    --swatch-border-color: var(--bright-accent);

    /* Primary Header Colors */
    --swatch-headerh1-color: var(--dark-accent);
    --swatch-headerh2-color: var(--dark-accent);
    --rating-module-button-color: var(--black-monochrome);
    --rating-module-text-color: var(--black-monochrome);
}
:root:lang(cn) {
    --header-title: "乌克兰国家安全局";
    --header-subtitle: "异常威胁部门";
    --gradient-header: url("http://scp-ukrainian.wdfiles.com/local--files/theme%3Avaz-sbu/vaz-head.png");
}



#header {
    /* ===HEADER ELEMENTS=== */

     /* ===SEARCH ELEMENT=== */
    --search-icon-hover-bg-color: var(--swatch-primary);
     /* Focus is when the search box has been clicked on */
    --search-focus-textbox-text-color: var(--swatch-text-tertiary-color);
}

div#extra-div-1 {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: var(--header-height-on-desktop);
}

div#extra-div-1 {
    z-index: -2;
    background: linear-gradient(to bottom, rgba(30, 55, 30, 1) 0%, rgba(30, 55, 30, 1) 30%, rgba(255, 219, 90, 1) 100%);
    background: var(--gradient-header);
    background-repeat: repeat;
    background-size: var(--header-background-image-size);
}

#header {
    position: -webkit-sticky;
    position: sticky;
    top: calc((var(--header-height-on-desktop) + 0.125rem) * -1);
    background-image: initial;
    -webkit-filter: inherit;
     filter: inherit;
}

#header::before {
    content: " ";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: var(--header-height-on-desktop);
    opacity: 0.75;
    background-image: var(--logo-image);
    background-repeat: no-repeat;
    background-position: left 0.75rem center;
    background-size: auto 7rem;
    pointer-events: none;
}

#header h1,
#header h1 a,
#header h1 a::before,
#header h1 a::after {
    --wght: 700;
    display: flex;
    position: absolute;
    z-index: 0;
    top: 0;
    left: 0;
    align-items: center;
    justify-content: center;
    width: 100%;
    height: var(--header-height-on-desktop);
    max-height: inherit;
    margin: inherit;
    padding: inherit;
}

#header h1 a,
#header h1 a::before,
#header h1 a::after {
    color: rgb(var(--swatch-headerh1-color));
    line-height: 0.8;
    text-align: left;
    overflow-wrap: normal;
}

#header h1 a::before,
#header h1 a::after {
    content: "Відділ Аномальних Загроз";
    content: var(--header-title);
}

@media not all and (-webkit-min-device-pixel-ratio:0), not all and (min-resolution:.001dpcm) {
    @supports (-webkit-appearance:none) {
     #header h1 a::after {
     display: -webkit-box !important;
     }
    }
}

#header h1 a::after {
    background: url(https://wanderers-library.wikidot.com/local--files/component:dustjacket-theme/goldfoil.png);
    -webkit-background-clip: text;
    background-clip: text;
    background-size: cover;
    -webkit-text-fill-color: transparent;
}

#header h1 a::before {
    text-shadow:
     rgba(var(--dark-gray-monochrome), 0.5) 0.125rem 0rem 0,
     rgba(var(--gray-monochrome), 0.5) 0.0675rem 0.1052rem 0,
     rgba(var(--gray-monochrome), 0.5) -0.052rem 0.1137rem 0,
     rgba(var(--dark-gray-monochrome), 0.5) -0.1237rem 0.0176rem 0,
     rgba(var(--dark-gray-monochrome), 0.5) -0.0817rem -0.0946rem 0,
     rgba(var(--dark-gray-monochrome), 0.5) 0.0355rem -0.1199rem 0,
     rgba(var(--dark-gray-monochrome), 0.5) 0.12rem -0.0349rem 0;
}

#header > h1>a > span {
    top: 0;
    left: 0;
    width: 100%;
    max-width: inherit;
    height: var(--header-height-on-desktop);
}

#header h2 {
    --wght: 300;
    display: flex;
    position: absolute;
    z-index: 0;
    top: 0;
    left: 0;
    align-items: center;
    justify-content: center;
    width: 100%;
    height: var(--header-height-on-desktop);
    max-height: inherit;
    margin: 0;
    padding: 0;
}

#search-top-box form[id="search-top-box-form"]:not(:focus-within) input[type="text"] {
    color: rgba(0, 0, 0, 0);
}

@media only screen and (min-width: 56.25rem) {


    #account-options {
     background: var(--gradient-header);
    }

    #search-top-box {
     background: rgba(var(--search-focus-textbox-bg-color), 0.4);
     box-shadow: calc(var(--search-height) / 2 * -1) 0 calc(var(--search-height) / 2) rgba(var(--search-focus-textbox-bg-color), 0.4);
    }

    #search-top-box::after {
            transition:
                background-color 150ms cubic-bezier(0.4, 0, 0.2, 1),
                -webkit-clip-path 150ms cubic-bezier(0.4, 0, 0.2, 1);
            transition:
                background-color 150ms cubic-bezier(0.4, 0, 0.2, 1),
                clip-path 150ms cubic-bezier(0.4, 0, 0.2, 1);
            transition:
                background-color 150ms cubic-bezier(0.4, 0, 0.2, 1),
                clip-path 150ms cubic-bezier(0.4, 0, 0.2, 1),
                -webkit-clip-path 150ms cubic-bezier(0.4, 0, 0.2, 1);
        }

        #search-top-box:not(:focus-within)::after {
            background-color: rgba(var(--search-icon-bg-color, --dark-accent),1);
            -webkit-clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
                    clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
        }

    #page-title::after,
    .meta-title::after,
    #page-title::before,
    .meta-title::before {
        content: "";
        flex-grow: 1;
        height: 0.0625rem;
        background: rgb(var(--swatch-primary));
    }

    #page-title::before,
    .meta-title::before {
        margin: auto 1.25rem auto auto;
    }

}
#side-bar {
    background: rgba(237, 243, 250);
}

/* Sticky Top-Bar BG */
div#extrac-div-1 {
    position: absolute;
    z-index: 3;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
}

div#extrac-div-1 span {
    display: flex;
    position: -webkit-sticky;
    position: sticky;
    top: calc(-1 * (var(--header-height-on-desktop) + 0.125rem));
    right: 0;
    left: 0;
    box-sizing: inherit;
    align-items: center;
    width: 100%;
    height: var(--final-header-height-on-desktop);
    margin: 0;
    margin-top: 0;
    padding: 0;
    background: var(--gradient-topmenu);
    background-size: 100% var(--final-header-height-on-desktop);
}

#top-bar br {
    display: none;
}

#header div[class*="top-bar"] li a {
    transition:
     font-weight 300ms cubic-bezier(0.4, 0, 0.2, 1),
     font-variation-settings 300ms cubic-bezier(0.4, 0, 0.2, 1);
}

/* TOGGLE SIDEBAR */

@media only screen and (min-width: 56.25rem) {
    #side-bar .close-menu {
     display: block;
     position: fixed;
     top: 0.25rem;
     left: 0.5rem;
     width: 3rem;
     opacity: 1;
     pointer-events: all;
    }

    #side-bar .close-menu::before,
    #side-bar .close-menu::after {
     content: "";
     display: block;
     position: fixed;
     top: 0.25rem;
     left: 0.5rem;
     box-sizing: border-box;
     width: 3rem;
     height: 3rem;
     margin: 0;
     padding: 0;
     transition:
     opacity var(--sidebar-transition-timing);
     text-align: center;
     cursor: pointer;
     pointer-events: all;
    }

    #side-bar .close-menu::before {
     --mask: url("data:image/svg+xml,%3C%3Fxml version='1.0' %3F%3E%3C!DOCTYPE svg PUBLIC '-//W3C//DTD SVG 1.1//EN' 'http://www.w3.org/Graphics/SVG/1.1/DTD/svg11.dtd'%3E%3Csvg height='32px' id='Layer_1' style='enable-background:new 0 0 32 32;' version='1.1' viewBox='0 0 32 32' width='32px' xml:space='preserve' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink'%3E%3Cpath d='M4,10h24c1.104,0,2-0.896,2-2s-0.896-2-2-2H4C2.896,6,2,6.896,2,8S2.896,10,4,10z M28,14H4c-1.104,0-2,0.896-2,2 s0.896,2,2,2h24c1.104,0,2-0.896,2-2S29.104,14,28,14z M28,22H4c-1.104,0-2,0.896-2,2s0.896,2,2,2h24c1.104,0,2-0.896,2-2 S29.104,22,28,22z'/%3E%3C/svg%3E");
     z-index: -1;
     background-color: var(--toggle-icon-color, rgb(var(--sidebar-links-text))) !important;
     -webkit-mask: var(--mask);
     mask: var(--mask);
     -webkit-mask-repeat: no-repeat;
     mask-repeat: no-repeat;
     -webkit-mask-position: 50% 50%;
     mask-position: 50% 50%;
     -webkit-mask-size: 60%;
     mask-size: 60%;
    }

    #side-bar .close-menu::after {
     z-index: -2;
     border: var(--toggle-border-color, rgb(var(--swatch-border-color))) var(--toggle-border-width, 0.25rem) solid;
     border-radius: var(--toggle-roundness, 50%);
     background-color: var(--toggle-button-bg, rgb(var(--swatch-menubg-color))) !important;
    }

    #side-bar:focus-within .close-menu::before,
    #side-bar:focus-within .close-menu::after {
     opacity: 0;
    }

    #side-bar {
     display: block;
     position: fixed;
     z-index: 10;
     top: 0;
     left: calc(var(--sidebar-width-on-desktop)*-1);
     height: 100%;
     margin-top: 0;
     overflow-x: hidden;
     overflow-y: auto;
     transition:
     left var(--sidebar-transition-timing);
    }

    #side-bar:focus-within {
     left: 0;
    }

    #side-bar .side-block {
     margin-top: 1rem;
     overflow: visible;
     border-right-width: 0rem;
     border-left-width: 0rem;
     border-radius: 0;
     background-color: rgb(0, 0, 0, 0);
    }

    #main-content::before {
     content: "";
     display: block;
     position: fixed;
     z-index: -1;
     z-index: 99;
     top: 0;
     right: 0;
     width: 100%;
     height: 100vh;
     margin-left: var(--sidebar-width-on-desktop);
     padding-right: 0;
     transition:
     opacity var(--sidebar-transition-timing),
     width var(--sidebar-transition-timing);
     opacity: 0;
     background: rgba(var(--swatch-menubg-black-color), .3) 0.0625rem 0.0625rem repeat;
     pointer-events: none;
    }

    #side-bar:focus-within~#main-content::before {
     width: calc(100% - var(--sidebar-width-on-desktop));
     opacity: 1;
     pointer-events: all;
    }

    #main-content {
     position: initial;
     width: 100%;
     width: var(--body-width-on-desktop);
     max-width: var(--body-width-on-desktop);
     max-height: 100%;
     margin: 0 auto;
     padding: 2rem 1rem;
    }

    @media not all and (-webkit-min-device-pixel-ratio:0), not all and (min-resolution:.001dpcm) {
     @supports (-webkit-appearance:none) {
     #side-bar {
     position: fixed;
     z-index: 999;
     padding-right: 0;
     overflow-x: visible;
     overflow-y: visible;
     transition:
     left var(--sidebar-transition-timing),
     background-color var(--sidebar-transition-timing);
     background: rgba(var(--swatch-menubg-light-color), 0);
     pointer-events: all;
     }

     #side-bar::-webkit-scrollbar {
     -webkit-transition: opacity var(--sidebar-transition-timing);
     transition: opacity var(--sidebar-transition-timing);
     opacity: 0;
     }

     #side-bar .close-menu::before {
     z-index: 999;
     }

     #side-bar .close-menu::after {
     z-index: 998;
     }

     #side-bar:hover .close-menu::before {
     opacity: 0;
     }

     #side-bar:hover {
     left: 0;
     padding-right: 0;
     background: rgba(var(--swatch-menubg-light-color), 1);
     }

     #side-bar:hover::-webkit-scrollbar {
     opacity: 1;
     }

     #side-bar:hover .close-menu::before,
     #side-bar:hover .close-menu::after {
     opacity: 0;
     }

     #side-bar:hover~#main-content::before {
     width: calc(100% - var(--sidebar-width-on-desktop));
     opacity: 1;
     pointer-events: all;
     }
     }
    }
}
.page-rate-widget-box .rate-points {
    background-color: #A4C2EC !important;
}
[[/module]]

