[[code type="css"]]
@import url(https://scp-wiki.wdfiles.com/local--code/theme%3Awanderers/1);
@font-face {
    font-family: '망고빙수';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2106@1.1/SF_IceMango.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

#header h1 a {
    font-family: 'BauhausLTDemi', '망고빙수', san-serif;
    font-weight: 100;
}

#header h1 a::before {
    content: "방랑자의 도서관";
    position: relative;
    bottom: -0.25em;
}

#header h2 span::before {
    content: "동산은 뱀의 곳이고, 우리는 뱀의 손이다.";
}

#side-bar a,
#interwiki a { 
    color: #059400;
}

.ko-footer hr {
  background-color: #059400;
  margin: 1em 0;
}
[[/code]]
