[[module themepreviewer noUi="true"theme_url=" "]]
[[include :scp-int:component:interwiki-style priority=1|css=body{font-family:verdana,arial,helvetica,sans-serif;font-size:.82em}a:hover{background:%23eee}img{display:none}%23interwiki p{line-height:unset;margin-bottom:1em}%23interwiki .heading,%23interwiki .menu-item,%23interwiki .side-block{background:none;border:none;border-radius:unset;box-shadow:none;color:%23000;font-size:1em;font-weight:400;margin:unset;padding:unset}%23interwiki .menu-item a{color:%2300a;font-weight:400;text-decoration:underline]]
[[module css]]
/{$side}
.close-menu{
	display:none;
	height:100%;
	left:0;
	position:fixed;
	top:0;
	width:100%;
	z-index:-1
}
.close-menu:hover{
	background:none
}
.mobile-top-bar{
	display:none
}
.open-menu a{
	left:0;
	position:fixed;
	top:0
}
/**/

/{$misc}
#page-content{
	min-height:100vh
}
#search-top-box-input{
	display:none
}
#side-bar{
	background:#fff;
	position:absolute
}
.blockquote{
	background:#f4f4f4;
	border:1px dashed#999;
	margin:1em 40px;
	padding-inline:1em
}
.open-menu a{
	background:#fff;
	font-size:2em;
	left:1rem;
	top:1rem
}
.page-tags a[href*='_']{
	display:none
}
.scp-image-block.block-center{
	margin-inline:auto
}
.scp-image-block.block-left{
	float:left
}
.scp-image-block.block-right{
	float:right
}
/**/

/{$comp}
#u-credit-view{
	transition:none
}
.creditRate{
	margin:unset
}
.creditButton p a{
	border:none;
	margin-left:-4.5px;
	line-height:normal
}
.modalbox{
	background:#fff;
	border:none;
	border-radius:unset;
	box-shadow:none
}
.page-rate-widget-box{
	display:inline-block
}
.rate-box-with-credit-button{
	background:#666;
	border:none;
	border-radius:unset;
	box-shadow:none
}
.rate-box-with-credit-button .cancel{
	-moz-border-radius:unset
}
/**/

/{$rate}
div.page-rate-widget-box a{
	text-decoration:underline;
	color:#00a;
	background:unset;
	padding:unset;
}
div.page-rate-widget-box .cancel,
div.page-rate-widget-box .cancel a{
	-moz-border-radius:unset
}
div.page-rate-widget-box .rate-points{
	background:unset;
	color:inherit;
	font-weight:unset;
	padding:unset;
	-moz-border-radius:unset
}
.creditButton p{
	font-size:1em
}
.creditButton p a{
	color:#00a;
	font-size:1em;
	margin:unset;
	text-decoration:underline;
	width:unset
}
.rate-box-with-credit-button{
	background:none;
	-moz-border-radius:0 4px 4px 0
}
/**/

/{$trans}
@media (max-width:767px){
/**//*{$trans}/
@media (max-width:{$width}){
/**/

	/{$side}
	#main-content{
		margin-left:unset
	}
	#side-bar{
		left:-16em;
		position:fixed;
		top:0;
		z-index:99
	}
	#side-bar:target{
		left:0;
		overflow:auto;
		height:calc(100% - 2em)
	}
	#side-bar:target .close-menu,.mobile-top-bar{
		display:block
	}
	.top-bar{
		display:none
	}
	/**/
}
[[/module]][[iftags +theme]][[>]]
