[[code type="css"]]
@import url("http://scp-jp-storage.wikidot.com/local--files/file:3396310-45-05ku/national-park.css");

/*
    Wilson's Wildlife Theme
    [2018 Wikidot Theme]
    Created by stormbreath and DarkStuff.
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan, and MC&D Theme created by Randomini.
    The orginial WWS logo was created by DarkStuff.
    The new WWS logo was created by Jade Skylar.
    The National Park Font is licensed under the SIL Open Font License and can be found here: https://nationalparktypeface.com/

    Edited for SCP-JP by ukwhatn
*/

/* RECOLOR THE TITLE */
h1, #page-title {
    color: #6C7F41;}

#page-title {
    font-weight: lighter;
    border-color: #6C7F41;
}

/*RECOLOR THE LINKS */
a {
    color: #148c14;
}

a.newpage {
    color: #c8e293;
}

 a:visited {
    color: #036403; 
}

/* Recolor Side-Bar Links */
#side-bar a {
    color: #665731
}

#side-bar a:visited {
    color: #665731
}

/* HEADER */
div#container-wrap {
    background: url(none);
}

div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background: linear-gradient(to top,
     rgb(81, 181, 75) 0%,
     rgb(170, 220, 131) 70%,
     rgb(209, 237, 154) 100%);
} /* Light green gradient */

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: linear-gradient(to top,
      rgb(51, 43, 24),      
      rgb(102, 87, 49));
    box-shadow: 0 5px 10px rgba(0,0,0,.3);
} /* Dark brown gradient for top-bar */

#header {
    background: url("http://scp-jp-storage.wikidot.com/local--files/file:3396310-45-9rdj/WWS_Logo_2.2_abrv.png") 15px 40px no-repeat;
    background-size: 6rem;
}

@media (max-width: 580px) and (min-width: 480px) {
    #header {
        background-position: 0.5em 4.5em ;
        background-size: 66px 66px ;
    }
}

@media (max-width: 479px) {
    #header {
        background-position: 0 5.5em ;
        background-size: 55px 55px ;
    }
}

/* HIDE AND CHANGE HEADER TEXT */
#header h1 a {
    color: transparent;
    text-shadow: none;
    font-family: 'national_parkregular', sans-serif;
}

#header h1 a::before {
    content: "Wilson's Wildlife Solutions";
    color: #ffffff;
    text-shadow: 3px 3px 5px #000000;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
    font-family: 'national_parkregular', sans-serif;
}
#header h2 span::before {
    content: "Where All Critters Are Welcome";
    color: #ffffff;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* RECOLOR THE SEARCH BAR */
#search-top-box-input {
    border: solid 1px #665731;
    color: #665731 !important;
    background-color: #a3c162;
    font-family: 'national_parkregular', sans-serif;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #d1ed9a;
}

#search-top-box-form input[type=submit] {
    border: solid 1px #665731;
    color: #ffffff;
    background: #665731;
    font-family: 'national_parkregular', sans-serif;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #665731;
    background: #c8e293;
    color: #665731;
}

/* LOGIN STUFF */
#login-status {
    color: #665731;
}
#login-status a {
    color: #897543;
}
#login-status ul a {
    color: #665731;
}
#account-topbutton {
    background: #ccc;
    color: #665731;
}

/* IMAGE BLOCK RECOLORING */
.scp-image-block {
    border: solid 1px #665731;
}
.scp-image-block .scp-image-caption {
    background-color: #d1ed9a;
    border-top: solid 1px #665731;
}

/* MODULE RATE RECOLOR */
div.page-rate-widget-box .rate-points {
    background-color: #a3c162;
    border: solid 1px #665731;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #d1ed9a;
    border-top: solid 1px #665731;
    border-bottom: solid 1px #665731;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #a3c162;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #a3c162;
    color: #665731;
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: #a3c162;
    border: solid 1px #665731;
}
.page-rate-widget-box .cancel a {
    color: #f1fff0;
}
.page-rate-widget-box .cancel a:hover {
    background: #c8e293;
    color: #f1fff0;
}

/* INFO MODULE COMPATIBILITY */
#page-content .rate-box-with-credit-button {
    background-color: #a3c162 ; 
    border: 1px solid #665731 ;
    border-radius: 5px ;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
}
#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: #f1fff0;
}
.rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
}
.rate-box-with-credit-button div.page-rate-widget-box .rate-points {
    border-left: 0;
}

#page-content .rate-box-with-credit-button .fa-info {
     border-color: #f1fff0 ;
     color: #f1fff0 ;
}
#page-content .rate-box-with-credit-button .fa-info:hover {
     color: #665731;
}


.close-credits,
.credit-back {
    filter: hue-rotate(140deg);
}

#page-content .modalbox {
    box-shadow: 0 2px 6px rgba(0,30,0,.5);
}

/* BEARITAGE RATING MODULE */
.heritage-rating-module {
    background-color: #a3c162;
    border: solid 2px #665731;
    box-shadow: 0px 0px 0px 2px rgb(204,192,102);
}

.heritage-rating-module div.page-rate-widget-box .rate-points {
    border: solid 1px #a3c162;
}
.heritage-rating-module .page-rate-widget-box .rateup,
.heritage-rating-module .page-rate-widget-box .ratedown {
    border-top: solid 1px #a3c162;
    border-bottom: solid 1px #a3c162;
}

.heritage-rating-module .page-rate-widget-box .cancel {
    border: solid 1px #a3c162;
}


/* AYER'S INFO MODULE */
:root {
--barColour: #a3c162;
}

/* TAB RECOLORING */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color:#d1ed9a; /* color between tab list and content */
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color:#a3c162! important; /* tab background */
    border-color:#665731;
    color:#665731;
    transition: 0.125s;
}

.yui-navset .yui-nav a em {
     background-color: #fdfdfd;
     box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
     border-color: #b7a9a9;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-image: none;
    background-color: #a3c162; /* selected tab background */
    color:white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: transparent;
    box-shadow: 0px 1px 2px rgba(90,134,50,0.5);
    background-color: #a3c162;
}

.yui-navset .yui-content {
    background-color: #F7F7F7; /* content background color */
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: #b7a9a9; /* content border */
    border-top-color:#b7a9a9; /* different border color */
}

.yui-navset-left .yui-content {
    border-left-color: #b7a9a9; /* different border color */
}

/*SIDE BAR RECOLOR AND STUFF */
#side-bar {
   font-family: 'national_parkregular', sans-serif;
}

#side-bar .side-block {
    border: 1px solid #665731;
    box-shadow: 0 2px 6px rgba(0,30,0,.5);
}

#side-bar .heading {
    color: #665731;
    border-bottom: solid 1px #665731;
    font-size: 10pt;
}

#side-bar .side-block.media {
     background-color: #d2e2c5;
}

#side-bar .side-block.resources {
     background-color: #fff6dd;
}

.side-block .menu-item > .image {
    filter: hue-rotate(70deg);
}

/* TOP BAR RECOLORING*/
#top-bar {
   font-family: 'national_parkregular', sans-serif;
}

#top-bar ul li ul {
    border: solid 1px #3d2b1f;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid #665731;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #82a016;
    color: #ffffff;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #8db600;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #c8e293; /* top bar hover background color */
    color: #665731;
}

/* FOOTER STUFF */
#footer {
    background: rgb(80, 68, 37);
    color: #d1ed9a;
}

/* RECOLOR THE TABLES */
#page-content .wiki-content-table tr th {background-color: #a3c162; color: white; border: 1px solid #665731;}

/* RECOLOR THE QUOTEBLOCK */
blockquote,
div.blockquote,
#toc {
    border-color: #dcf2b0;
    background-color: #dcf2b0; 
    color:#665731;
}

/* RECOLOR THE LINE BREAKS */
hr {
    background-color: #665731;
}

/* SCROLLBAR */
::-webkit-scrollbar-track {
    background: #DAEAB9;
}

::-webkit-scrollbar-thumb {
    background: #755400;
}


/* MOBILE REFORMATTING */
@media (max-width: 767px) {
    #header h1 a::before {
          font-size: 60%;
     } 

    #side-bar {
          background-color: #d1ed9a
     }

    #top-bar .open-menu a {
         border: 0.2em solid #6C7F41;
         background-color: #83994E;
         color: #665731;
    }
}

/* TRANSLATION BLOCK */
iframe.scpnet-interwiki-frame {
    filter: hue-rotate(70deg);
    -webkit-filter: hue-rotate(70deg);
}

/* WILSON'S DIVS */
div.wilson-border {border: 1px solid #665731;}

div.wilson-header {color:white; background: #a3c162; padding: 15px; font-size: 150%; font-weight: bold; text-align: center;}

div.wilson-subtitle {color:#665731; background: #c8e293;padding: 5px; text-align: right; font-weight: bold;}

div.wilson-body {color:#665731; background: #d1ed9a; padding: 15px;}

/* for SCP-JP */

#page-content .page-rate-widget-box .cancel {
    padding: 0;
    overflow: hidden;
}

#page-content .creditRate {
    margin-right: unset;
}

#page-content .creditRate.creditModule .rate-box-with-credit-button {
    background-color: unset;
    border: unset;
    border-radius: unset;
    box-shadow: unset;
}

#page-content .creditRate.creditModule .rate-box-with-credit-button div.page-rate-widget-box .rate-points {
    border-left: solid 1px #665731;
}

.creditRate.creditModule .rate-box-with-credit-button .creditButton {
    background-color: #a3c162!important;
}

.creditRate.creditModule .rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0 5px 5px 0;
}

[[/code]]
[[/iftags]]
