[[code type="css"]]
@import url('https://scp-wiki.wdfiles.com/local--code/theme%3Awilsons/1');

@font-face {
    font-family: '국립공원 반달이';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2201-2@1.0/KNPSBandali-Regular00.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}
@font-face {
    font-family: '국립공원 꼬미';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2201-2@1.0/KNPSKkomi-Regular00.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}
@font-face {
    font-family: 'Gmarket Sans L';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2001@1.1/GmarketSansLight.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

/*
    Wilson's Wildlife Theme
    [2018 Wikidot Theme]
    Created by stormbreath and DarkStuff.
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan, and MC&D Theme created by Randomini.
    The orginial WWS logo was created by DarkStuff.
    The new WWS logo was created by Jade Skylar.
    The National Park Font is licensed under the SIL Open Font License and can be found here: https://nationalparktypeface.com/
*/

@supports(--css:variables) {

:root:lang(ko) {
    --national-park-thin: 'national_parkthin', 'Dosis', 'Gmarket Sans L', sans-serif;
    --national-park: 'national_parkregular', 'Comfortaa', '국립공원 꼬미', sans-serif;
    --national-park-heavy: 'national_parkheavy', 'Nunito', '국립공원 반달이', sans-serif;
}

}

/* HIDE AND CHANGE HEADER TEXT */
#header h1 a {
    font-size: 60%;
    font-family: var(--national-park);
}

#header h1 a::before {
    content: "모든 조물딱지들을 환영하는";
}

#header h2 span {
    font-size:  300%;
    font-family: var(--national-park);
}
#header h2 span::before {
    content: "윌슨 야생생물대책";
}
[[/code]]
