[[code type="css"]]
@import url('https://scp-wiki.wdfiles.com/local--code/theme%3Awondertainment/1');

@import url('https://fonts.googleapis.com/css?family=Stylish');
@font-face { font-family: 'NapjakBlock'; src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_two@1.0/NapjakBlock.woff') format('woff'); font-weight: normal; font-style: normal; }

/*
    Wondertainment Theme
    [2018 Wikidot Theme]
    Created for the SCP Foundation by DarkStuff and taylor_itkin; revised by stormbreath.
    credit for the header image: https://pixabay.com/en/star-pattern-wallpaper-purple-763785/
    logo made by Zhange, edited by taylor_itkin
*/

/* COMMON */

h1, #page-title {
    font-family: 'Berkshire Swash',  'NapjakBlock',  cursive;
}

/* BACKGROUND COLOR FONT COLOR */
body {
    font-family: 'Sansita', 'Stylish', sans-serif;
}

/* HEADER */
#header h1 a {
    font-family: 'Berkshire Swash',  'NapjakBlock',  cursive;
}

#header h1 a::before {
    content: "원더테인먼트 박사!";
}

#header h2 span {
    font-family: 'Sansita', 'Stylish', sans-serif;
}

#header h2 span::before {
    content: '명심해 너의 머릿속이 원더풀한 곳이라는 걸';
}

@media (min-width: 768px) {
  #header h2 {
     padding-top: 10px;
  }
}
[[/code]]
