[[module CSS show="true"]]
/*
    Words of Wisdom theme
    [2021 Wikidot Theme]
    Created by Cole 13
    CC BY-SA 3.0
*/

@import url('https://fonts.googleapis.com/css2?family=Mate+SC&display=swap');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnJamoBatang.css');

/* -=- HEADER -=- */

#header h1 a::before {
    content: "권사단";
    font-family: 'Mate SC', 'UnJamoBatang', serif;
    padding-top: 30px;
}

#header h2::before {
    display: inline-block;
    width: 100%;
    text-align: center;
    content: "The Wandsmen";
    color: #ffffff;
    font-family: 'Mate SC', 'UnJamoBatang', serif;
    font-style: normal;
    font-size: 1.5rem;
    text-shadow: 0rem 0rem 0.3125rem #000000;
    position: absolute;
    left: 46.5%;
    transform: translateX(-50%);
    top: 50px;
}

/* -=- SIDEBAR -=- */

#side-bar .heading {
     font-family: 'Mate SC', 'UnJamoBatang', serif;
}

textarea {
   font-family: var(--mono-font);
}
input#edit-page-title.text {
   font-family: var(--title-font);
}
[[/module]]
[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Anton&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display+SC&display=swap');
@font-face {
    font-family: '조선일보';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_20-04@1.0/ChosunLo.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}
@font-face {
    font-family: '꺾깎체';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2104@1.0/SLEIGothicTTF.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}
@import url('https://scpko.wdfiles.com/local--files/korean-font/조선100년체.css');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnShinmun.css');

div.newspaper-border {
    border: 2px solid black;
}

div.newspaper-main {
    font-family: 'Special Elite', '조선100년체', sans-serif;
}

div.newspaper-title {
    font-family: 'Playfair Display SC', '조선일보', serif;
    font-size: 4em;
    text-align: center;
    margin: 0.25em;
}

div.newspaper-banner {
    font-family: 'UnShinmun', serif;
    font-size: 110%;
    border-top-style: solid;
    border-bottom-style: solid;
}

div.newspaper-headline {
    font-family: 'Anton', '꺾깎체', serif;
    margin: 2px 0 5px;
    text-align: center;
}

div.newspaper-reporter {
    font-family: serif;
    font-size: 120%;
    font-style: italic;
    text-align: center;
}

div.newspaper-body {
    font-family: 'UnShinmun', serif;
    font-size: 1.2em;
    text-align: justify;
    text-justify: inter-word;
    line-height: 1.4em;
    padding: 1em;
}

div.newspaper-body div.scp-image-block {
    margin: 0.5em 0 0 1em;
}
[[/module]]
[[/div]]
