[[code type="css"]]
@import url('https://fonts.googleapis.com/css2?family=Indie+Flower&display=swap');
/*
    "Writing anomalies" theme
    [2021 Wikidot Theme]
    Created by Cole 13
    CC BY-SA 3.0
*/


body {
    background: #FAFAFA;
    color: #000000;
}
a,a:visited {
    color: #B40431;
}
h1 {
    color: #000000;
     font-family: 'Indie Flower', cursive;
}
blockquote {
    border: 2px dashed; #000000;
    background: #F2F2F2;
}
 div#container-wrap { 
background: url(http://scp-wiki.wdfiles.com/local--files/theme%3Awriting-anomalies/HEADER%20H) top left repeat-x; background-size: auto 160px; 
}
#header {
     background: url(" http://scp-wiki.wdfiles.com/local--files/theme%3Awriting-anomalies/THX%40 ") 0px 40px no-repeat;
     background-size: auto 100px;
}

div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "SCP Foundation";
    color: #000000;
    font-family: 'Indie Flower', cursive;
    text-shadow: 1px 1px 1px black;

}
div#header h2 span {
    font-size:0px;
    padding: 4px;


}
div#header h2:after {
    content: "Secure, Contain, Protect";
    font-weight: bold;
    color: #000000;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
   font-family: 'Indie Flower', cursive;
}
@media (max-width: 479px)
#header {
    background-position: 0 5em!important;
    background-size: 65px 65px!important;
     background: url(" http://scp-wiki.wdfiles.com/local--files/theme%3Awriting-anomalies/THX%40") 0px 40px no-repeat;
     background-size: auto 100px;
}
#account-topbutton{
    border: solid 1px black;
}
#login-status,
#login-status a {
   color: black;
    font-weight: bold;
}
#search-top-box-input {
    border: solid 1px white;
    background-color: #000000;
    color: white;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px white;
    background-color: #000000;
}
#search-top-box-form input[type=submit] {
    border: solid 1px white;
    background-color: #FA58F4;
    background:#000000;

}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px white;
    color: white;
    background-color:#000000;
    background: #000000;
}
div#header h1 a span {
    font-size: 0px;
}
#top-bar a {
    color: white;
    font-weight: bold;   
}
div#side-bar div.side-block {
    background: #FAFAFA;
    border-radius: 15px;
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: #000000;
    border-color: #000000;
    background: #FAFAFA;
}
div#side-bar .heading {
    color: black;
    border: solid 1px black;
}
#side-bar .side-block.media {
  background:#FAFAFA;
}
#side-bar .side-block.resources {
  background-color: #FAFAFA;
}
div.yui-navset div.yui-content {
    background: #F2F2F2;
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #F2F2F2;
    color: #000000;
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: #F2F2F2;
    color: #000000;
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: #E6E6E6;
    color: #000000;
    border: solid #000000;
}
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color:#F2F2F2 !important;
    border: solid #F2F2F2 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid transparent!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #000000;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #F2F2F2!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #000000!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: #000000!important;
}
div.open-menu a {
    color: #000000!important;
    border-color: #000000!important;
 /
}
.footnotes-footer {
border-top: 2px solid #000000;
height: 1%;
{
