/* FONTS AND COLORS */

body {
    font-family: 'Noto Serif JP', 'Noto Serif KR', 'UnBatang', serif;
} 
h1,h2,h3,h4,h5,h6 {
    font-family: 'Noto Serif JP', 'Noto Serif KR', 'UnBatang', serif;
}
div#header h1 a:before {
    content: "어둠의 초밥";
    font-family: 'Noto Serif JP', 'Noto Serif KR', 'UnBatang', serif;
}
div#header h2:after {
    content: "闇寿司 최강 무적 초밥";
}
