[[iftags +版式]]

[[module CSS]]
@import url("https://scp-wiki-cn.wikidot.com/theme:parallel/code/1");

div.hover {
    opacity: 0;
    transition: ease 0.25s;
}

div.hover:hover {
    opacity: 1;
}

div.ctn > img {
    opacity: 1;
    transition: ease 0.25s;
}

div.ctn:hover > img {
    opacity: 0;
}

/* Fuck ACS */

div#main-content > div#page-content div.meta-title {
    display: block;
}
[[/module]]

[[div class="meta-title"]]
