++ ACS

使用{{@@[[div class="Parallel-ACS"]]@@}}把ACS的{{@@[[include]]@@}}语法套住以实现。

[[div class="Parallel-ACS"]]
[[include :scp-wiki-cn:component:anomaly-class-bar-source
|lang=cn
|item-number=CN-XXX
|container-class=keter
|disruption-class=ekhi
|risk-class=notice
|clearance=4
]]
[[/div]]

[[/iftags]]

[[module CSS]]
/* https://pixabay.com/zh/photos/buddhism-buddhist-cave-grotto-1317432/ */

:root:lang(cn) {
    --title: url("https://ah-sandbox.wikidot.com/local--files/hormress-s-logos/ARED_text_i.svg");
    --subtitle: "凡人终不朽";
}

:root:lang(en) {
    --subtitle: "Mortales Desinunt Immortales";
}

:root {
    --basalt-primary-color: 254, 254, 254;
    --basalt-secondary-color: 237, 238, 240;
    --basalt-tertiary-color: 208, 209, 212;
    --basalt-main-text-color: 20, 20, 20;
    --basalt-overtone: 50, 50, 50;
    --basalt-undertone: 103, 97, 97;

    --basalt-bright-element-color: 134, 127, 127;
    --basalt-dark-element-color: 87, 82, 82;
    --basalt-darker-element-color: 14, 0, 0;

    --bottom-area-background-color: var(--basalt-secondary-color);

    --hr-color: var(--basalt-tertiary-color);

    --logo: url("https://ah-sandbox.wikidot.com/local--files/hormress-s-logos/ARED_2_i.svg");
    --yore-bg: url("https://ah-sandbox.wikidot.com/local--files/theme:yore/yore_bg.jpg");

    --header-gradient: none;
    --header-stripes: none;

    --header-border-width: 0rem;

    --main-content-top-margin: 1rem;
    --main-content-side-margin: 2rem;

    --UI-title-background-color: transparent;

    --content-columns: [full-start] var(--main-content-side-margin) [wide-start] 1fr [content-start] min(calc(100% - 2 * var(--main-content-side-margin)), calc(var(--main-content-width) - 2 * var(--main-content-side-margin))) [content-end] 1fr [wide-end] var(--main-content-side-margin) [full-end];
}

div#header::before,
div#header::after,
div#main-content,
div#page-title::before,
div.meta-title::before,
div.blockquote.info,
div.chapter-title,
div.chapter-title h1,
div.section-title::after,
div.page-cut::before,
div.page-cut::after,
div.blockquote.title::before,
div.blockquote.title::after,
div.Parallel-ACS div.anom-bar-container div.top-center-box > div {
    clip-path: var(--main-clip-path);
    -webkit-clip-path: var(--main-clip-path);
    --main-clip-path: polygon(0 var(--cut-lt), var(--cut-lt) 0, calc(100% - var(--cut-rt)) 0, 100% var(--cut-rt), 100% calc(100% - var(--cut-rb)), calc(100% - var(--cut-rb)) 100%, var(--cut-lb) 100%, 0 calc(100% - var(--cut-lb)));
    --cut-lt: 0rem;
    --cut-rt: 0rem;
    --cut-lb: 0rem;
    --cut-rb: 0rem;
}

body {
    background-color: rgb(var(--basalt-darker-element-color));
    background-image: var(--yore-bg);
    background-attachment: fixed;
    background-position: center;
    background-size: cover;
}

div#header {
    position: static;
    align-self: flex-end;
    width: min(60rem, 100vw);
    padding-left: var(--header-final-height);
    margin: 3rem 0 0 0;
    background-color: transparent;
    grid-template-areas: 'h-link top-bar search user';
    grid-template-columns: max-content 1fr var(--search-button-width) var(--user-button-width);
    box-shadow: none;
}

div#header::before,
div#header::after {
    --cut-lb: var(--header-final-height);
}

div#header::before {
    content: "";
    z-index: -1;
    position: absolute;
    left: calc(var(--mov) * -1.414);
    top: 0;
    width: calc(100% + var(--mov) * 1.414);
    height: calc(100% + var(--mov));
    background-color: rgba(var(--basalt-primary-color), 0.8);
    clip-path: var(--fit-clip-path);
    -webkit-clip-path: var(--fit-clip-path);
    --fit-clip-path: polygon(0 0, calc(var(--mov) * 1.414) 0, calc(var(--cut-lb) + var(--mov) * 1.414) var(--cut-lb), 100% var(--cut-lb), 100% 100%, calc(var(--cut-lb) + var(--mov)) 100%);
    --mov: 0.5rem;
}

div#header::after {
    content: "";
    z-index: -1;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(var(--basalt-darker-element-color), 0.8);
}

div#header h1 a span::before {
    min-width: 7.5rem;
    min-height: 2rem;
}

div#top-bar div[class*=top-bar] ul li ul {
    right: 0;
    left: unset;
}

div#top-bar div[class*=top-bar] > ul > li > ul::before {
    right: 0;
    left: unset;
    pointer-events: none;
}

div#content-wrap {
    display: grid;
    margin: 3rem 0;
    grid-template-areas: "side-bar main-content .";
    grid-template-columns: var(--side-bar-width) 1fr var(--side-bar-width);
}

div#main-content {
    grid-area: main-content;
    display: grid;
    grid-template-columns: var(--content-columns);
    margin: 0;
    width: auto;
    max-width: 100%;
    padding: var(--main-content-top-margin) 0 var(--main-content-bottom-margin);
    background-color: rgba(var(--basalt-primary-color), 0.8);
    --cut-rt: 2rem;
    --cut-rb: 2rem;
}

:where(div#main-content) > * {
    max-width: min(var(--main-content-width), 100%);
    grid-column: content;
}

div#page-content {
    min-height: 125vh;
    grid-column: full;
    display: grid;
    max-width: unset;
    grid-template-columns: var(--content-columns);
}

:where(:is(div#page-content, div.list-pages-item)) > * {
    grid-column: content;
}

:where(:is(div#page-content, div.list-pages-item)) > p {
    margin-block: .5em;
}

div#side-bar::before,
div#side-bar::after {
    display: none;
}

div#side-bar {
    grid-area: side-bar;
    position: sticky;
    left: 0;
    top: 0;
    margin-top: 0;
    height: 100vh;
    width: auto;
    max-width: 100%;
    background-color: rgba(var(--basalt-primary-color), 0.8);
    border-right: solid var(--base-border-width) rgb(var(--basalt-dark-element-color));
    box-sizing: border-box;
}

div#side-bar div#interwiki {
    background-color: transparent;
}

div.meta-title {
    z-index: 3;
    width: fit-content;
    width: -moz-fit-content;
    width: -webkit-fit-content;
    position: relative;
    margin-left: -2.5rem;
    padding-left: 2.5rem;
    padding-right: 2.5rem;
    margin-right: auto;
    margin-bottom: 0.5rem;
    max-width: calc(100% + 2.5rem - 2 * var(--main-content-side-margin));
    border-bottom: none;
    box-sizing: border-box;
    filter: drop-shadow(0 0 0.25rem rgba(var(--basalt-overtone), 0.2));
    -webkit-filter: drop-shadow(0 0 0.25rem rgba(var(--basalt-overtone), 0.2));
}

div#page-title,
:is(div#page-content, div.list-pages-item) > div.meta-title {
    grid-column: full;
    position: relative;
    width: auto;
    margin-left: 0rem;
    padding-left: max(calc((100% - var(--main-content-width)) * 0.5 + var(--main-content-side-margin)), var(--main-content-side-margin));
    padding-right: 2.5rem;
    margin-right: auto;
    margin-bottom: 0.5rem;
    max-width: min(calc(50% + var(--main-content-width) * 0.5 - var(--main-content-side-margin)), calc(100% - var(--main-content-side-margin)));
    border-bottom: none;
    justify-self: start;
    box-sizing: border-box;
    filter: none;
    -webkit-filter: none;
}

div#page-title::before,
div.meta-title::before {
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: calc(1.5rem - var(--base-border-width));
    left: 0;
    bottom: 0;
    background-color: rgb(var(--basalt-secondary-color));
    border-bottom: solid var(--base-border-width) rgb(var(--basalt-dark-element-color));
    --cut-rb: 1.5rem;
    --cut-lt: 0rem;
    --cut-lb: 0rem;
    z-index: -1;
}

:not(:is(div#page-content, div.list-pages-item)) > div.meta-title::before {
    --cut-lt: 0.75rem;
    --cut-lb: 0.75rem;
}

/* 自定义 DIVs */

div.hor {
    height: 5rem;
}

:where(div#page-content) > :is(blockquote, div.blockquote, div.chapter-title) {
    margin-block: 0.5rem;
    margin-inline: unset;
}

blockquote,
div.blockquote {
    justify-self: center;
}

div.blockquote.dark {
    background-color: rgb(var(--basalt-darker-element-color));
    background-image: linear-gradient(rgba(var(--basalt-darker-element-color), 0.8) 0% 100%), var(--yore-bg);
    color: rgb(var(--basalt-light-text-color));
    border-color: transparent;
    background-attachment: fixed;
    background-position: center;
    background-size: cover;
}

div.chapter-title {
    min-height: 8rem;
    position: relative;
    margin: 1rem 0;
    background-image: var(--yore-bg);
    color: rgb(var(--basalt-light-text-color));
    border-color: transparent;
    background-attachment: var(--bg-mode);
    background-position: center;
    background-size: cover;
    --cut-lt: 2rem;
    --cut-rb: 2rem;
    --bg-mode: fixed;
}

div.chapter-title h1 {
    position: absolute;
    left: 0;
    bottom: 0;
    margin: 0;
    padding: 0 max(3.5rem, 8.75vw) 0 max(1.5rem, 3.75vw);
    height: max(2.5rem, 6.25vw);
    font-size: max(1.6rem, 4vw);
    line-height: max(2.5rem, 6.25vw);
    background-color: rgba(var(--basalt-darker-element-color), 0.8);
    --cut-rb: max(2.5rem, 6.25vw);
}

@media (min-width: 1024px) {
div.chapter-title h1 {
    padding: 0 5.6rem 0 2.4rem;
    height: 4rem;
    font-size: 2.56rem;
    line-height: 4rem;
    --cut-rb: 4rem;
}
}

div.section-title {
    min-height: 1.5rem;
    font-size: 1.25rem;
    font-weight: 700;
    line-height: 1.5rem;
    margin: 0.5rem 0 0 -0.2rem;
    padding-left: 0.2rem;
    box-shadow: -0.25rem 0 0 rgb(var(--section-color));
    --section-color: var(--basalt-dark-element-color);
}

div.section-title p {
    display: inline;
}

div.section-title::after {
    content: "";
    display: inline-block;
    vertical-align: top;
    height: 1.5rem;
    width: 5rem;
    background-color: rgb(var(--section-color));
    --cut-rb: 1.5rem;
}

div.section-title.dark {
    --section-color: var(--basalt-overtone);
}

div.section-title.darker {
    --section-color: var(--basalt-darker-element-color);
}

div.blockquote.info {
    border: none;
    border-left: solid var(--base-border-width) rgb(var(--basalt-overtone));
    --cut-rt: 1.5rem;
    --cut-rb: 1.5rem;
}

div#main-content div.blockquote.title {
    border: none;
    border-left: solid var(--base-border-width) rgb(var(--basalt-UI-dark-palette));
    background-color: transparent;
    box-sizing: border-box;
}

div#main-content div.blockquote.title::before {
    inset-inline-start: calc(var(--base-border-width) * -1);
    padding-right: 2rem;
    --cut-rb: 1.5rem;
}

div#main-content div.blockquote.title::after {
    content: "";
    display: block;
    top: 0;
    left: 0;
    position: absolute;
    width: 100%;
    height: 100%;
    background-color: rgb(var(--basalt-secondary-color));
    --cut-rt: 1.5rem;
    --cut-rb: 1.5rem;
    z-index: -1;
}

div#main-content div.blockquote.title.small {
    border-left: none;
    padding-top: 1.5rem;
}

div#main-content div.blockquote.title.small::before {
    inset-inline-start: 0rem;
    inset-block-end: calc(100% - 0.8rem);
}

div.page-cut {
    display: grid;
    grid-column: full;
    grid-template-columns: var(--content-columns);
    background-color: rgb(var(--basalt-darker-element-color));
    background-image: var(--yore-bg);
    border-color: transparent;
    background-attachment: fixed;
    background-position: center;
    background-size: cover;
}

div.page-cut::before {
    content: "";
    display: block;
    height: 2rem;
    grid-column: full;
    background-color: rgba(var(--basalt-primary-color), 0.8);
    --cut-rb: 2rem;
}

div.page-cut::after {
    content: "";
    display: block;
    height: 2rem;
    grid-column: full;
    background-color: rgba(var(--basalt-primary-color), 0.8);
    --cut-rt: 2rem;
}

div.part-nav table tr {
    display: grid;
    grid-template-areas: "l c r";
    grid-template-columns: 1fr 1fr 1fr;
    --section-color: var(--basalt-dark-element-color);
}

div.part-nav table tr > td {
    display: grid;
    grid-template-areas: "a";
    padding: 0rem;
    position: relative;
    min-height: 3rem;
    line-height: 1rem;
    text-align: center;
    border: none;
}

div.part-nav table tr > td a,
div.part-nav table tr > td a:visited {
    color: inherit;
    margin: 1rem 2rem;
    grid-area: a;
    align-self: center;
}

div.part-nav table tr > td::before {
    content: "";
    display: block;
    grid-area: a;
    z-index: -1;
    clip-path: var(--clip);
    -webkit-clip-path: var(--clip);
}

div.part-nav table tr > td::after {
    content: "";
    display: block;
    grid-area: a;
    background-color: rgb(var(--basalt-secondary-color));
    clip-path: var(--clip);
    -webkit-clip-path: var(--clip);
    z-index: -1;
}

div.part-nav table tr > td:nth-child(1) {
    grid-area: l;
}

div.part-nav table tr > td:nth-child(1) a,
div.part-nav table tr > td:nth-child(1) a:visited {
    margin-right: 1rem;
}

div.part-nav table tr > td:nth-child(1)::before {
    --clip: polygon(1.5rem 0%, 1.75rem 0%, 0.25rem 50%, 1.75rem 100%, 1.5rem 100%, 0% 50%);
    background-color: rgb(var(--section-color));
}

div.part-nav table tr > td:nth-child(1)::after {
    --clip: polygon(2rem 0%, 100% 0%, 100% 100%, 2rem 100%, 0.5rem 50%);
}

div.part-nav table tr > td:nth-child(2) {
    grid-area: c;
}

div.part-nav table tr > td:nth-child(2)::after {
    --clip: polygon(0% 50%, 1.25rem 0.25rem, calc(100% - 1.25rem) 0.25rem, 100% 50%, calc(100% - 1.25rem) calc(100% - 0.25rem), 1.25rem calc(100% - 0.25rem), 0% 50%, 0% 100%, 100% 100%, 100% 0%, 0% 0%);
}

div.part-nav table tr > td:nth-child(3) {
    grid-area: r;
}

div.part-nav table tr > td:nth-child(3) a,
div.part-nav table tr > td:nth-child(3) a:visited {
    margin-left: 1rem;
}

div.part-nav table tr > td:nth-child(3)::before {
    --clip: polygon(calc(100% - 1.5rem) 0%, calc(100% - 1.75rem) 0%, calc(100% - 0.25rem) 50%, calc(100% - 1.75rem) 100%, calc(100% - 1.5rem) 100%, 100% 50%);
    background-color: rgb(var(--section-color));
}

div.part-nav table tr > td:nth-child(3)::after {
    --clip: polygon(calc(100% - 2rem) 0%, 0% 0%, 0% 100%, calc(100% - 2rem) 100%, calc(100% - 0.5rem) 50%);
}

div.part-nav.first-part table tr {
    grid-template-areas: "c r";
    grid-template-columns: 1fr 1fr;
}

div.part-nav.first-part table tr > td:nth-child(1) {
    display: none;
}

div.part-nav.first-part table tr > td:nth-child(2)::after {
    --clip: polygon(0rem 50%, 0.25rem 50%, 0.25rem 0.25rem, calc(100% - 1.25rem) 0.25rem, 100% 50%, calc(100% - 1.25rem) calc(100% - 0.25rem), 0.25rem calc(100% - 0.25rem), 0.25rem 50%, 0% 50%, 0% 100%, 100% 100%, 100% 0%, 0% 0%);
}

div.part-nav.first-part table tr > td:nth-child(2) a,
div.part-nav.first-part table tr > td:nth-child(2) a:visited {
    margin-left: 1rem;
}

div.part-nav.last-part table tr {
    grid-template-areas: "l c";
    grid-template-columns: 1fr 1fr;
}

div.part-nav.last-part table tr > td:nth-child(3) {
    display: none;
}

div.part-nav.last-part table tr > td:nth-child(2)::after {
    --clip: polygon(0% 50%, 1.25rem 0.25rem, calc(100% - 0.25rem) 0.25rem, calc(100% - 0.25rem) 50%, calc(100% - 0.25rem) calc(100% - 0.25rem), 1.25rem calc(100% - 0.25rem), 0% 50%, 0% 100%, 100% 100%, 100% 0%, 0% 0%);
}

div.part-nav.last-part table tr > td:nth-child(2) a,
div.part-nav.last-part table tr > td:nth-child(2) a:visited {
    margin-right: 1rem;
}

@media (max-width: 480px) {
div.part-nav table tr > td a,
div.part-nav table tr > td a:visited {
    margin: 0.5rem 1.5rem;
}

div.part-nav table tr > td:nth-child(1) a,
div.part-nav table tr > td:nth-child(1) a:visited {
    margin-right: 0.5rem;
}

div.part-nav table tr > td:nth-child(3) a,
div.part-nav table tr > td:nth-child(3) a:visited {
    margin-left: 0.5rem;
}

div.part-nav.first-part table tr > td:nth-child(2) a,
div.part-nav.first-part table tr > td:nth-child(2) a:visited {
    margin-left: 0.5rem;
}

div.part-nav.last-part table tr > td:nth-child(2) a,
div.part-nav.last-part table tr > td:nth-child(2) a:visited {
    margin-right: 0.5rem;
}
}

div.Parallel-ACS {
    --swatch-menubg-black-color: var(--basalt-main-text-color);
    --section-color: var(--basalt-dark-element-color);

    --five-color: 53, 50, 50;
    --four-color: 68, 65, 65;
    --three-color: 93, 90, 90;
    --two-color: 110, 107, 107;
    --one-color: 134, 127, 127;
}

div.Parallel-ACS div.danger-diamond > div.quadrants > div {
    background-color: transparent;
}

/* div.Parallel-ACS div.anom-bar-container > div.anom-bar > div.top-box > div.top-left-box > span.item {
    position: relative;
    left: -0.2rem;
    padding-left: 0.2rem;
    box-shadow: -0.25rem 0 0 rgb(var(--section-color));
} */

div.Parallel-ACS div.anom-bar-container div.top-center-box > div {
    --cut-rt: 0.5rem;
    --cut-lb: 0.5rem;
}

@media (max-width: 480px) {
/* div.Parallel-ACS div.anom-bar-container > div.anom-bar > div.top-box > div.top-left-box {
    position: relative;
    left: -0.2rem;
    padding-left: 0.2rem;
    box-shadow: -0.25rem 0 0 rgb(var(--section-color));
}

div.Parallel-ACS div.anom-bar-container > div.anom-bar > div.top-box > div.top-left-box > span.item {
    position: static;
    padding-left: 0rem;
    box-shadow: none;
} */

div.Parallel-ACS div.anom-bar-container div.top-center-box > div {
    margin: 0.15rem 0rem 0rem 0rem;
}
}

div.Parallel-ACS div.anom-bar-container > div.anom-bar > div.top-box > div.top-left-box > span.number {
    white-space: nowrap;
}

div.Parallel-ACS :is(div.main-class, div.disrupt-class, div.risk-class) {
    background-image: linear-gradient(to left, rgba(var(--basalt-primary-color), 0.8) 0%, rgba(var(--basalt-primary-color), 0.3) 100%), var(--yore-bg);
    color: rgb(var(--basalt-light-text-color));
    background-attachment: fixed;
    background-position: center;
    background-size: cover;
}

/* ListPages */

div.list-pages-box {
    grid-column: full;
}

div.list-pages-item {
    display: grid;
    grid-template-columns: var(--content-columns);
}

/* 页面标签 */

div#main-content > div.page-tags {
    margin-top: 0.5rem;
}

div#main-content > div.page-tags > span a {
    border-radius: 0;
}

div#page-info-break {
    height: 0.5rem;
}

/* 数不清的适配 */

@media (max-width: 1440px) {
div#content-wrap {
    grid-template-areas: "side-bar main-content .";
    grid-template-columns: var(--side-bar-width) 1fr auto;
}

div#main-content {
    max-width: min(var(--main-content-width), 100%);
}
}

@media (max-width: 1280px) {
div#content-wrap {
    grid-template-areas: "pre main-content .";
    grid-template-columns: 1fr min(100%, calc(var(--main-content-width) + 2 * var(--main-content-side-margin))) 1fr;
}

div#content-wrap::before {
    content: "";
    display: block;
    grid-area: pre;
    background-image: linear-gradient(to right, rgba(var(--basalt-primary-color), 0.5), rgba(var(--basalt-primary-color), 0.8));
    border-right: solid var(--base-border-width) rgb(var(--basalt-dark-element-color));
}

div#side-bar {
    position: fixed;
    width: var(--side-bar-width);
    left: calc(var(--side-bar-width) * -1);
    border-right: none;
}

div#side-bar:hover,
div#side-bar:focus-within {
    left: 0 !important;
}

div#side-bar::before,
div#side-bar::after {
    content: "";
    display: block;
    height: var(--side-bar-button-width);
    transition: opacity var(--side-bar-transition);
}

div#side-bar::before {
    opacity: 0.25;
}

div#side-bar:hover::before,
div#side-bar:hover::after {
    z-index: -10000;
    height: var(--side-bar-button-width);
    opacity: 0;
}

div#main-content {
    max-width: 100%;
}
}

@media (max-width: 1024px) {
div#header {
    padding-left: 0;
    grid-template-areas:
    'h-link h-link h-link search user' 
    '. top-bar top-bar top-bar top-bar';
    grid-template-columns: var(--side-bar-button-width) max-content 1fr var(--search-button-width) var(--user-button-width);
}

div#top-bar div.mobile-top-bar > div.open-menu {
    display: none;
}

div#header::after,
div#header::before {
    --cut-lb: 2rem;
}

div#header::before {
    width: 100%;
    height: 100%;
    left: 0;
    top: var(--mov);
    --fit-clip-path: polygon(0 0, 0 calc(100% - var(--mov) - var(--cut-lb)), var(--cut-lb) calc(100% - var(--mov)), 100% calc(100% - var(--mov)), 100% 100%, calc(var(--cut-lb) - var(--mov) * 0.414) 100%, 0 calc(100% - var(--cut-lb) + var(--mov) * 0.414));
}

div#content-wrap::before {
    display: none;
}
}
[[/module]]
