content:
[[module css]]
@import url('https://scp-wiki-cn.wikidot.com/local--code/theme:zakuro-format-shirasutaro/1');
[[/module]]

[!--
[[code type="css"]]

@import url('https://fonts.googleapis.com/css?family=Noto+Serif+SC|Noto+Serif+SC&display=swap');

ol {
    list-style-type: katakana-iroha;
}

body {
    background-color: #e7ddce;
    font-size: 0.80em;
    color: #000;
    font-family: 'Noto Serif SC';
}

div #header h1 a {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    opacity: 0;
    font-size: 1px;
}

#header h2 span {
  font-size:0px;
}

div #header {
    background: url("http://scp-jp-sandbox3.wikidot.com/local--files/draft:5011139-8-eab3/zakurogo.png") 10px 40px no-repeat;
}

div#container-wrap {
    background: url(http://scp-jp-sandbox3.wikidot.com/local--files/draft:5011139-8-eab3/zakuroheader.jpg) top left repeat-x;
}

div #header h1 {
    position: relative;
    top: 35px;
    width: 210px;
    min-height: 100px;
    margin-left: 0;
}


#page-title {
    display: none;
}

h4 {
    margin: 0.5em 0 0.6em;
    padding: 0.3em 0 0.2em;
    color: #000;
    font-family: 'Noto Serif SC';
    font-size: 200%;
    font-weight: normal;
    text-align: center;

    -webkit-font-feature-settings: "trad";
       -moz-font-feature-settings: "trad";
            font-feature-settings: "trad";
    font-variant-east-asian: traditional;
    unicode-bidi: bidi-override;
}

h4 > span {
    display: block;
    margin: 0 -0.5em;
}
h4 > span span {
    display: inline-block;
    position: relative;
}
h4 > span > span:before, h4 > span > span:after {
    display: block;
    position: absolute;
}
h4 > span > span {
    padding: 0 1.5em;
}
h4 > span > span:before {
    top: 0;
    left: 0;
    content: "【";
}
h4 > span > span:after {
    right: 0;
    bottom: 0;
    content: "】";
}

h3 {
    margin: 0 0 0.5em;
    padding: 0 0 0.75em;

    color: #000;
    font-family: 'Noto Serif SC';
    font-size: 200%;
    font-weight: normal;
    text-align: center;

    -webkit-font-feature-settings: "trad";
       -moz-font-feature-settings: "trad";
            font-feature-settings: "trad";
    font-variant-east-asian: traditional;
    unicode-bidi: bidi-override;
}

h3 > span > span[style="font-size:60%;"] {
    padding: 0 0.5em;
    text-indent: 0.25em;
    letter-spacing: 0.25em;
}
h3 > span > span[style="font-size:60%;"]:before {
    top: 0;
    left: 0;
    content: "第";
}
h3 > span > span[style="font-size:60%;"]:after {
    right: 0;
    bottom: 0;
    content: "号";
}

blockquote {
    border: 1px solid #30291d;
    box-shadow: 0.25em 0.25em rgba(0,0,0,0.15);
}

.kakoi {
    margin:2em 0;
    position: relative;
    border: solid 1px #30291d;
    padding: 0.25em 1em;
    border-radius: 3px 0 3px 0;
    background: #f4f0e8;
    box-shadow: 0 3px 4px rgba(0, 0, 0, 0.1);
    border-left: double 5px #30291d;
    border-right: double 5px #30291d;
}
.kakoi p {
    margin: 0;
    padding: 0;
}

#search-top-box-input {
    border: solid 1px #2A110B;
    border-radius: 5px;
    background-color: #fff;
    box-shadow: none;
    color: #000;
}
#search-top-box-input:hover, #search-top-box-input:focus {
    border: solid 1px #000;
    background-color: #fff;
    box-shadow: none;
    color: #000;
}
#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    border: solid 1px #2A110B;
    border-radius: 5px;
    background: none;
    background-color: #CCA49B;
    box-shadow: none;
    color: #000;
    font-size: 90%;
    font-weight: bold;
    cursor: pointer;
}
#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
    border: solid 1px #2A110B;
    background: none;
    background-color: #CCA49B;
    box-shadow: none;
    text-shadow: none;
    color: #000;
}

@media (max-width: 450px) {
#search-top-box-input {
    display:none;
}
}

[[/code]]
--]
