        display: none !important;
    }
}

div#content-wrap {
    justify-contents: center;
}

div#header {
    background-image: linear-gradient(to bottom, #AAA, #AAA);
}

div#header::before {
    grid-row: 1/6;
    grid-column: 1/6;
    width: auto;
}

div#header::after {
    content: "";
    position: absolute;
    left: 0;
    bottom: var(--sp_top-bar-height);
    width: calc(var(--sp_header-height) * var(--sp_side-logo-scale));
    height: calc(var(--sp_header-height) * var(--sp_side-logo-scale));
    max-width: 25vw;
    max-height: 25vw;
    background-image: var(--sp_header-side-logo);
    background-position: center;
    background-repeat: no-repeat;
    background-size: 100% auto;
}

div#header h1 {
    grid-row: 3/4;
    grid-column: 2/4;
    display: block;
    max-width: unset;
    text-align: center;
    margin: 0;
    padding: 0;
}

div#header h2 {
    grid-row: 4/5;
    grid-column: 2/4;
    display: block;
    max-width: unset;
    text-align: center;
    margin: 0;
    padding: 0;
}

div#header h1 a,
div#header h2 span {
    display: inline-grid;
    font-weight: 900;
    justify-content: center;
    align-items: center;
}

div#header h1 a::before,
div#header h1 a::after {
    grid-column: 1 / 2;
    grid-row: 1 / 2;
    content: var(--header-title);
    font-size: var(--sp_header-title-size);
    font-family: var(--header-font);
    z-index: 5;
    text-shadow: none;
    padding: 0.5rem;
    letter-spacing: 0.25rem;
}

div#header h1 a::before {
    color: #000;
}

div#header h1 a::after {
    color: transparent;
    text-stroke: 0.4rem #AAA;
    -webkit-text-stroke: 0.4rem #AAA;
    z-index: 1;
}

div#header h2 span::before,
div#header h2 span::after {
    grid-column: 1 / 2;
    grid-row: 1 / 2;
    color: #000;
    content: var(--header-subtitle);
    font-size: var(--sp_header-subtitle-size);
    font-family: var(--header-font);
    letter-spacing: 0.05vw;
    z-index: 5;
    text-shadow: none;
    padding: 0 0.5rem;
}

div#header h2 span::after {
    color: transparent;
    text-stroke: 0.3rem #AAA;
    -webkit-text-stroke: 0.3rem #AAA;
    z-index: 1;
}

div#page-title,
input#search-top-box-input {
    display: none;
}

div#top-bar {
    box-shadow: 0 0.4rem 0 rgba(0, 0, 0, 0.6), 0 0.8rem 0 rgba(0, 0, 0, 0.4);
}

div#top-bar div[class*="top-bar"] > ul {
    justify-content: center;
}

div#login-status {
    color: #FFF;
    text-shadow: -1px 0 #000, 0 1px #000, 1px 0 #000, 0 -1px #000;
    z-index: 30;
}

div#account-options {
    text-shadow: none;
    text-align: center;
    background-color: #DDD;
    border: solid 3px #888;
    z-index: 35;
}

div#account-options a {
    color: #000;
}

div#account-options a:hover {
    text-decoration: none;
    background-color: #EEE;
}

form#search-top-box-form input[type="submit"] {
    color: #000;
    background-color: #EEE;
    background-image: none;
    border: solid 3px #888;
    border-radius: 0;
    box-shadow: none;
}

form#search-top-box-form input[type="submit"]:hover,
form#search-top-box-form input[type="submit"]:focus {
    color: #FFF;
    background-color: #888;
    background-image: none;
    border: solid 3px #888;
    border-radius: 0;
    box-shadow: none;
}

div#top-bar div.open-menu a {
    color: #000 !important;
    border: solid 3px #888;
    background-color: #EEE;
}

div#side-bar,
div#side-bar:target {
    border: none;
    background-color: #FFF;
    z-index: 36;
}

div#side-bar div.side-block {
    box-shadow: none;
    background-color: #DDD !important;
    border: solid 3px #888;
    border-radius: 0;
}

div#side-bar div.side-block img {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

div.page-rate-widget-box {
    margin-right: 0;
    padding: 0;
    box-shadow: none;
    border: solid 3px #888;
    border-radius: 0;
}

div.blockquote {
    font-size: 1rem;
    border: solid 3px #888;
    background-color: #DDD;
    margin: 2rem 0;
}

div.document {
    margin: 3rem;
}

div.image-photo {
    padding: 0.75rem;
    background-color: #FFF;
    width: 60%;
    margin: 4rem auto 0 auto;
    transform: rotate(4deg);
    -webkit-transform: rotate(4deg);
}

/* for HUBPAGE */
/* inspired by Sekai_s */

div.content-container-container {
    overflow-x: hidden;
}

div.content-container {
    display: grid;
    align-items: start;
    position: relative;
    grid-template-columns: 1fr 0.05fr 1fr 0.05fr 1fr 0.05fr 1fr;
    grid-template-areas: "p1 . p2 . p3 . p4";
    width: 415%;
    left: 0%;
    max-width: unset !important;
    transition: ease 0.5s;
}

div.content-container div.page1 {
    grid-area: p1;
}

div.content-container div.page2 {
    grid-area: p2;
}

div.content-container div.page3 {
    grid-area: p3;
}

div.content-container div.page4 {
    grid-area: p4;
}

div.target {
    position: fixed;
}

div#u-p-1:target ~ div.content-container-container div.content-container {
    left: 0%;
}

div#u-p-2:target ~ div.content-container-container div.content-container {
    left: -105%;
}

div#u-p-3:target ~ div.content-container-container div.content-container {
    left: -210%;
}

div#u-p-4:target ~ div.content-container-container div.content-container {
    left: -315%;
}

div.link-container {
    position: sticky;
    margin-left: -4rem;
    bottom: 1rem;
    width: calc(100% + 8rem);
    max-width: calc(100% + 8rem) !important;
    height: 3rem;
    z-index: 6;
}

div.link-container p {
    margin: 0;
    display: flex;
    justify-content: space-between;
}

div.link-container br {
     display: none;
}

.float-arrow {
    display: none;
    width: 3rem;
    height: 3rem;
    border-radius: 1.5rem;
    background-color: #888;
    color: #FFF;
    font-size: 2rem;
    font-weight: 900;
    line-height: 3rem;
    text-align: center;
}

.placeholder {
    display: none;
}

a.float-arrow:hover,
a.float-arrow:focus,
a.float-arrow:active,
a.float-arrow:visited {
    color: #FFF;
    text-decoration: none;
    background-color: #888;
}

.arrow-p1 {
    display: block;
}

div.target:target ~ div.link-container .arrow-p1 {
    display: none;
}

div.target#u-p-1:target ~ div.link-container .arrow-p1 {
    display: block;
}

div.target#u-p-2:target ~ div.link-container .arrow-p2 {
    display: block;
}

div.target#u-p-3:target ~ div.link-container .arrow-p3 {
    display: block;
}

div.target#u-p-4:target ~ div.link-container .arrow-p4 {
    display: block;
}

@media (max-width: 960px) {
div.link-container {
    margin-left: -2rem;
    width: calc(100% + 4rem);
    opacity: 0.4;
    transition: ease 0.25s;
}

div.link-container:hover {
    opacity: 1;
}
}

@media (max-width: 880px) {
div.link-container {
    margin-left: 0;
    width: 100%;
}
}
