[[module css]]
#header {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/collab%3Achristoph-ray-shineshadowd/Cache_-583eb5bf446f66a7..jpg) no-repeat left 15px !important;
    background-size: 120px 120px !important;
    max-width: none;
}
body {
    background-color: white
}
#header {
    width: 100%;
    max-width: none;
    right: 0;
    left: 0;

    width: 90%;
    max-width: 980px;
    margin: 0 auto;
}

:root {
    --bg: #ADD8E6;
    --key: black;
    --page-title: #000000;

    --fg-light: #082567;
    --fg-muted: #0047AB;
    --fg-medium: #002FA7;

    --fg-med-dark: #F8F8FF;
    --fg-dark: #E6E6FA;
    --fg-dark-2: #000000;

    --fg-muted-dark: #4D80E6;
    --fg-grad-2: #6495ED;
    --fg-grad-3: #87CEFA;
}
/* HEADER */
#header h1 {
    margin-left: 120px;
    padding: 0;
    float: left;
    max-height: 95px;
}
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: BauhausLTDemi, Arial, sans-serif;
    font-size: 180%;
    text-shadow: none;
    text-decoration: none;
    letter-spacing: 0.9px;
}/* 标题 */

#header h1 a::before {
    content: "PCS异度署";
    color: #E0FFFF;
    text-shadow: 3px 3px 5px #0c4d66;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    font-size: 100%;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}/* 小标题 */

#header h2 span::before {
    content: ' 放生，锁定，压制　';
    color: #48D1CC;
    text-shadow: 1px 1px 1px #0c4d66;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
div#container-wrap {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/collab%3Achristoph-ray-shineshadowd/pcsyds.png) top left repeat-x !important;
}
 /* SIDEBAR */
#side-bar .heading {
    color: var(--fg-medium);
    border-bottom: solid 1px var(--fg-medium);
}

#side-bar div.menu-item.inactive a {
    color: var(--fg-med-dark);
}

#side-bar div.menu-item .sub-text {
    color: var(--fg-med-dark);
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px var(--fg-med-dark);
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: var(--fg-med-dark);
    font-weight: bold;
}

a.collapsible-block-link {
    font-weight: bold; 
}

/* LINKS */
a {
    color: var(--fg-light);
    text-decoration: bold;
    background: transparent;
}

a.newpage {
    color: var(--fg-light);
    text-decoration: bold;
    background: transparent;
}

#side-bar a:visited{ color: var(--fg-light); }

a:visited { color: var(--fg-light); }

/* INFO PANE */
.rate-box-with-credit-button {
    background-color: var(--fg-dark) !important;
    border-color: var(--fg-med-dark) !important;
    border-radius: 0px !important;
}

.page-rate-widget-box{
    border: none !important;
    border-radius: 0px !important;
}

.rate-box-with-credit-button .fa-info {
    border-color: var(--fg-med-dark) !important;
    color: var(--bg) !important;
}

.rate-box-with-credit-button .fa-info:hover {
    color: var(--fg-light) !important;
}

.rate-box-with-credit-button .cancel {
    border: none !important;
}

.close-credits{
    filter: hue-rotate(280deg);
}

.modalbox {
    box-shadow: 0px 1px 10px rgb(166,37,221,0.3) !important;
}

/* Page Rating Module Customizations */
.page-rate-widget-box {
  display: inline-block;
  border-radius: 5px;
  box-shadow: 1px 1px 3px rgba(0,0,0,.5);
  margin-bottom: 10px;
  margin-right: 2em;
}
.page-rate-widget-box .rate-points {
  background-color: #0481d1 !important;
  border: solid 1px #0d3754 ;
  border-right: 0;
  border-radius: 5px 0 0 5px;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #5eb8f2;
  border-top: solid 1px #0d3754 ;
  border-bottom: solid 1px #0d3754 ;
  font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  background: transparent;
  color: #0d3754 ;
  padding: 0 4px;
  margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #0d3754 ;
  color: #5eb8f2;
  text-decoration: none;
}
.page-rate-widget-box .cancel {
  background-color: #0481d1 ;
  border: solid 1px #0d3754 ;
  border-left: 0;
  border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
  background: transparent;
  text-transform: uppercase;
  color: #0d3754;
}
.page-rate-widget-box .cancel a:hover {
  border-radius: 0 3px 3px 0;
  background: #0d3754 ;
  color: #5eb8f2;
  text-decoration: none;
}

/* PAGE TITLEs */
h1, #page-title {
    color: #082567;
}

/* SEARCH BAR */
#search-top-box-input {
    border: solid 1px var(--fg-med-dark);
    background-color: var(--fg-dark);
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px var(--fg-med-dark);
    background-color: var(--fg-dark);
}

#search-top-box-input:hover, #search-top-box-input:focus {
    background: var(--fg-muted);
    border: solid 1px var(--fg-med-dark);
}

#search-top-box-form input[type=submit] {
    border: solid 1px var(--fg-med-dark);
    background-color: var(--fg-dark);
    color: var(--fg-dark-2);
    background: linear-gradient(to bottom, var(--fg-med-dark),var(--fg-grad-2),var(--fg-grad-3));
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px var(--fg-med-dark);
    color: var(--fg-dark-2);
    background-color: var(--fg-dark);
    background: linear-gradient(to bottom, var(--fg-med-dark),var(--fg-grad-2),var(--fg-grad-3));
}

/* TOP MENU */

#top-bar ul li ul {
    border: solid 1px var(--fg-muted-dark);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: var(--bg);
    color: var(--fg-muted-dark);
}

/* SIDE MENU */
#side-bar {
    display: block;
    background:123;
}

#side-bar img {
    filter: hue-rotate(273deg) saturate(50%) brightness(111%);
    -webkit-filter: hue-rotate(273deg) saturate(50%) brightness(111%);
}

#side-bar div.side-block.sns img {
    filter: none;
    -webkit-filter: none;
}

#side-bar .side-block {
    border: 2px solid var(--fg-muted-dark);
    border-radius: 3px;
    box-shadow: 150 100px 1000px rgba(32,32,32,0) !important;
    background: var(--bg) !important;
}

#side-bar .heading {
    color: var(--fg-muted-dark);
    border-bottom: solid 1px var(--fg-muted-dark);
}

#side-bar div.menu-item.inactive a {
    color: var(--fg-muted-dark);
}

#side-bar div.menu-item .sub-text {
    color: var(--fg-muted-dark);
}

#side-bar .collapsible-block-folded {
    background: none;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px var(--fg-muted-dark);
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: var(--fg-muted-dark);
    font-weight: bold;
}

/* YUI-TABS */
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    filter: hue-rotate(273deg) saturate(50%) brightness(111%);
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    filter: hue-rotate(273deg) saturate(50%) brightness(111%);
}

/* Custom page content classes */
.content-panel .panel-body {
    background: var(--bg);
}

.content-panel .panel-footer {
    color: var(--bg);
}

.content-panel .panel-footer a {
    color: var(--bg);
}

/* Standard Image Block */
.scp-image-block {
    border: solid 1px var(--fg-muted-dark);
}

.scp-image-block .scp-image-caption {
    background-color: var(--bg);
    border-top: solid 1px var(--fg-muted-dark);
}

/* Wikiwalk Navigation */
.footer-wikiwalk-nav {
    font-weight: bold;
    font-size: 75%;
}

/* off-canvas */
@media (max-width: 767px) {
.open-menu a {
    border: 0.2em solid var(--fg-muted-dark) !important;
    color: var(--fg-muted-dark) !important;
}

#side-bar {
    background-color: rgb(192, 192, 192);
}

#side-bar:target + #main-content {
    left: 0;
}
}

@media (min-width: 768px) {
#header h1 a {
    font-size: 150%;
}
}

/* Interwiki */
.scpnet-interwiki-frame {
    filter: hue-rotate(273deg) saturate(50%) brightness(111%);
[[/module]]

